import React, { useState, useRef, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faChevronLeft, 
  faChevronRight, 
  faChevronDown, 
  faChevronUp,
  faStar
} from '@fortawesome/free-solid-svg-icons';
import './DailyMenu.css';

// Sample menu items data
const MENU_ITEMS = [
  {
    id: "meal-1",
    name: "Chicken Biryani",
    description: "Aromatic basmati rice cooked with tender chicken, spices, and herbs",
    image: "https://images.unsplash.com/photo-1589302168068-964664d93dc0?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 130,
    category: "Greatest Hits",
    rating: 4.8,
    tags: ["Spicy", "Popular"]
  },
  {
    id: "meal-2",
    name: "Paneer Butter Masala",
    description: "Soft paneer cubes in a rich and creamy tomato-based gravy",
    image: "https://images.unsplash.com/photo-1505253758473-96b7015fcd40?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 120,
    category: "Greatest Hits",
    rating: 4.7,
    tags: ["Vegetarian", "Medium Spicy"]
  },
  {
    id: "meal-3",
    name: "Dal Makhani",
    description: "Black lentils and kidney beans cooked overnight with butter and cream",
    image: "https://images.unsplash.com/photo-1546833998-877b37c2e5c6?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 110,
    category: "Easy Meals",
    rating: 4.6,
    tags: ["Vegetarian", "Comfort Food"]
  },
  {
    id: "meal-4",
    name: "Fish Curry",
    description: "Fresh fish simmered in a flavorful coconut and tamarind curry",
    image: "https://images.unsplash.com/photo-1626102555690-b4534a3600b2?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 150,
    category: "Supermeals",
    rating: 4.9,
    tags: ["Seafood", "Spicy"]
  },
  {
    id: "meal-5",
    name: "Masala Dosa",
    description: "Crispy rice and lentil crepe filled with spiced potato filling and served with chutneys",
    image: "https://images.unsplash.com/photo-1610192244261-3f33de3f24a9?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 100,
    category: "Day Starters",
    rating: 4.8,
    tags: ["Breakfast", "Vegetarian"]
  },
  {
    id: "meal-6",
    name: "Butter Chicken",
    description: "Tender chicken cooked in a velvety tomato and butter sauce",
    image: "https://images.unsplash.com/photo-1588166524941-3bf61a9c41db?ixlib=rb-1.2.1&auto=format&fit=crop&w=1169&q=80",
    price: 140,
    category: "Greatest Hits",
    rating: 4.9,
    tags: ["Popular", "Mild Spicy"]
  },
  {
    id: "meal-7",
    name: "Veg Pulao",
    description: "Fragrant rice cooked with mixed vegetables and aromatic spices",
    image: "https://images.unsplash.com/photo-1596797038530-2c107229654b?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 110,
    category: "Easy Meals",
    rating: 4.5,
    tags: ["Vegetarian", "Light"]
  },
  {
    id: "meal-8",
    name: "Chicken Tikka",
    description: "Marinated chicken chunks grilled to perfection in a tandoor",
    image: "https://images.unsplash.com/photo-1599487488170-d11ec9c172f0?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 160,
    category: "Supermeals",
    rating: 4.7,
    tags: ["Grilled", "High Protein"]
  }
];

const DailyMenu = () => {
  const [activeCategory, setActiveCategory] = useState("Greatest Hits");
  const [isExpanded, setIsExpanded] = useState(false);
  const scrollRef = useRef(null);
  const carouselRef = useRef(null);

  // Categories based on our menu items
  const categories = ["Greatest Hits", "Supermeals", "Easy Meals", "Day Starters"];

  const filteredItems = MENU_ITEMS.filter(item => item.category === activeCategory);

  const scroll = (direction) => {
    if (scrollRef.current) {
      const { current } = scrollRef;
      const scrollAmount = direction === "left" ? -300 : 300;
      current.scrollBy({ left: scrollAmount, behavior: "smooth" });
    }
  };

  // Apply scroll animation for menu cards
  useEffect(() => {
    const handleScroll = () => {
      if (!carouselRef.current) return;

      const cards = carouselRef.current.querySelectorAll('.menu-card');
      const carouselRect = carouselRef.current.getBoundingClientRect();
      const centerX = carouselRect.left + carouselRect.width / 2;

      cards.forEach((card) => {
        const cardEl = card;
        const cardRect = cardEl.getBoundingClientRect();
        const cardCenterX = cardRect.left + cardRect.width / 2;
        const distanceFromCenter = Math.abs(centerX - cardCenterX);
        const maxDistance = window.innerWidth / 2;

        // Calculate scale based on distance from center
        let scale = 1 - (distanceFromCenter / maxDistance) * 0.15;
        scale = Math.max(0.85, scale); // Don't scale below 0.85

        // Calculate opacity based on distance from center
        let opacity = 1 - (distanceFromCenter / maxDistance) * 0.5;
        opacity = Math.max(0.6, opacity); // Don't fade below 0.6

        // Apply transformations
        cardEl.style.transform = `scale(${scale})`;
        cardEl.style.opacity = opacity.toString();
      });
    };

    const scrollContainer = scrollRef.current;
    if (scrollContainer) {
      scrollContainer.addEventListener('scroll', handleScroll);
      // Initial calculation
      handleScroll();

      return () => {
        scrollContainer.removeEventListener('scroll', handleScroll);
      };
    }
  }, [activeCategory]);

  // Price indicator based on category
  const getCategoryPrice = () => {
    switch(activeCategory) {
      case "Supermeals": return "₹150";
      case "Easy Meals": return "₹120";
      case "Day Starters": return "₹100";
      default: return "₹120";
    }
  };

  return (
    <section id="daily-combos" className="py-16 md:py-24 bg-white overflow-hidden">
      <div className="container mx-auto px-4 md:px-6">
        <motion.div
          className="text-center max-w-3xl mx-auto mb-12"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.7, ease: [0.22, 1, 0.36, 1] }}
        >
          <h2 className="text-3xl md:text-5xl font-bold mb-6 text-gray-900 tracking-tight">
            Daily Combos
          </h2>
          <p className="text-xl text-gray-600 leading-relaxed mb-4">
            Find your new favorite meal with delicious options starting at {getCategoryPrice()} per serving
          </p>
          <div className="bg-gray-50 p-5 rounded-xl border border-gray-100 shadow-sm">
            <h3 className="font-medium text-lg text-gray-900 mb-2">Preorder Pricing</h3>
            <p className="text-gray-600 text-base mb-3">
              Order 4 hours in advance for your mealtime and enjoy our best rates
            </p>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="bg-white p-3 rounded-lg border border-primary/20">
                <span className="font-semibold text-primary block">Supermeals</span>
                <span className="text-xl font-bold">₹150</span>
              </div>
              <div className="bg-white p-3 rounded-lg border border-primary/20">
                <span className="font-semibold text-primary block">Meals</span>
                <span className="text-xl font-bold">₹130</span>
              </div>
              <div className="bg-white p-3 rounded-lg border border-primary/20">
                <span className="font-semibold text-primary block">Day Starters</span>
                <span className="text-xl font-bold">₹100</span>
              </div>
            </div>
            <p className="text-gray-500 text-sm mt-3">*Desserts and Juices prices may vary. Subscribers get Supermeals and Desserts included with their plan at no extra cost.</p>
          </div>
        </motion.div>

        {/* Menu Navigation - Modern tabs */}
        <motion.div
          className="flex justify-center mb-10 overflow-x-auto"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.7, delay: 0.2, ease: [0.22, 1, 0.36, 1] }}
        >
          <div className="inline-flex rounded-lg bg-gray-100 p-1.5 shadow-inner">
            {categories.map((category, index) => (
              <motion.button
                key={index}
                className={`px-6 py-3 rounded-lg font-medium transition-all ${
                  activeCategory === category 
                    ? "bg-primary text-white shadow-md" 
                    : "text-gray-700 hover:bg-gray-200"
                }`}
                onClick={() => setActiveCategory(category)}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.98 }}
                transition={{ duration: 0.2 }}
              >
                {category}
              </motion.button>
            ))}
          </div>
        </motion.div>

        {/* Menu Carousel with 3D-like effects */}
        <div ref={carouselRef} className="relative mb-16">
          <motion.div
            className="relative"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.7, delay: 0.4 }}
          >
            <div 
              ref={scrollRef}
              className="overflow-x-auto pb-8 scrollbar-hide"
              style={{ scrollbarWidth: 'none', msOverflowStyle: 'none' }}
            >
              <div className="flex space-x-6 px-12" style={{ width: "max-content" }}>
                {filteredItems.map((item, index) => (
                  <motion.div
                    key={index}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5, delay: index * 0.1 }}
                  >
                    <MenuItemCard item={item} />
                  </motion.div>
                ))}
              </div>
            </div>

            {/* Navigation Arrows with animation */}
            <motion.button
              type="button"
              onClick={() => scroll("left")}
              className="absolute top-1/2 left-2 -translate-y-1/2 bg-white rounded-full p-3 shadow-lg z-10 hover:bg-gray-50 transition-colors"
              whileHover={{ scale: 1.1, boxShadow: "0 10px 25px -5px rgba(0, 0, 0, 0.1)" }}
              whileTap={{ scale: 0.95 }}
            >
              <FontAwesomeIcon icon={faChevronLeft} className="text-gray-800 w-4 h-4" />
            </motion.button>
            <motion.button
              type="button"
              onClick={() => scroll("right")}
              className="absolute top-1/2 right-2 -translate-y-1/2 bg-white rounded-full p-3 shadow-lg z-10 hover:bg-gray-50 transition-colors"
              whileHover={{ scale: 1.1, boxShadow: "0 10px 25px -5px rgba(0, 0, 0, 0.1)" }}
              whileTap={{ scale: 0.95 }}
            >
              <FontAwesomeIcon icon={faChevronRight} className="text-gray-800 w-4 h-4" />
            </motion.button>
          </motion.div>
        </div>

        {/* Expandable Full Menu Section */}
        <div className="mt-8">
          <motion.div
            className="text-center"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.7, delay: 0.6 }}
          >
            <button
              onClick={() => setIsExpanded(!isExpanded)}
              className="bg-primary hover:bg-primary/90 text-white font-medium px-8 py-3 rounded-full shadow-md transition-all hover:shadow-lg hover:-translate-y-1 flex items-center gap-2 mx-auto"
            >
              {isExpanded ? 'Show Less' : 'Explore Full Menu'}
              <FontAwesomeIcon icon={isExpanded ? faChevronUp : faChevronDown} className="w-4 h-4" />
            </button>
          </motion.div>

          <AnimatePresence>
            {isExpanded && (
              <motion.div
                initial={{ opacity: 0, height: 0 }}
                animate={{ opacity: 1, height: "auto" }}
                exit={{ opacity: 0, height: 0 }}
                transition={{ duration: 0.5, ease: [0.22, 1, 0.36, 1] }}
                className="overflow-hidden mt-12"
              >
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {MENU_ITEMS.map((item, index) => (
                    <motion.div
                      key={index}
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ duration: 0.5, delay: index * 0.03 }}
                    >
                      <ExpandedMenuItemCard item={item} />
                    </motion.div>
                  ))}
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </div>
    </section>
  );
};

function MenuItemCard({ item }) {
  return (
    <motion.div 
      className="w-64 md:w-72 bg-white rounded-xl overflow-hidden shadow-lg menu-card transform transition-all duration-300"
      whileHover={{ 
        y: -10, 
        boxShadow: "0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)" 
      }}
    >
      <div className="relative h-48 overflow-hidden">
        <img 
          src={item.image} 
          alt={item.name} 
          className="w-full h-full object-cover transform transition-transform duration-700 hover:scale-110"
        />
        <div className="absolute top-0 left-0 w-full h-full bg-gradient-to-b from-black/60 via-transparent to-transparent p-4">
          <div className="flex items-center gap-1">
            <span className="text-white text-sm font-semibold bg-primary/80 backdrop-blur-sm px-2 py-0.5 rounded-full">
              {item.category}
            </span>
          </div>
        </div>
        <div className="absolute bottom-0 left-0 w-full p-3 bg-gradient-to-t from-black/80 to-transparent">
          <div className="flex items-center justify-between">
            <span className="text-white font-semibold text-lg">₹{item.price}</span>
            <div className="flex items-center gap-1 bg-white/10 backdrop-blur-sm px-2 py-1 rounded-full">
              <FontAwesomeIcon icon={faStar} className="text-yellow-400 w-3 h-3" />
              <span className="text-white text-xs font-medium">{item.rating}</span>
            </div>
          </div>
        </div>
      </div>
      <div className="p-4">
        <h3 className="font-semibold text-lg mb-1 text-gray-900">{item.name}</h3>
        <p className="text-gray-600 text-sm line-clamp-2">{item.description}</p>
        <div className="mt-3 flex flex-wrap gap-2">
          {item.tags.map((tag, index) => (
            <span 
              key={index} 
              className="text-xs font-medium bg-gray-100 text-gray-700 px-2 py-1 rounded-full"
            >
              {tag}
            </span>
          ))}
        </div>
        <button 
          className="mt-4 w-full bg-primary hover:bg-primary/90 text-white font-medium py-2 rounded-lg transition-colors"
        >
          Add to Cart
        </button>
      </div>
    </motion.div>
  );
}

function ExpandedMenuItemCard({ item }) {
  return (
    <div className="bg-white rounded-xl overflow-hidden shadow-md border border-gray-100 flex flex-col h-full hover:shadow-lg transition-shadow duration-300">
      <div className="relative h-48 overflow-hidden">
        <img 
          src={item.image} 
          alt={item.name} 
          className="w-full h-full object-cover transform transition-transform duration-700 hover:scale-110"
        />
        <div className="absolute top-0 left-0 w-full h-full bg-gradient-to-b from-black/60 via-transparent to-transparent p-4">
          <div className="flex items-center gap-1">
            <span className="text-white text-xs font-medium bg-primary/80 backdrop-blur-sm px-2 py-0.5 rounded-full">
              {item.category}
            </span>
          </div>
        </div>
      </div>
      <div className="p-4 flex-grow">
        <div className="flex justify-between items-start mb-2">
          <h3 className="font-semibold text-base text-gray-900">{item.name}</h3>
          <div className="flex items-center gap-1 bg-gray-100 px-2 py-0.5 rounded-full">
            <FontAwesomeIcon icon={faStar} className="text-yellow-400 w-3 h-3" />
            <span className="text-gray-700 text-xs font-medium">{item.rating}</span>
          </div>
        </div>
        <p className="text-gray-600 text-sm mb-3 line-clamp-2">{item.description}</p>
        <div className="mt-auto pt-3 flex items-center justify-between">
          <span className="font-semibold text-lg text-primary">₹{item.price}</span>
          <button 
            className="bg-primary hover:bg-primary/90 text-white font-medium px-3 py-1 rounded-lg text-sm transition-colors"
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
}

export default DailyMenu;
