import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes } from '@fortawesome/free-solid-svg-icons';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const offset = window.scrollY;
      setScrolled(offset > 10);
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      window.scrollTo({
        top: section.offsetTop - 80,
        behavior: 'smooth'
      });
      setIsMenuOpen(false);
    }
  };

  return (
    <header className={`sticky top-0 z-50 bg-white transition-shadow duration-300 ${scrolled ? 'shadow-md' : 'shadow-sm'}`}>
      <div className="container mx-auto px-4 md:px-6 py-3 md:py-4">
        <div className="flex items-center justify-between">
          <button onClick={() => scrollToSection('hero')} className="flex items-center space-x-2">
            <svg width="36" height="36" viewBox="0 0 36 36" fill="none" xmlns="http://www.w3.org/2000/svg" className="text-primary">
              <path d="M18 3C9.716 3 3 9.716 3 18C3 26.284 9.716 33 18 33C26.284 33 33 26.284 33 18C33 9.716 26.284 3 18 3ZM24.6 19.8H19.8V24.6C19.8 25.48 19.08 26.2 18.2 26.2C17.32 26.2 16.6 25.48 16.6 24.6V19.8H11.8C10.92 19.8 10.2 19.08 10.2 18.2C10.2 17.32 10.92 16.6 11.8 16.6H16.6V11.8C16.6 10.92 17.32 10.2 18.2 10.2C19.08 10.2 19.8 10.92 19.8 11.8V16.6H24.6C25.48 16.6 26.2 17.32 26.2 18.2C26.2 19.08 25.48 19.8 24.6 19.8Z" fill="currentColor"/>
            </svg>
            <span className="text-xl md:text-2xl font-semibold text-primary">Myna <span className="font-normal">Kitchen</span></span>
          </button>

          {/* Mobile menu button */}
          <div className="flex md:hidden items-center space-x-3">
            <button 
              onClick={() => scrollToSection('try-meal')}
              className="bg-accent text-white px-3 py-1.5 rounded-full text-sm font-medium shadow-sm hover:bg-orange-600 transition-colors"
            >
              Try a Meal
            </button>
            <button 
              type="button" 
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="flex items-center justify-center p-2 rounded-md text-gray-400 hover:text-gray-500 hover:bg-gray-100 focus:outline-none"
              aria-expanded={isMenuOpen}
            >
              <span className="sr-only">Open main menu</span>
              {isMenuOpen ? (
                <FontAwesomeIcon icon={faTimes} className="block h-5 w-5" />
              ) : (
                <FontAwesomeIcon icon={faBars} className="block h-5 w-5" />
              )}
            </button>
          </div>

          {/* Desktop navigation */}
          <nav className="hidden md:flex items-center space-x-6 lg:space-x-8">
            <button 
              onClick={() => scrollToSection('hero')}
              className="text-gray-800 hover:text-primary font-medium transition-colors"
            >
              Home
            </button>
            <button 
              onClick={() => scrollToSection('how-it-works')}
              className="text-gray-800 hover:text-primary font-medium transition-colors"
            >
              How It Works
            </button>
            <button 
              onClick={() => scrollToSection('daily-menu')}
              className="text-gray-800 hover:text-primary font-medium transition-colors"
            >
              Menu
            </button>
            <button 
              onClick={() => scrollToSection('subscription-plans')}
              className="text-gray-800 hover:text-primary font-medium transition-colors"
            >
              Pricing
            </button>
            <button 
              onClick={() => scrollToSection('delivery-map')}
              className="text-gray-800 hover:text-primary font-medium transition-colors"
            >
              Delivery Areas
            </button>
            <button 
              onClick={() => scrollToSection('try-meal')}
              className="bg-accent text-white px-6 py-2.5 rounded-full font-medium shadow-sm hover:bg-orange-600 transition-colors"
            >
              Try a Meal
            </button>
          </nav>
        </div>

        {/* Mobile navigation */}
        <div className={`pt-4 pb-3 border-t border-gray-200 md:hidden ${isMenuOpen ? 'block' : 'hidden'}`}>
          <div className="space-y-1">
            <button 
              onClick={() => scrollToSection('hero')}
              className="block w-full text-left px-3 py-2 rounded-md text-base font-medium text-gray-900 hover:bg-gray-50"
            >
              Home
            </button>
            <button 
              onClick={() => scrollToSection('how-it-works')}
              className="block w-full text-left px-3 py-2 rounded-md text-base font-medium text-gray-900 hover:bg-gray-50"
            >
              How It Works
            </button>
            <button 
              onClick={() => scrollToSection('daily-menu')}
              className="block w-full text-left px-3 py-2 rounded-md text-base font-medium text-gray-900 hover:bg-gray-50"
            >
              Menu
            </button>
            <button 
              onClick={() => scrollToSection('subscription-plans')}
              className="block w-full text-left px-3 py-2 rounded-md text-base font-medium text-gray-900 hover:bg-gray-50"
            >
              Pricing
            </button>
            <button 
              onClick={() => scrollToSection('delivery-map')}
              className="block w-full text-left px-3 py-2 rounded-md text-base font-medium text-gray-900 hover:bg-gray-50"
            >
              Delivery Areas
            </button>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
