import React, { useRef } from "react";
import { motion, useInView } from "framer-motion";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faUtensils, 
  faCalendarCheck, 
  faTruckFast, 
  faThumbsUp,
  faLocationDot,
  faClipboardCheck,
  faHeart
} from '@fortawesome/free-solid-svg-icons';

export default function HowItWorks() {
  const containerRef = useRef(null);
  const isInView = useInView(containerRef, { once: false, amount: 0.2 });

  const steps = [
    {
      number: 1,
      title: "Browse & Select",
      description: "Browse and select a plan, meal type, and check availability in your location. Choose meals that match your taste and dietary preferences.",
      icon: faUtensils,
      bgColor: "bg-blue-100",
      iconColor: "text-blue-600",
      gradientFrom: "from-blue-400",
      gradientTo: "to-blue-600",
      accentColor: "bg-blue-300"
    },
    {
      number: 2,
      title: "Schedule Your Delivery",
      description: "Schedule your delivery, manage orders with complete transparency. No commitments - you're free to modify or cancel anytime.",
      icon: faCalendarCheck,
      bgColor: "bg-green-100",
      iconColor: "text-green-600",
      gradientFrom: "from-green-400",
      gradientTo: "to-green-600",
      accentColor: "bg-green-300"
    },
    {
      number: 3,
      title: "Doorstep Delivery",
      description: "Receive your freshly prepared meals right at your doorstep. Our delivery team ensures your food arrives in perfect condition.",
      icon: faTruckFast,
      bgColor: "bg-amber-100",
      iconColor: "text-amber-600",
      gradientFrom: "from-amber-400",
      gradientTo: "to-amber-600",
      accentColor: "bg-amber-300"
    },
    {
      number: 4,
      title: "Enjoy Your Meal",
      description: "Simply unpack and enjoy your delicious, home-style meal. Please keep updating your feedback with us to help us serve you better.",
      icon: faHeart,
      bgColor: "bg-red-100",
      iconColor: "text-red-600",
      gradientFrom: "from-red-400",
      gradientTo: "to-red-600",
      accentColor: "bg-red-300"
    }
  ];

  return (
    <section id="how-it-works" className="py-20 md:py-32 bg-gradient-to-b from-secondary/50 to-white overflow-hidden">
      <div className="container mx-auto px-4 md:px-6">
        <div className="text-center max-w-3xl mx-auto mb-16 md:mb-24">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6 }}
          >
            <h2 className="text-3xl md:text-5xl font-bold mb-6 text-gray-900 tracking-tight">
              How It Works
            </h2>
            <p className="text-xl text-gray-600 leading-relaxed">
              Experience a seamless journey from selection to satisfaction with our simple process.
            </p>
          </motion.div>
        </div>

        <div ref={containerRef} className="relative">
          {/* Interactive timeline */}
          <div className="absolute top-24 md:top-36 left-4 md:left-1/2 h-[calc(100%-8rem)] md:h-auto md:w-[calc(100%-8rem)] border-l-2 md:border-l-0 md:border-t-2 border-primary/30 md:transform md:-translate-x-1/2 z-0"></div>

          <div className="space-y-24 md:space-y-0 md:grid md:grid-cols-2 lg:grid-cols-4 md:gap-8">
            {steps.map((step, index) => (
              <motion.div
                key={index}
                className="relative"
                initial={{ opacity: 0, y: 50 }}
                animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 50 }}
                transition={{ duration: 0.6, delay: index * 0.2 }}
              >
                {/* Step number with pulse effect */}
                <div className="absolute top-0 left-0 md:left-1/2 md:top-0 transform translate-x-3 md:-translate-x-1/2 md:-translate-y-8 z-20">
                  <div className="relative">
                    <div className={`w-16 h-16 bg-gradient-to-br ${step.gradientFrom} ${step.gradientTo} text-white rounded-full flex items-center justify-center text-xl font-bold shadow-lg`}>
                      {step.number}
                    </div>
                    <motion.div
                      className={`absolute inset-0 rounded-full ${step.gradientFrom} opacity-30`}
                      animate={{ 
                        scale: [1, 1.5, 1],
                        opacity: [0.7, 0.2, 0.7]
                      }}
                      transition={{
                        duration: 2,
                        repeat: Infinity,
                        ease: "easeInOut"
                      }}
                    ></motion.div>
                  </div>
                </div>

                <div className="pt-6 pl-12 md:pl-0 md:pt-12 md:px-4">
                  {/* Visual icon representation with improved visuals */}
                  <div className="mb-6 rounded-xl overflow-hidden shadow-xl transform transition-all duration-300 hover:scale-105 h-56">
                    <div className={`w-full h-full relative ${step.bgColor} flex items-center justify-center bg-gradient-to-br ${step.gradientFrom}/10 ${step.gradientTo}/20`}>
                      {/* Floating accent circles for visual interest */}
                      <motion.div 
                        className={`absolute -bottom-6 -right-6 w-24 h-24 rounded-full ${step.accentColor} opacity-20 blur-xl`}
                        animate={{ 
                          scale: [1, 1.2, 1],
                          opacity: [0.2, 0.3, 0.2] 
                        }}
                        transition={{ 
                          duration: 4,
                          ease: "easeInOut",
                          repeat: Infinity,
                          delay: index * 0.5
                        }}
                      ></motion.div>

                      <motion.div 
                        className={`absolute top-4 -left-4 w-16 h-16 rounded-full ${step.accentColor} opacity-30`}
                        animate={{ 
                          x: [0, 10, 0],
                          y: [0, -10, 0],
                          opacity: [0.3, 0.5, 0.3] 
                        }}
                        transition={{ 
                          duration: 5,
                          ease: "easeInOut",
                          repeat: Infinity,
                          delay: index * 0.3
                        }}
                      ></motion.div>

                      {/* Main icon with glowing effect */}
                      <div className="relative">
                        <FontAwesomeIcon 
                          icon={step.icon} 
                          className={`w-20 h-20 ${step.iconColor} drop-shadow-lg`} 
                        />
                        <motion.div
                          className="absolute inset-0 blur-lg opacity-50"
                          animate={{ 
                            opacity: [0.5, 0.8, 0.5]
                          }}
                          transition={{
                            duration: 2,
                            repeat: Infinity,
                            ease: "easeInOut"
                          }}
                        >
                          <FontAwesomeIcon 
                            icon={step.icon} 
                            className={`w-20 h-20 ${step.iconColor}`} 
                          />
                        </motion.div>
                      </div>

                      {/* Decorative elements based on the step */}
                      {step.number === 1 && (
                        <motion.div 
                          className="absolute bottom-4 right-4"
                          animate={{ rotate: [0, 10, 0] }}
                          transition={{ duration: 3, repeat: Infinity }}
                        >
                          <svg width="40" height="40" viewBox="0 0 24 24" fill="none" className={step.iconColor}>
                            <path d="M12 6v6l4 2" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                            <circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2"/>
                          </svg>
                        </motion.div>
                      )}

                      {step.number === 2 && (
                        <motion.div 
                          className="absolute top-4 right-4"
                          animate={{ scale: [1, 1.1, 1] }}
                          transition={{ duration: 2, repeat: Infinity }}
                        >
                          <svg width="40" height="40" viewBox="0 0 24 24" fill="none" className={step.iconColor}>
                            <path d="M9 11l3 3L22 4" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                            <path d="M21 12v7a2 2 0 01-2 2H5a2 2 0 01-2-2V5a2 2 0 012-2h11" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                          </svg>
                        </motion.div>
                      )}

                      {step.number === 3 && (
                        <motion.div 
                          className="absolute bottom-4 left-4"
                          animate={{ x: [0, 5, 0] }}
                          transition={{ duration: 2, repeat: Infinity }}
                        >
                          <svg width="40" height="40" viewBox="0 0 24 24" fill="none" className={step.iconColor}>
                            <path d="M22 12h-4l-3 9L9 3l-3 9H2" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                          </svg>
                        </motion.div>
                      )}

                      {step.number === 4 && (
                        <motion.div 
                          className="absolute top-4 left-4"
                          animate={{ scale: [1, 1.2, 1] }}
                          transition={{ duration: 1.5, repeat: Infinity }}
                        >
                          <svg width="40" height="40" viewBox="0 0 24 24" fill="none" className={step.iconColor}>
                            <path d="M14 9V5a3 3 0 00-3-3l-4 9v11h11.28a2 2 0 002-1.7l1.38-9a2 2 0 00-2-2.3H14z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                            <path d="M7 22V11L3 12" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                          </svg>
                        </motion.div>
                      )}
                    </div>
                  </div>

                  {/* Step content */}
                  <div className="bg-white rounded-xl p-6 shadow-lg border border-gray-100 h-[calc(100%-14rem)]">
                    <h3 className="text-xl font-semibold mb-3">{step.title}</h3>
                    <p className="text-gray-600">{step.description}</p>
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        </div>

        {/* Customer support */}
        <motion.div 
          className="mt-24 md:mt-32 bg-gradient-to-r from-primary/10 to-primary/5 rounded-2xl p-8 md:p-12 flex flex-col md:flex-row items-center gap-8 md:gap-12"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          <div className="md:w-1/2">
            <div className="flex items-center gap-3 mb-4">
              <FontAwesomeIcon icon={faThumbsUp} className="w-6 h-6 text-primary" />
              <span className="text-sm font-medium bg-primary/20 text-primary px-3 py-1 rounded-full">
                We're here to help
              </span>
            </div>

            <h3 className="text-2xl md:text-3xl font-bold mb-4">Get Personalized Assistance</h3>
            <p className="text-gray-600 mb-6">
              We have a Customer support team to answer your queries before you take a plan. 
              They'll help you choose a plan suitable for your needs, so please feel free to talk to us before making a payment.
            </p>

            <div className="flex flex-col sm:flex-row gap-4">
              <a 
                href="https://wa.me/yourphonenumber" 
                target="_blank" 
                rel="noopener noreferrer"
                className="inline-flex items-center justify-center gap-2 bg-green-600 hover:bg-green-700 text-white py-3 px-6 rounded-lg font-medium transition-colors"
              >
                <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" viewBox="0 0 16 16">
                  <path d="M13.601 2.326A7.85 7.85 0 0 0 7.994 0C3.627 0 .068 3.558.064 7.926c0 1.399.366 2.76 1.057 3.965L0 16l4.204-1.102a7.9 7.9 0 0 0 3.79.965h.004c4.368 0 7.926-3.558 7.93-7.93A7.9 7.9 0 0 0 13.6 2.326zM7.994 14.521a6.57 6.57 0 0 1-3.356-.92l-.24-.144-2.494.654.666-2.433-.156-.251a6.56 6.56 0 0 1-1.007-3.505c0-3.626 2.957-6.584 6.591-6.584a6.56 6.56 0 0 1 4.66 1.931 6.56 6.56 0 0 1 1.928 4.66c-.004 3.639-2.961 6.592-6.592 6.592m3.615-4.934c-.197-.099-1.17-.578-1.353-.646-.182-.065-.315-.099-.447.1-.133.197-.513.645-.627.775-.114.133-.232.148-.43.05-.197-.1-.836-.308-1.592-.985-.59-.525-.985-1.175-1.103-1.372-.114-.198-.011-.304.088-.403.087-.088.197-.232.296-.346.1-.114.133-.198.198-.33.065-.134.034-.248-.015-.347-.05-.099-.445-1.076-.612-1.47-.16-.389-.323-.335-.445-.34-.114-.007-.247-.007-.38-.007a.73.73 0 0 0-.529.247c-.182.198-.691.677-.691 1.654s.71 1.916.81 2.049c.098.133 1.394 2.132 3.383 2.992.47.205.84.326 1.129.418.475.152.904.129 1.246.08.38-.058 1.171-.48 1.338-.943.164-.464.164-.86.114-.943-.049-.084-.182-.133-.38-.232"/>
                </svg>
                Chat with Us
              </a>
              <a 
                href="tel:+919876543210" 
                className="inline-flex items-center justify-center gap-2 bg-white border border-gray-300 text-gray-700 py-3 px-6 rounded-lg font-medium hover:bg-gray-50 transition-colors"
              >
                <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" viewBox="0 0 16 16">
                  <path d="M3.654 1.328a.678.678 0 0 0-1.015-.063L1.605 2.3c-.483.484-.661 1.169-.45 1.77a17.6 17.6 0 0 0 4.168 6.608 17.6 17.6 0 0 0 6.608 4.168c.601.211 1.286.033 1.77-.45l1.034-1.034a.678.678 0 0 0-.063-1.015l-2.307-1.794a.68.68 0 0 0-.58-.122l-2.19.547a1.75 1.75 0 0 1-1.657-.459L5.482 8.062a1.75 1.75 0 0 1-.46-1.657l.548-2.19a.68.68 0 0 0-.122-.58L3.654 1.328zM1.884.511a1.745 1.745 0 0 1 2.612.163L6.29 2.98c.329.423.445.974.315 1.494l-.547 2.19a.68.68 0 0 0 .178.643l2.457 2.457a.68.68 0 0 0 .644.178l2.189-.547a1.75 1.75 0 0 1 1.494.315l2.306 1.794c.829.645.905 1.87.163 2.611l-1.034 1.034c-.74.74-1.846 1.065-2.877.702a18.6 18.6 0 0 1-7.01-4.42 18.6 18.6 0 0 1-4.42-7.009c-.362-1.03-.037-2.137.703-2.877z"/>
                </svg>
                Call Us
              </a>
            </div>
          </div>

          <div className="md:w-1/2 flex justify-center">
            <motion.div 
              className="relative w-full max-w-md"
              whileHover={{ y: -5 }}
              transition={{ duration: 0.3 }}
            >
              <div className="bg-white rounded-xl overflow-hidden shadow-xl">
                <div className="bg-primary/10 p-8 flex justify-center">
                  <FontAwesomeIcon icon={faLocationDot} className="w-24 h-24 text-primary/60" />
                </div>
                <div className="p-6">
                  <h4 className="font-semibold text-lg mb-4">Join our WhatsApp Group</h4>
                  <p className="text-gray-600 mb-6">Get daily menu updates and stay informed about special offers and new meal options.</p>

                  <a 
                    href="https://wa.me/yourphonenumber" 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="w-full flex items-center justify-center gap-2 bg-green-600 hover:bg-green-700 text-white py-3 rounded-lg font-medium transition-colors"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" viewBox="0 0 16 16">
                      <path d="M13.601 2.326A7.85 7.85 0 0 0 7.994 0C3.627 0 .068 3.558.064 7.926c0 1.399.366 2.76 1.057 3.965L0 16l4.204-1.102a7.9 7.9 0 0 0 3.79.965h.004c4.368 0 7.926-3.558 7.93-7.93A7.9 7.9 0 0 0 13.6 2.326zM7.994 14.521a6.57 6.57 0 0 1-3.356-.92l-.24-.144-2.494.654.666-2.433-.156-.251a6.56 6.56 0 0 1-1.007-3.505c0-3.626 2.957-6.584 6.591-6.584a6.56 6.56 0 0 1 4.66 1.931 6.56 6.56 0 0 1 1.928 4.66c-.004 3.639-2.961 6.592-6.592 6.592m3.615-4.934c-.197-.099-1.17-.578-1.353-.646-.182-.065-.315-.099-.447.1-.133.197-.513.645-.627.775-.114.133-.232.148-.43.05-.197-.1-.836-.308-1.592-.985-.59-.525-.985-1.175-1.103-1.372-.114-.198-.011-.304.088-.403.087-.088.197-.232.296-.346.1-.114.133-.198.198-.33.065-.134.034-.248-.015-.347-.05-.099-.445-1.076-.612-1.47-.16-.389-.323-.335-.445-.34-.114-.007-.247-.007-.38-.007a.73.73 0 0 0-.529.247c-.182.198-.691.677-.691 1.654s.71 1.916.81 2.049c.098.133 1.394 2.132 3.383 2.992.47.205.84.326 1.129.418.475.152.904.129 1.246.08.38-.058 1.171-.48 1.338-.943.164-.464.164-.86.114-.943-.049-.084-.182-.133-.38-.232"/>
                    </svg>
                    Join WhatsApp Group
                  </a>
                </div>
              </div>

              {/* Decorative elements */}
              <div className="absolute -top-4 -right-4 w-12 h-12 bg-green-500 rounded-full opacity-20"></div>
              <div className="absolute -bottom-6 -left-6 w-16 h-16 bg-primary rounded-full opacity-10"></div>
            </motion.div>
          </div>
        </motion.div>
      </div>
    </section>
  );
}
