import React, { useEffect } from 'react';
import './App.css';
import Header from './components/Header';
import HeroSection from './components/HeroSection';
import Features from './components/Features';
import HowItWorks from './components/HowItWorks';
import SubscriptionPlans from './components/SubscriptionPlans';
import DailyMenu from './components/DailyMenu';
import Testimonials from './components/Testimonials';
import DeliveryMap from './components/DeliveryMap';
import FAQ from './components/FAQ';
import TryMeal from './components/TryMeal';
import Footer from './components/Footer';
import { createScrollObserver } from './lib/utils';

function App() {
  useEffect(() => {
    // Add intersection observer for fade-in animations
    const observer = createScrollObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('active');
          observer.unobserve(entry.target);
        }
      });
    });

    // Apply to any .section-fade elements
    document.querySelectorAll('.section-fade').forEach(section => {
      observer.observe(section);
    });

    return () => {
      observer.disconnect();
    };
  }, []);

  return (
    <div className="font-sans text-gray-800 bg-white overflow-x-hidden">
      <Header />
      <HeroSection />
      <Features />
      <HowItWorks />
      <SubscriptionPlans />
      <DailyMenu />
      <Testimonials />
      <DeliveryMap />
      <FAQ />
      <TryMeal />
      <Footer />
    </div>
  );
}

export default App;
