import React from 'react';
import './SubscriptionPlans.css';

const SubscriptionPlans = () => {
  const plans = [
    {
      title: 'One Meal a Day',
      price: '₹120',
      period: '/day',
      description: 'Lunch or Dinner',
      features: [
        'Choose your meal of the day',
        'Minimum commitment'
      ]
    },
    {
      title: 'Two Meals a Day',
      price: '₹220',
      period: '/day',
      description: 'Lunch and Dinner',
      features: [
        'Intermittent fasting friendly',
        'Non-veg & veg options'
      ]
    },
    {
      title: 'Three Meals a Day',
      price: '₹250',
      period: '/day',
      description: 'Breakfast, Lunch and Dinner',
      features: [
        'Complete daily nutrition',
        'Best value for money'
      ]
    }
  ];

  return (
    <section className="subscription-plans" id="subscription">
      <div className="container">
        <div className="section-title">
          <h2>Choose Your Plan</h2>
          <p>Select what best fits your lifestyle and budget.</p>
        </div>

        <div className="plan-cards">
          {plans.map((plan, index) => (
            <div className="plan-card" key={index}>
              <div className="plan-header">
                <h3>{plan.title}</h3>
                <div className="plan-price">
                  {plan.price}<span>{plan.period}</span>
                </div>
                <p>{plan.description}</p>
              </div>

              <div className="plan-features">
                <ul>
                  {plan.features.map((feature, i) => (
                    <li key={i}>{feature}</li>
                  ))}
                </ul>
              </div>

              <div className="plan-footer">
                <p>Prices include delivery.</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default SubscriptionPlans;
