import React, { useRef } from "react";
import { motion } from "framer-motion";
import './HowItWorks.css';

export default function HowItWorks() {
  const handleWhatsAppContact = () => {
    // Replace with your actual WhatsApp number
    const phoneNumber = '+918123456789'; // Update this with your actual WhatsApp number
    const message = encodeURIComponent('Hi! I\'m interested in Myna Kitchen\'s meal plans and would like to discuss my requirements to choose the best plan for me.');
    const whatsappUrl = `https://wa.me/${phoneNumber}?text=${message}`;

    window.open(whatsappUrl, '_blank');
  };

  const steps = [
    {
      number: "01",
      title: "Browse Plans",
      description: "Explore our delicious meal plans and find the perfect fit for your lifestyle and dietary needs.",
      accent: "#825F45", // Primary brown
      bgGradient: "bg-gradient-to-br from-gold-50 to-white",
      image: "/images/how-it-works/1.png",
      imageAlt: "Browse meal plans interface showing various food options"
    },
    {
      number: "02", 
      title: "Connect on WhatsApp",
      description: "Get in touch with our team to discuss your requirements and get personalized meal recommendations.",
      accent: "#D08C60", // Warm orange
      bgGradient: "bg-gradient-to-br from-warmOrange-50 to-white",
      image: "/images/how-it-works/2.png",
      imageAlt: "WhatsApp chat interface for customer support",
      hasButton: true
    },
    {
      number: "03",
      title: "Easy Registration",
      description: "Complete your registration using our simple form and receive your personalized user dashboard via email. Sign up takes just a few minutes and you'll get instant access to your account.",
      accent: "#797D62", // Sage green
      bgGradient: "bg-gradient-to-br from-sage-50 to-white",
      image: "/images/how-it-works/3.png",
      imageAlt: "User registration form and dashboard preview"
    },
    {
      number: "04",
      title: "Manage Everything",
      description: "Use your personalized dashboard to manage everything - add or cancel subscriptions, change delivery addresses, order delicious add-ons, and customize your meal preferences all in one place.",
      accent: "#997B66", // Muted brown
      bgGradient: "bg-gradient-to-br from-mutedBrown-50 to-white",
      image: "/images/how-it-works/4.png",
      imageAlt: "User dashboard showing subscription management features"
    }
  ];

  // Simple, clean card component without complex stacking
  const StepCard = ({ step, index }) => {
    return (
      <motion.div 
        className="w-full max-w-4xl mx-auto mb-12 md:mb-16 how-it-works-card"
        initial={{ 
          opacity: 0, 
          y: 60
        }}
        whileInView={{ 
          opacity: 1, 
          y: 0
        }}
        viewport={{ 
          once: true,
          amount: 0.2
        }}
        transition={{ 
          duration: 0.5, 
          delay: index * 0.1,
          ease: [0.21, 0.47, 0.32, 0.98]
        }}
      >
        <div 
          className={`${step.bgGradient} border-2 border-black shadow-xl md:shadow-2xl rounded-2xl md:rounded-3xl overflow-hidden hover:shadow-2xl md:hover:shadow-3xl transition-shadow duration-300`}
        >
          {/* Card Header with Step Number */}
          <div className="px-4 md:px-6 lg:px-8 pt-4 md:pt-6 lg:pt-8 pb-3 md:pb-4">
            <div className="flex items-center justify-between mb-3 md:mb-4">
              <motion.div 
                className="flex items-center justify-center w-10 h-10 md:w-12 md:h-12 lg:w-16 lg:h-16 rounded-full border-2 md:border-3 border-black bg-white shadow-lg"
                style={{ backgroundColor: step.accent }}
                initial={{ scale: 0.8, opacity: 0 }}
                whileInView={{ scale: 1, opacity: 1 }}
                viewport={{ once: true }}
                transition={{ 
                  duration: 0.4, 
                  delay: 0.2 + index * 0.1,
                  ease: "easeOut"
                }}
              >
                <span className="text-white font-black text-lg md:text-xl lg:text-2xl">{step.number}</span>
              </motion.div>
              <div className="flex-1 ml-3 md:ml-4 lg:ml-6">
                <motion.h3 
                  className="text-xl md:text-2xl lg:text-3xl xl:text-4xl font-black text-black mb-1 md:mb-2 tracking-tight"
                  initial={{ opacity: 0, x: 20 }}
                  whileInView={{ opacity: 1, x: 0 }}
                  viewport={{ once: true }}
                  transition={{ 
                    duration: 0.4, 
                    delay: 0.3 + index * 0.1,
                    ease: "easeOut"
                  }}
                >
                  {step.title}
                </motion.h3>
              </div>
            </div>
          </div>

          {/* Main Content Area */}
          <div className="grid lg:grid-cols-2 gap-4 md:gap-6 lg:gap-8 px-4 md:px-6 lg:px-8 pb-4 md:pb-6 lg:pb-8">
            {/* Image Section */}
            <motion.div 
              className="order-2 lg:order-1 flex items-center justify-center p-2 md:p-4"
              initial={{ opacity: 0, scale: 0.95 }}
              whileInView={{ opacity: 1, scale: 1 }}
              viewport={{ once: true }}
              transition={{ 
                duration: 0.4, 
                delay: 0.4 + index * 0.1,
                ease: "easeOut"
              }}
            >
              <div className="relative w-full max-w-xs md:max-w-sm lg:max-w-md mx-auto">
                {/* Fixed aspect ratio container for consistent display */}
                <div className="how-it-works-image-container">
                  <div className="absolute inset-0 bg-gradient-to-br from-black/5 to-black/10" />
                  <img
                    src={step.image}
                    alt={step.imageAlt}
                    className="how-it-works-image"
                    loading="lazy"
                    onError={(e) => {
                      console.error(`Failed to load image: ${step.image}`);
                      e.target.style.backgroundColor = '#f3f4f6';
                      e.target.style.display = 'flex';
                      e.target.style.alignItems = 'center';
                      e.target.style.justifyContent = 'center';
                      e.target.innerHTML = 'ðŸ“· Image Loading...';
                    }}
                    onLoad={(e) => {
                      console.log(`Successfully loaded image: ${step.image}`);
                      e.target.style.opacity = '1';
                    }}
                  />
                  {/* Decorative overlay */}
                  <div className="absolute inset-0 bg-gradient-to-t from-black/10 via-transparent to-transparent" />
                </div>
              </div>
            </motion.div>

            {/* Text Content Section */}
            <div className="order-1 lg:order-2 flex flex-col justify-center space-y-4 md:space-y-6">
              <motion.p 
                className="text-black text-sm md:text-base lg:text-lg xl:text-xl leading-relaxed"
                initial={{ opacity: 0 }}
                whileInView={{ opacity: 1 }}
                viewport={{ once: true }}
                transition={{ 
                  duration: 0.4, 
                  delay: 0.5 + index * 0.1,
                  ease: "easeOut"
                }}
              >
                {step.description}
              </motion.p>

              {/* WhatsApp Button */}
              {step.hasButton && (
                <motion.div
                  initial={{ opacity: 0 }}
                  whileInView={{ opacity: 1 }}
                  viewport={{ once: true }}
                  transition={{ 
                    duration: 0.4, 
                    delay: 0.6 + index * 0.1,
                    ease: "easeOut"
                  }}
                >
                  <motion.button
                    onClick={handleWhatsAppContact}
                    className="w-full lg:w-auto px-4 md:px-6 lg:px-8 py-2.5 md:py-3 lg:py-4 bg-accent hover:bg-warmOrange-600 text-white font-bold text-sm md:text-base border-2 border-accent hover:border-warmOrange-600 transition-all duration-200 flex items-center justify-center gap-2 md:gap-3 rounded-lg md:rounded-xl shadow-lg hover:shadow-xl"
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    transition={{ duration: 0.2 }}
                  >
                    <span className="text-sm md:text-base lg:text-lg">ðŸ’¬</span>
                    <span>Contact on WhatsApp</span>
                  </motion.button>
                </motion.div>
              )}

              {/* Feature highlight */}
              <motion.div 
                className="border-t border-tertiary/30 pt-3 md:pt-4"
                initial={{ opacity: 0 }}
                whileInView={{ opacity: 1 }}
                viewport={{ once: true }}
                transition={{ 
                  duration: 0.4, 
                  delay: 0.7 + index * 0.1,
                  ease: "easeOut"
                }}
              >
                <div className="flex items-center">
                  <div 
                    className="w-2.5 h-2.5 md:w-3 md:h-3 rounded-full mr-2 md:mr-3 shadow-sm" 
                    style={{backgroundColor: step.accent}}
                  />
                  <span className="text-xs md:text-sm lg:text-base text-tertiary uppercase tracking-wider font-bold">
                    {index === 0 && "Multiple Plans Available"}
                    {index === 1 && "Instant Support Available"}
                    {index === 2 && "Quick & Easy Process"}
                    {index === 3 && "Complete Control & Flexibility"}
                  </span>
                </div>
              </motion.div>
            </div>
          </div>
        </div>
      </motion.div>
    );
  };

  return (
    <section id="how-it-works" className="relative bg-highlight/10">
      {/* Simplified background decoration */}
      <div className="absolute inset-0 opacity-[0.02]">
        <div className="absolute top-20 left-10 w-32 h-32 bg-primary rounded-full blur-3xl" />
        <div className="absolute bottom-20 right-10 w-40 h-40 bg-secondary rounded-full blur-3xl" />
      </div>

      {/* Header Section */}
      <div className="container mx-auto px-4 md:px-6">
        <motion.div 
          className="text-center flex flex-col justify-center py-12 md:py-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6, ease: "easeOut" }}
        >
          <h2 className="text-3xl md:text-4xl lg:text-5xl font-black text-black mb-4 tracking-tight">
            How It Works
          </h2>
          <p className="text-base md:text-lg text-primary max-w-2xl mx-auto leading-relaxed">
            From browsing to enjoying delicious meals at your doorstep - we've made it incredibly simple in just 4 easy steps.
          </p>
        </motion.div>
      </div>

      {/* Steps Container - Simple layout */}
      <div className="container mx-auto px-4 md:px-6 pb-20">
        {steps.map((step, index) => (
          <StepCard 
            key={index}
            step={step} 
            index={index}
          />
        ))}
      </div>

      {/* Call to Action */}
      <div className="container mx-auto px-4 md:px-6 pb-16 md:pb-24">
        <motion.div 
          className="text-center"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          <div className="bg-gradient-to-br from-white to-sage-50 border-2 border-black shadow-2xl p-8 md:p-10 max-w-2xl mx-auto rounded-2xl hover:shadow-3xl transition-shadow duration-300">
            <h3 className="text-2xl md:text-3xl font-black text-black mb-4 md:mb-6 tracking-tight">Ready to get started?</h3>
            <p className="text-primary mb-6 md:mb-8 text-sm md:text-base leading-relaxed">Join hundreds of satisfied customers enjoying fresh, delicious meals delivered daily.</p>
            <motion.button 
              onClick={() => {
                const section = document.getElementById('subscription-plans');
                if (section) {
                  window.scrollTo({
                    top: section.offsetTop - 80,
                    behavior: 'smooth'
                  });
                }
              }}
              className="w-full md:w-auto px-8 md:px-12 py-4 md:py-5 bg-primary text-white font-black text-base md:text-lg border-2 border-primary hover:bg-brown-600 hover:border-brown-600 transition-all duration-200 rounded-lg shadow-lg hover:shadow-xl"
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              transition={{ duration: 0.2 }}
            >
              Sign up now
            </motion.button>
          </div>
        </motion.div>
      </div>
    </section>
  );
}
