import React from 'react';
import './Testimonials.css';

const Testimonials = () => {
  const testimonials = [
    {
      text: "The food reminds me of my mom's cooking. The delivery is mostly on time, and food is spicy!",
      author: "Naveen",
      position: "IT Professional, Tidel Park"
    },
    {
      text: "I've been eating from Myna for the past month, and I'm impressed with their taste. I get good food, and I don't have to doomscroll Swiggy anymore!",
      author: "Robin",
      position: "Software Engineer, World Trade Center"
    },
    {
      text: "The variety of options is amazing! I never get bored with their menu.",
      author: "Amit Kumar",
      position: "College Student, Shollinganallur"
    }
  ];

  return (
    <section className="testimonials">
      <div className="container">
        <div className="section-title">
          <h2>What Our Customers Say</h2>
          <p>Don't just take our word for it</p>
        </div>
        <div className="testimonial-grid">
          {testimonials.map((testimonial, index) => (
            <div className="testimonial-card" key={index}>
              <p className="testimonial-text">{testimonial.text}</p>
              <div className="testimonial-author">
                <div className="author-info">
                  <h4>{testimonial.author}</h4>
                  <p>{testimonial.position}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Testimonials;
