import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import './ProblemStatement.css';

const ProblemStatement = () => {
  const [isSticky, setIsSticky] = useState(false);
  const [visibleBoxes, setVisibleBoxes] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;

      const solutionSection = document.querySelector('.sticky-solution-section');
      if (solutionSection) {
        const rect = solutionSection.getBoundingClientRect();
        const sectionTopPosition = currentScrollY + rect.top;

        // Check if section should be sticky
        const shouldBeSticky = currentScrollY >= sectionTopPosition;
        setIsSticky(shouldBeSticky);

        if (shouldBeSticky) {
          // Calculate which boxes should be visible based on scroll progress
          const scrollProgress = currentScrollY - sectionTopPosition;
          const boxRevealDistance = 350; // Distance to scroll for each box

          if (scrollProgress < boxRevealDistance) {
            setVisibleBoxes(1);
          } else if (scrollProgress < boxRevealDistance * 2) {
            setVisibleBoxes(2);
          } else if (scrollProgress < boxRevealDistance * 3) {
            setVisibleBoxes(3);
          } else if (scrollProgress < boxRevealDistance * 4) {
            setVisibleBoxes(4);
          } else if (scrollProgress < boxRevealDistance * 5) {
            // Keep all boxes visible for a while
            setVisibleBoxes(4);
          } else {
            // Release sticky after all boxes are shown and user scrolled more
            setIsSticky(false);
          }
        } else {
          setVisibleBoxes(0);
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Initial call

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const problems = [
    {
      id: 'busy',
      title: 'Too Busy to Cook',
      description: 'Your schedule is packed. Between work, commute, and life, who has time to plan, shop, and cook?',
      icon: '⏰',
      fact: 'Average person works 8+ hours daily'
    },
    {
      id: 'junk',
      title: 'Stuck with Junk Food',
      description: 'Quick fixes lead to unhealthy choices. Fast food becomes the norm when convenience matters most.',
      icon: '🍔',
      fact: '70% of daily intake is processed food'
    },
    {
      id: 'expensive',
      title: 'Expensive & Wasteful',
      description: 'Ordering out daily burns through your budget. Groceries expire unused. Money down the drain.',
      icon: '💸',
      fact: 'Spend ₹500+ daily on food delivery'
    },
    {
      id: 'away',
      title: 'Away from Home',
      description: 'Living away from family means no home-cooked meals. Missing that comfort and nutrition.',
      icon: '🏠',
      fact: 'Missing the comfort of home-cooked meals'
    }
  ];

  const solutionFeatures = [
    {
      icon: '🍽️',
      title: 'Daily Meals Delivered',
      description: 'Fresh, home-style meals prepared daily and delivered right to your doorstep',
      gradient: 'from-blue-500 to-purple-600',
      bgColor: 'bg-blue-50',
      iconBg: 'bg-blue-100'
    },
    {
      icon: '⏰',
      title: 'Save Your Time',
      description: 'No more planning, shopping, or cooking. We handle everything while you focus on what matters',
      gradient: 'from-green-500 to-teal-600',
      bgColor: 'bg-green-50',
      iconBg: 'bg-green-100'
    },
    {
      icon: '💰',
      title: 'Budget-Friendly',
      description: 'Starting at ₹120/day - more affordable than ordering out, with zero waste',
      gradient: 'from-yellow-500 to-orange-600',
      bgColor: 'bg-yellow-50',
      iconBg: 'bg-yellow-100'
    },
    {
      icon: '🏠',
      title: 'Home-Style Comfort',
      description: 'Taste the comfort of home-cooked meals, even when you\'re away from family',
      gradient: 'from-pink-500 to-red-600',
      bgColor: 'bg-pink-50',
      iconBg: 'bg-pink-100'
    }
  ];

  return (
    <section id="problem-statement" className="problem-section">
      <div className="container mx-auto px-4 md:px-6">
        {/* Problem Section Header */}
        <motion.div 
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <motion.h2 
            className="problem-title"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            We Know Your <span className="text-red-600">Daily Struggle</span>
          </motion.h2>
          <motion.p 
            className="problem-subtitle"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            Modern life shouldn't mean compromising on nutrition and taste
          </motion.p>
        </motion.div>

        {/* Problems List */}
        <div className="problems-list">
          {problems.map((problem, index) => (
            <motion.div
              key={problem.id}
              className="problem-item"
              initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
              whileInView={{ opacity: 1, x: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8, delay: index * 0.2 }}
            >
              <motion.div 
                className="problem-icon-large"
                initial={{ scale: 0, rotate: -180 }}
                whileInView={{ scale: 1, rotate: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6, delay: 0.3 + index * 0.2 }}
              >
                {problem.icon}
              </motion.div>

              <div className="problem-content-text">
                <motion.h3 
                  className="problem-title-large"
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: 0.4 + index * 0.2 }}
                >
                  {problem.title}
                </motion.h3>

                <motion.p 
                  className="problem-description-text"
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: 0.5 + index * 0.2 }}
                >
                  {problem.description}
                </motion.p>

                <motion.div 
                  className="problem-fact"
                  initial={{ opacity: 0, scale: 0.8 }}
                  whileInView={{ opacity: 1, scale: 1 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.5, delay: 0.6 + index * 0.2 }}
                >
                  {problem.fact}
                </motion.div>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Transition to Solution */}
        <motion.div 
          className="problem-cta"
          initial={{ opacity: 0, y: 30 }}
