import React, { useRef, useEffect, useState } from "react";
import { motion, useInView, useScroll, useTransform } from "framer-motion";

export default function HowItWorks() {
  const containerRef = useRef(null);
  const stackedCardsRef = useRef(null);
  const isInView = useInView(containerRef, { once: true, amount: 0.2 });

  // Simplified scroll progress for the entire section
  const { scrollYProgress } = useScroll({
    target: stackedCardsRef,
    offset: ["start center", "end center"]
  });

  const handleWhatsAppContact = () => {
    // Replace with your actual WhatsApp number
    const phoneNumber = '+918123456789'; // Update this with your actual WhatsApp number
    const message = encodeURIComponent('Hi! I\'m interested in Myna Kitchen\'s meal plans and would like to discuss my requirements to choose the best plan for me.');
    const whatsappUrl = `https://wa.me/${phoneNumber}?text=${message}`;

    window.open(whatsappUrl, '_blank');
  };

  const steps = [
    {
      number: "01",
      title: "Browse Plans",
      description: "Explore our delicious meal plans and find the perfect fit for your lifestyle and dietary needs.",
      accent: "#825F45", // Primary brown
      bgGradient: "bg-gradient-to-br from-gold-50 to-white",
      image: "/images/how-it-works/1.png",
      imageAlt: "Browse meal plans interface showing various food options"
    },
    {
      number: "02", 
      title: "Connect on WhatsApp",
      description: "Get in touch with our team to discuss your requirements and get personalized meal recommendations.",
      accent: "#D08C60", // Warm orange
      bgGradient: "bg-gradient-to-br from-warmOrange-50 to-white",
      image: "/images/how-it-works/2.png",
      imageAlt: "WhatsApp chat interface for customer support",
      hasButton: true
    },
    {
      number: "03",
      title: "Easy Registration",
      description: "Complete your registration using our simple form and receive your personalized user dashboard via email. Sign up takes just a few minutes and you'll get instant access to your account.",
      accent: "#797D62", // Sage green
      bgGradient: "bg-gradient-to-br from-sage-50 to-white",
      image: "/images/how-it-works/3.png",
      imageAlt: "User registration form and dashboard preview"
    },
    {
      number: "04",
      title: "Manage Everything",
      description: "Use your personalized dashboard to manage everything - add or cancel subscriptions, change delivery addresses, order delicious add-ons, and customize your meal preferences all in one place.",
      accent: "#997B66", // Muted brown
      bgGradient: "bg-gradient-to-br from-mutedBrown-50 to-white",
      image: "/images/how-it-works/4.png",
      imageAlt: "User dashboard showing subscription management features"
    }
  ];

  const StackedCard = ({ step, index, totalCards }) => {
    const cardRef = useRef(null);

    // Simplified card progress calculation
    const cardProgress = useTransform(
      scrollYProgress,
      [index / totalCards, (index + 1) / totalCards],
      [0, 1]
    );

    // Smoother and more subtle stacking transforms - only apply after initial animation
    const stackY = useTransform(cardProgress, [0, 0.6, 1], [
      0,           // Initial position
      0,           // Hold position longer
      -200         // Slide up less dramatically
    ]);

    const stackOpacity = useTransform(cardProgress, [0, 0.9, 1], [1, 1, 0]);

    const stackScale = useTransform(cardProgress, [0, 0.8, 1], [
      1 - (index * 0.01), // More subtle stacked effect
      1 - (index * 0.01), // Maintain scale
      0.95                 // Less dramatic shrink on exit
    ]);

    return (
      <motion.div 
        ref={cardRef}
        className="sticky w-full max-w-4xl mx-auto"
        style={{
          top: `${80 + index * 2}px`, // Reduced offset for smoother stacking
          zIndex: totalCards - index + 10,
          y: stackY,
          opacity: stackOpacity,
          scale: stackScale
        }}
      >
        <motion.div 
          className={`${step.bgGradient} border-2 border-black shadow-2xl rounded-3xl overflow-hidden transition-shadow duration-200 hover:shadow-3xl`}
          initial={{ 
            opacity: 0, 
            y: 60,
            scale: 0.95
          }}
          whileInView={{ 
            opacity: 1, 
            y: 0,
            scale: 1
          }}
          viewport={{ 
            once: true,
            amount: 0.3,
            margin: "-100px"
          }}
          transition={{ 
            duration: 0.7, 
            delay: index * 0.2,
            ease: [0.25, 0.25, 0.25, 1]
          }}
        >
          {/* Card Header with Step Number */}
          <div className="px-6 md:px-8 pt-6 md:pt-8 pb-4">
            <div className="flex items-center justify-between mb-4">
              <motion.div 
                className="flex items-center justify-center w-12 h-12 md:w-16 md:h-16 rounded-full border-3 border-black bg-white shadow-lg"
                style={{ backgroundColor: step.accent }}
                initial={{ scale: 0, rotate: -180 }}
                whileInView={{ scale: 1, rotate: 0 }}
                viewport={{ once: true }}
                transition={{ 
                  duration: 0.6, 
                  delay: 0.3 + index * 0.2,
                  ease: "easeOut",
                  type: "spring",
                  stiffness: 150,
                  damping: 15
                }}
              >
                <span className="text-white font-black text-xl md:text-2xl">{step.number}</span>
              </motion.div>
              <div className="flex-1 ml-4 md:ml-6">
                <motion.h3 
                  className="text-2xl md:text-3xl lg:text-4xl font-black text-black mb-2 tracking-tight"
                  initial={{ opacity: 0, x: 30 }}
                  whileInView={{ opacity: 1, x: 0 }}
                  viewport={{ once: true }}
                  transition={{ 
                    duration: 0.5, 
                    delay: 0.4 + index * 0.2,
                    ease: "easeOut"
                  }}
                >
                  {step.title}
                </motion.h3>
              </div>
            </div>
          </div>

          {/* Main Content Area */}
          <div className="grid lg:grid-cols-2 gap-6 md:gap-8 px-6 md:px-8 pb-6 md:pb-8">
            {/* Image Section */}
            <motion.div 
              className="order-2 lg:order-1 flex items-center justify-center"
              initial={{ opacity: 0, scale: 0.9 }}
              whileInView={{ opacity: 1, scale: 1 }}
              viewport={{ once: true }}
              transition={{ 
                duration: 0.5, 
                delay: 0.5 + index * 0.2,
                ease: "easeOut"
              }}
            >
              <div className="relative w-full max-w-md aspect-square">
                <div className="absolute inset-0 bg-gradient-to-br from-black/5 to-black/10 rounded-2xl" />
                <img
                  src={step.image}
                  alt={step.imageAlt}
                  className="w-full h-full object-cover rounded-2xl shadow-xl border-2 border-white/20"
                  loading="lazy"
                />
                {/* Decorative overlay */}
                <div className="absolute inset-0 bg-gradient-to-t from-black/10 via-transparent to-transparent rounded-2xl" />
              </div>
            </motion.div>

            {/* Text Content Section */}
            <div className="order-1 lg:order-2 flex flex-col justify-center space-y-6">
              <motion.p 
                className="text-black text-base md:text-lg lg:text-xl leading-relaxed"
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ 
                  duration: 0.5, 
                  delay: 0.6 + index * 0.2,
                  ease: "easeOut"
                }}
              >
                {step.description}
              </motion.p>

              {/* WhatsApp Button */}
              {step.hasButton && (
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ 
                    duration: 0.5, 
                    delay: 0.7 + index * 0.2,
                    ease: "easeOut"
                  }}
                >
                  <motion.button
                    onClick={handleWhatsAppContact}
                    className="w-full lg:w-auto px-6 md:px-8 py-3 md:py-4 bg-accent hover:bg-warmOrange-600 text-white font-bold text-sm md:text-base border-2 border-accent hover:border-warmOrange-600 transition-all duration-150 flex items-center justify-center gap-3 rounded-xl shadow-lg hover:shadow-xl"
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    transition={{ duration: 0.2 }}
                  >
                    <span className="text-base md:text-lg">💬</span>
                    <span>Contact on WhatsApp</span>
                  </motion.button>
                </motion.div>
              )}

              {/* Feature highlight */}
              <motion.div 
                className="border-t border-tertiary/30 pt-4"
                initial={{ opacity: 0, scale: 0.9 }}
                whileInView={{ opacity: 1, scale: 1 }}
                viewport={{ once: true }}
                transition={{ 
                  duration: 0.4, 
                  delay: 0.8 + index * 0.2,
                  ease: "easeOut"
                }}
              >
                <div className="flex items-center">
                  <div 
                    className="w-3 h-3 rounded-full mr-3 shadow-sm" 
                    style={{backgroundColor: step.accent}}
                  />
                  <span className="text-sm md:text-base text-tertiary uppercase tracking-wider font-bold">
                    {index === 0 && "Multiple Plans Available"}
                    {index === 1 && "Instant Support Available"}
                    {index === 2 && "Quick & Easy Process"}
                    {index === 3 && "Complete Control & Flexibility"}
                  </span>
                </div>
              </motion.div>
            </div>
          </div>
        </motion.div>
      </motion.div>
    );
  };

  return (
    <section id="how-it-works" className="relative bg-highlight/10">
      {/* Simplified background decoration */}
      <div className="absolute inset-0 opacity-[0.02]">
        <div className="absolute top-20 left-10 w-32 h-32 bg-primary rounded-full blur-3xl" />
        <div className="absolute bottom-20 right-10 w-40 h-40 bg-secondary rounded-full blur-3xl" />
      </div>

      {/* Header Section - Simplified animations */}
      <div className="container mx-auto px-4 md:px-6">
        <motion.div 
          className="text-center flex flex-col justify-center py-12 md:py-16"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, ease: "easeOut" }}
        >
          <h2 className="text-3xl md:text-4xl lg:text-5xl font-black text-black mb-4 tracking-tight">
            How It Works
          </h2>
          <p className="text-base md:text-lg text-primary max-w-2xl mx-auto leading-relaxed">
            From browsing to enjoying delicious meals at your doorstep - we've made it incredibly simple in just 4 easy steps.
          </p>
        </motion.div>
      </div>

      {/* Stacked Cards Container - Optimized height calculation */}
      <div 
        ref={stackedCardsRef} 
        className="relative px-4 md:px-6 pb-20"
        style={{ height: `${steps.length * 120}vh` }}
      >
        <div ref={containerRef} className="relative h-full">
          {steps.map((step, index) => (
            <StackedCard 
              key={index}
              step={step} 
              index={index} 
              totalCards={steps.length}
            />
          ))}
        </div>
      </div>

      {/* Call to Action - Simplified */}
      <div className="container mx-auto px-4 md:px-6 pb-16 md:pb-24">
        <motion.div 
          className="text-center"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5 }}
        >
          <div className="bg-gradient-to-br from-white to-sage-50 border-2 border-black shadow-2xl p-8 md:p-10 max-w-2xl mx-auto rounded-2xl hover:shadow-3xl transition-shadow duration-200">
            <h3 className="text-2xl md:text-3xl font-black text-black mb-4 md:mb-6 tracking-tight">Ready to get started?</h3>
            <p className="text-primary mb-6 md:mb-8 text-sm md:text-base leading-relaxed">Join hundreds of satisfied customers enjoying fresh, delicious meals delivered daily.</p>
            <motion.button 
              onClick={() => {
                const section = document.getElementById('subscription-plans');
                if (section) {
                  window.scrollTo({
                    top: section.offsetTop - 80,
                    behavior: 'smooth'
                  });
                }
              }}
              className="w-full md:w-auto px-8 md:px-12 py-4 md:py-5 bg-primary text-white font-black text-base md:text-lg border-2 border-primary hover:bg-brown-600 hover:border-brown-600 transition-all duration-200 rounded-lg shadow-lg hover:shadow-xl"
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              transition={{ duration: 0.2 }}
            >
              Sign up now
            </motion.button>
          </div>
        </motion.div>
      </div>
    </section>
  );
}
