import React from 'react';
import { motion } from 'framer-motion';
import './FeaturesSection.css';

const FeaturesSection = () => {
  const features = [
    {
      icon: 'üçΩÔ∏è',
      title: 'Daily Meals Delivered',
      description: 'Fresh, home-style meals prepared daily and delivered right to your doorstep'
    },
    {
      icon: '‚è∞',
      title: 'Save Your Time',
      description: 'No more planning, shopping, or cooking. We handle everything while you focus on what matters'
    },
    {
      icon: 'üí∞',
      title: 'Budget-Friendly',
      description: 'Starting at ‚Çπ120/day - more affordable than ordering out, with zero waste'
    },
    {
      icon: 'üè†',
      title: 'Home-Style Comfort',
      description: 'Taste the comfort of home-cooked meals, even when you\'re away from family'
    }
  ];

  return (
    <section className="features-section">
      {/* Initial vertical layout */}
      <div className="features-initial-layout">
        {features.map((feature, index) => (
          <motion.div
            key={`initial-${index}`}
            className="feature-card-initial"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6, delay: index * 0.2 }}
          >
            <div className="feature-icon">{feature.icon}</div>
            <h3 className="feature-title">{feature.title}</h3>
            <p className="feature-description">{feature.description}</p>
          </motion.div>
        ))}
      </div>

      {/* Sticky horizontal row */}
      <div className="sticky-features-container">
        <div className="sticky-features-row">
          {features.map((feature, index) => (
            <div key={`sticky-${index}`} className="sticky-feature-card">
              <div className="feature-icon">{feature.icon}</div>
              <h3 className="feature-title">{feature.title}</h3>
              <p className="feature-description">{feature.description}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Spacer for scroll effect */}
      <div className="features-scroll-spacer"></div>
    </section>
  );
};

export default FeaturesSection;
