import React, { useState, useRef } from "react";
import { motion, useInView } from "framer-motion";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faChevronDown, 
  faQuestionCircle,
  faTruck,
  faUtensils,
  faPause,
  faThermometerHalf,
  faLeaf
} from '@fortawesome/free-solid-svg-icons';

const FAQ_ITEMS = [
  {
    id: "faq-1",
    question: "How does the delivery work?",
    answer: "We deliver meals to your home or office once a day at a specified time window. You can choose your preferred delivery time during checkout. Our insulated packaging keeps the food at the right temperature until you're ready to eat.",
    icon: faTruck,
    color: "from-blue-500 to-cyan-500"
  },
  {
    id: "faq-2",
    question: "Can I customize my meals or select preferences?",
    answer: "Yes, you can select your meal preferences when you sign up. We offer vegetarian and non-vegetarian options, and you can also specify dietary restrictions. Our weekly menu has enough variety to accommodate most preferences.",
    icon: faUtensils,
    color: "from-green-500 to-emerald-500"
  },
  {
    id: "faq-3",
    question: "How do I pause or cancel my subscription?",
    answer: "You can pause or cancel your subscription anytime through your account. For pauses, we require at least 24 hours notice before your next scheduled delivery. For cancellations, you can cancel at any time, and you won't be charged for the following week.",
    icon: faPause,
    color: "from-yellow-500 to-orange-500"
  },
  {
    id: "faq-4",
    question: "Are the meals delivered hot and ready to eat?",
    answer: "Our meals are delivered fresh, not hot. They are prepared the same day and delivered in microwave-safe containers. You'll need to heat them up for 1-2 minutes before enjoying. This ensures maximum freshness and food safety.",
    icon: faThermometerHalf,
    color: "from-red-500 to-pink-500"
  },
  {
    id: "faq-5",
    question: "Do you use preservatives in your food?",
    answer: "No, we do not use any artificial preservatives in our meals. We use fresh ingredients and prepare the food on the same day as delivery, eliminating the need for preservatives.",
    icon: faLeaf,
    color: "from-purple-500 to-indigo-500"
  }
];

const FAQ = () => {
  const sectionRef = useRef(null);
  const isInView = useInView(sectionRef, { once: true, amount: 0.2 });

  return (
    <section id="faq" className="py-20 md:py-28 bg-gradient-to-br from-gray-50 to-white overflow-hidden relative">
      {/* Background decorative elements */}
      <div className="absolute inset-0 opacity-5">
        <div className="absolute top-20 right-20 w-64 h-64 bg-primary rounded-full blur-3xl"></div>
        <div className="absolute bottom-40 left-10 w-48 h-48 bg-secondary rounded-full blur-3xl"></div>
      </div>

      <div ref={sectionRef} className="container mx-auto px-4 md:px-6 relative z-10">
        {/* Header Section */}
        <motion.div
          className="text-center max-w-4xl mx-auto mb-20"
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
          transition={{ duration: 0.8 }}
        >
          <div className="inline-flex items-center bg-primary/10 text-primary px-6 py-3 rounded-full mb-6">
            <FontAwesomeIcon icon={faQuestionCircle} className="mr-2" />
            <span className="font-semibold">Got Questions?</span>
          </div>
          <h2 className="text-4xl md:text-6xl font-bold mb-6 text-gray-900">
            We've Got 
            <span className="text-primary"> Answers</span>
          </h2>
          <p className="text-lg md:text-xl text-gray-600 leading-relaxed">
            Everything you need to know about Myna Kitchen's meal delivery service, 
            from ordering to enjoying your delicious home-style meals.
          </p>
        </motion.div>

        {/* FAQ Grid */}
        <div className="max-w-5xl mx-auto">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {FAQ_ITEMS.map((item, index) => (
              <motion.div
                key={item.id}
                initial={{ opacity: 0, y: 30 }}
                animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
              >
                <FAQItem 
                  question={item.question} 
                  answer={item.answer} 
                  icon={item.icon}
                  color={item.color}
                />
              </motion.div>
            ))}
          </div>
        </div>

        {/* Bottom CTA */}
        <motion.div
          className="text-center max-w-2xl mx-auto mt-20"
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
          transition={{ duration: 0.8, delay: 0.6 }}
        >
          <div className="bg-white/80 backdrop-blur-sm rounded-3xl p-8 md:p-12 border border-gray-200/50 shadow-xl">
            <h3 className="text-2xl md:text-3xl font-bold mb-4 text-gray-900">
              Still have questions?
            </h3>
            <p className="text-gray-600 mb-8">
              Our friendly customer support team is here to help you with any other questions.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <motion.a
                href="mailto:kitchenmyna@gmail.com"
                className="inline-flex items-center justify-center px-8 py-4 bg-primary hover:bg-primary/90 text-white font-semibold rounded-xl shadow-lg transition-all duration-300"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                Email Support
              </motion.a>
              <motion.a
                href="tel:+919876543210"
                className="inline-flex items-center justify-center px-8 py-4 bg-white hover:bg-gray-50 text-gray-900 font-semibold rounded-xl shadow-lg border-2 border-gray-200 transition-all duration-300"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                Call Us
              </motion.a>
            </div>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default FAQ;

function FAQItem({ question, answer, icon, color }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <motion.div 
      className="bg-white/70 backdrop-blur-sm rounded-2xl border border-gray-200/50 shadow-lg hover:shadow-xl transition-all duration-300 overflow-hidden group"
      whileHover={{ y: -5 }}
    >
      <button
        className="flex w-full items-center p-6 text-left group-hover:bg-gray-50/50 transition-colors duration-300"
        onClick={() => setIsOpen(!isOpen)}
      >
        {/* Icon */}
        <div className={`w-12 h-12 rounded-xl bg-gradient-to-r ${color} flex items-center justify-center mr-4 flex-shrink-0 shadow-lg`}>
          <FontAwesomeIcon icon={icon} className="text-white text-lg" />
        </div>

        {/* Question */}
        <div className="flex-1">
          <h3 className="font-bold text-gray-900 text-lg leading-tight group-hover:text-primary transition-colors duration-300">
            {question}
          </h3>
        </div>

        {/* Chevron */}
        <motion.div
          animate={{ rotate: isOpen ? 180 : 0 }}
          transition={{ duration: 0.3 }}
          className="ml-4 flex-shrink-0"
        >
          <FontAwesomeIcon 
            icon={faChevronDown} 
            className="text-gray-400 group-hover:text-primary transition-colors duration-300" 
          />
        </motion.div>
      </button>

      {/* Answer */}
      <motion.div
        initial={{ height: 0, opacity: 0 }}
        animate={{ 
          height: isOpen ? "auto" : 0,
          opacity: isOpen ? 1 : 0
        }}
        transition={{ duration: 0.4, ease: "easeInOut" }}
        className="overflow-hidden"
      >
        <div className="px-6 pb-6 pl-[88px]">
          <motion.p 
            className="text-gray-600 leading-relaxed"
            initial={{ y: -10 }}
            animate={{ y: isOpen ? 0 : -10 }}
            transition={{ duration: 0.4, delay: isOpen ? 0.1 : 0 }}
          >
            {answer}
          </motion.p>
        </div>
      </motion.div>
    </motion.div>
  );
}
