];

const DailyMenu = () => {
  const [activeCategory, setActiveCategory] = useState("Add-ons");
  const scrollRef = useRef(null);
  const containerRef = useRef(null);
  const [currentPage, setCurrentPage] = useState(0);
  const [itemsPerPage, setItemsPerPage] = useState(3); // Default for desktop

  // Updated categories to match folder structure (only show categories with meals)
  const categories = ["Add-ons", "Super Meals", "Comfort Meals"];

  const filteredItems = MENU_ITEMS.filter(item => item.category === activeCategory);
  const pageCount = Math.ceil(filteredItems.length / itemsPerPage);

  // Calculate visible items for current page
  const visibleItems = filteredItems.slice(
    currentPage * itemsPerPage, 
    (currentPage + 1) * itemsPerPage
  );

  // Update itemsPerPage based on screen size
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 640) {
        setItemsPerPage(1);
      } else if (window.innerWidth < 1024) {
        setItemsPerPage(2);
      } else {
        setItemsPerPage(3);
      }
    };

    handleResize(); // Set initial value
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Reset current page when category changes
  useEffect(() => {
    setCurrentPage(0);
  }, [activeCategory]);

  // Smooth scroll to next/previous page
  const navigatePage = (direction) => {
    if (direction === "next" && currentPage < pageCount - 1) {
      setCurrentPage(prev => prev + 1);
    } else if (direction === "prev" && currentPage > 0) {
      setCurrentPage(prev => prev - 1);
    }
  };

  const getCategoryPrice = () => {
    const firstItem = filteredItems[0];
    return firstItem ? `â‚¹${firstItem.price}` : '';
  };

  return (
    <section id="daily-menu" className="py-16 md:py-20 bg-gradient-to-br from-gray-50 to-white">
      <div className="container mx-auto px-4 md:px-6">
        <motion.div 
          className="text-center mb-12"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true, margin: "-50px" }}
          transition={{ duration: 0.6 }}
        >
          <motion.h2 
            className="text-3xl md:text-4xl font-black text-gray-900 mb-4"
            initial={{ opacity: 0, y: 15 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6, delay: 0.1 }}
          >
            Today's Menu
          </motion.h2>
          <motion.p 
            className="text-lg text-gray-600 max-w-2xl mx-auto leading-relaxed"
            initial={{ opacity: 0, y: 15 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6, delay: 0.2 }}
          >
            Fresh meals prepared daily by our expert chefs using the finest ingredients
          </motion.p>
        </motion.div>

        {/* Category Navigation */}
        <motion.div 
          className="mb-8 flex justify-center"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6, delay: 0.3 }}
        >
          <div className="bg-gray-100 p-1 rounded-full shadow-inner">
            {categories.map((category, index) => (
              <button
                key={category}
                className={`px-4 py-2 md:px-6 rounded-full text-sm md:text-base font-medium transition-all ${
                  activeCategory === category
                    ? 'bg-white text-primary shadow-md'
                    : 'text-gray-600 hover:text-primary'
                }`}
                onClick={() => setActiveCategory(category)}
              >
                {category}
              </button>
            ))}
          </div>
        </motion.div>

        {/* Category Info */}
        <div className="text-center mb-6">
          <div className="inline-flex items-center justify-center bg-primary/10 rounded-full px-4 py-2 text-primary font-medium">
            {activeCategory === "Add-ons" && "Special additions to enhance your meal"}
            {activeCategory === "Super Meals" && "Nutrient-packed meals for optimal health"}
            {activeCategory === "Comfort Meals" && "Simple, satisfying comfort food"}
          </div>
        </div>

        {/* Price Info */}
        <div className="text-center mb-8 text-sm text-gray-600">
          * Subscribers receive Supermeals and Desserts as part of their daily plan at no extra cost.
        </div>

        {/* Menu Grid with improved layout */}
        <div ref={containerRef} className="relative mb-8">
          <motion.div
            className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 md:gap-6"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            {visibleItems.map((item, index) => (
              <motion.div
                key={item.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.4, delay: index * 0.05 }}
              >
                <MenuItemCard item={item} />
              </motion.div>
            ))}
          </motion.div>
        </div>

        {/* Navigation controls - redesigned to be more prominent */}
        <div className="flex justify-center items-center mb-6">
          <div className="flex items-center space-x-4">
            <motion.button
              type="button"
              onClick={() => navigatePage("prev")}
              className={`rounded-full w-10 h-10 flex items-center justify-center ${
                currentPage === 0 
                  ? 'bg-gray-100 text-gray-400 cursor-not-allowed' 
                  : 'bg-gray-200 text-gray-800 hover:bg-gray-300'
              }`}
              whileHover={currentPage > 0 ? { scale: 1.05 } : {}}
              whileTap={currentPage > 0 ? { scale: 0.95 } : {}}
            >
              <FontAwesomeIcon icon={faChevronLeft} className="w-4 h-4" />
            </motion.button>

            <div className="flex space-x-2">
              {Array.from({ length: pageCount }).map((_, index) => (
                <button
                  key={index}
                  onClick={() => setCurrentPage(index)}
                  className={`w-2.5 h-2.5 rounded-full transition-colors ${
                    currentPage === index ? 'bg-primary' : 'bg-gray-300'
                  }`}
                  aria-label={`Go to page ${index + 1}`}
                />
              ))}
            </div>

            <motion.button
              type="button"
              onClick={() => navigatePage("next")}
              className={`rounded-full w-10 h-10 flex items-center justify-center ${
                currentPage >= pageCount - 1 
                  ? 'bg-gray-100 text-gray-400 cursor-not-allowed' 
                  : 'bg-gray-200 text-gray-800 hover:bg-gray-300'
              }`}
              whileHover={currentPage < pageCount - 1 ? { scale: 1.05 } : {}}
              whileTap={currentPage < pageCount - 1 ? { scale: 0.95 } : {}}
            >
              <FontAwesomeIcon icon={faChevronRight} className="w-4 h-4" />
            </motion.button>
          </div>
        </div>
      </div>
    </section>
  );
};

