import React, { useRef } from "react";
import { motion, useInView } from "framer-motion";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faUtensils, 
  faCalendarCheck, 
  faTruckFast, 
  faHeart,
  faArrowRight
} from '@fortawesome/free-solid-svg-icons';

export default function HowItWorks() {
  const containerRef = useRef(null);
  const isInView = useInView(containerRef, { once: true, amount: 0.2 });

  const steps = [
    {
      number: "01",
      title: "Choose Your Plan",
      description: "Browse our meal plans and pick what works for your lifestyle. Whether you're a busy professional or a food enthusiast, we've got something perfect for you.",
      icon: faUtensils,
      highlight: "No commitment required",
      cardColor: "from-orange-50 to-orange-100"
    },
    {
      number: "02", 
      title: "Set Your Schedule",
      description: "Tell us when and where you'd like your meals delivered. Flexible scheduling means you're always in control of your food journey.",
      icon: faCalendarCheck,
      highlight: "Complete flexibility",
      cardColor: "from-blue-50 to-blue-100"
    },
    {
      number: "03",
      title: "We Deliver Fresh",
      description: "Our team prepares your meals fresh daily and delivers them right to your doorstep. Hot, delicious, and ready when you are.",
      icon: faTruckFast,
      highlight: "Always on time",
      cardColor: "from-green-50 to-green-100"
    },
    {
      number: "04",
      title: "Enjoy & Share",
      description: "Unpack, heat up, and enjoy your home-style meal. Love it? Share your experience with us - your feedback helps us serve you better.",
      icon: faHeart,
      highlight: "Your feedback matters",
      cardColor: "from-purple-50 to-purple-100"
    }
  ];

  return (
    <section id="how-it-works" className="py-16 md:py-24 bg-white overflow-hidden section-fade">
      <div className="container mx-auto px-4 md:px-6">
        {/* Header Section */}
        <div className="text-center max-w-4xl mx-auto mb-16">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6 }}
          >
            <div className="inline-flex items-center bg-primary/10 text-primary px-4 py-2 rounded-full mb-6">
              <span className="font-semibold">Simple Process</span>
            </div>
            <h2 className="text-3xl md:text-5xl font-bold mb-6 text-gray-900 leading-tight">
              From Craving to 
              <span className="text-primary"> Satisfaction</span>
            </h2>
            <p className="text-lg md:text-xl text-gray-600 leading-relaxed">
              Getting delicious, home-style meals has never been easier. Here's how we make it happen in just four simple steps.
            </p>
          </motion.div>
        </div>

        {/* Steps Section with Skewed Background */}
        <div className="relative mb-16">
          <div 
            className="absolute inset-0 bg-gray-50 skew-y-1 -z-10 rounded-3xl"
            style={{ transformOrigin: 'bottom left' }}
          ></div>

          <div ref={containerRef} className="py-16 px-4 md:px-8">
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 lg:gap-8">
            {steps.map((step, index) => (
              <motion.div
                key={index}
                  className="relative h-full"
                initial={{ opacity: 0, y: 50 }}
                animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 50 }}
                  transition={{ duration: 0.6, delay: index * 0.15 }}
              >
                  {/* Connecting Line */}
                  {index < steps.length - 1 && (
                    <div className="hidden lg:block absolute top-16 left-full w-full h-0.5 bg-primary/20 z-0">
                      <motion.div
                        className="h-full bg-primary/60"
                        initial={{ width: "0%" }}
                        animate={isInView ? { width: "100%" } : { width: "0%" }}
                        transition={{ duration: 0.8, delay: index * 0.2 + 0.5 }}
                      />
                    </div>
                  )}

                  <div className={`relative bg-gradient-to-br ${step.cardColor} border border-white/60 rounded-2xl p-6 shadow-lg hover:shadow-xl transition-all duration-300 group h-80 flex flex-col justify-between`}>
                    {/* Step Number and Icon */}
                    <div className="flex items-center justify-between mb-4">
                      <div className="w-14 h-14 bg-primary text-white rounded-2xl flex items-center justify-center font-bold text-lg shadow-md">
                        {step.number}
                  </div>
                      <div className="w-14 h-14 bg-white/80 backdrop-blur-sm rounded-2xl flex items-center justify-center group-hover:bg-white transition-colors shadow-sm">
                        <FontAwesomeIcon 
                          icon={step.icon} 
                          className="w-6 h-6 text-primary" 
                          />
                      </div>
                    </div>

                    {/* Content */}
                    <div className="flex-1 flex flex-col">
                      <h3 className="text-xl font-bold mb-3 text-gray-900">{step.title}</h3>
                      <p className="text-gray-700 mb-4 leading-relaxed text-sm flex-1">{step.description}</p>
                  </div>

                    {/* Highlight Badge */}
                    <div className="inline-flex items-center bg-white/90 backdrop-blur-sm text-primary px-3 py-2 rounded-full text-sm font-semibold shadow-sm border border-primary/20">
                      <div className="w-2 h-2 bg-primary rounded-full mr-2"></div>
                      {step.highlight}
                  </div>
                </div>
              </motion.div>
            ))}
            </div>
          </div>
        </div>

        {/* Call to Action Section */}
        <motion.div 
          className="text-center max-w-3xl mx-auto"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6, delay: 0.3 }}
        >
          <div className="bg-gradient-to-r from-primary/5 to-primary/10 rounded-2xl p-8 md:p-12 border border-primary/10">
            <h3 className="text-2xl md:text-3xl font-bold mb-4 text-gray-900">
              Ready to Start Your Food Journey?
            </h3>
            <p className="text-lg text-gray-600 mb-8">
              Join thousands of satisfied customers who've made the switch to convenient, delicious meals.
            </p>
            <motion.button 
              className="inline-flex items-center gap-3 bg-primary hover:bg-primary/90 text-white font-semibold px-8 py-4 rounded-xl shadow-lg transition-all duration-300 hover:shadow-xl hover:scale-105"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => {
                const section = document.getElementById('subscription-plans');
                if (section) {
                  window.scrollTo({
                    top: section.offsetTop - 80,
                    behavior: 'smooth'
                  });
                }
              }}
            >
              <span>Explore Our Plans</span>
              <FontAwesomeIcon icon={faArrowRight} className="w-4 h-4" />
            </motion.button>
          </div>
        </motion.div>
      </div>
    </section>
  );
}
