import React, { useState, useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes } from '@fortawesome/free-solid-svg-icons';
import { useThrottledScroll } from '../hooks/useThrottledScroll';
import { openSecureLink } from '../lib/utils';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  // Consolidate scroll state to reduce state updates
  const [scrollState, setScrollState] = useState({
    scrolled: false,
    isVisible: true
  });
  const lastScrollYRef = useRef(0);

  // Optimized scroll handler with consolidated state updates
  const handleScroll = () => {
    const currentScrollY = window.scrollY;
    const lastScrollY = lastScrollYRef.current;

    // Calculate new state values
    const newScrolled = currentScrollY > 10;
    let newIsVisible = true;

    // Determine scroll direction and visibility
    if (currentScrollY < 10) {
      // Always show header at the top of the page
      newIsVisible = true;
    } else if (currentScrollY > lastScrollY && currentScrollY > 100) {
      // Scrolling down & past 100px - hide header
      newIsVisible = false;
    } else if (currentScrollY < lastScrollY) {
      // Scrolling up - show header
      newIsVisible = true;
    }

    // Only update state if values have changed to prevent unnecessary re-renders
    if (scrollState.scrolled !== newScrolled || scrollState.isVisible !== newIsVisible) {
      setScrollState({
        scrolled: newScrolled,
        isVisible: newIsVisible
      });
    }

    // Update the ref with current scroll position
    lastScrollYRef.current = currentScrollY;
  };

  // Use optimized throttled scroll hook with reduced frequency
  useThrottledScroll(handleScroll, 50); // Reduced to 20fps for header updates

  const scrollToSection = (sectionId) => {
    try {
      const section = document.getElementById(sectionId);
      if (section) {
        window.scrollTo({
          top: section.offsetTop - 60,
          behavior: 'smooth'
        });
        setIsMenuOpen(false);
      }
    } catch (error) {
      console.error('Error scrolling to section:', error);
      // Fallback: try simple scroll
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  // Handle external link with security measures
  const handleExternalLink = (url) => {
    openSecureLink(url, {
      onError: (error) => {
        console.error('Failed to open signup link:', error);
        // Could show user notification here
      }
    });
  };

  return (
    <header className={`fixed top-0 left-0 right-0 z-50 bg-white fixed-element transition-all duration-300 ease-out ${
      scrollState.scrolled ? 'shadow-lg backdrop-blur-sm' : 'shadow-sm'
    } ${
      scrollState.isVisible ? 'translate-y-0' : '-translate-y-full'
    }`}>
      <div className="container mx-auto px-4 md:px-6 py-2 md:py-3">
        <div className="flex items-center justify-between min-h-[50px]">
          <button 
            onClick={() => scrollToSection('hero')} 
            className="flex items-center flex-shrink-0 scroll-optimized-child"
            aria-label="Go to home section"
          >
            <img 
              src="/images/myna-logo.jpg" 
              alt="Myna Kitchen Logo" 
              className="h-10 w-auto sm:h-12 md:h-14 lg:h-16 xl:h-18 object-contain transition-all duration-200 scroll-optimized-child"
              style={{ maxWidth: '250px' }}
              onError={(e) => {
                console.error('Logo image failed to load');
                e.target.style.display = 'none';
              }}
            />
          </button>

          {/* Mobile menu button */}
          <div className="flex md:hidden items-center space-x-3">
            <button 
              onClick={() => handleExternalLink('https://mynakitchen.in/SignUp')}
              className="bg-accent text-white px-3 py-1.5 rounded-full text-sm font-medium shadow-sm hover:bg-warmOrange-600 transition-colors min-h-[44px] min-w-[44px] flex items-center justify-center scroll-optimized-child"
              aria-label="Sign up for Myna Kitchen"
            >
              Sign Up
            </button>
            <button 
              type="button" 
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="flex items-center justify-center p-2 rounded-md text-tertiary hover:text-primary hover:bg-sage-50 focus:outline-none min-h-[44px] min-w-[44px] scroll-optimized-child"
              aria-expanded={isMenuOpen}
              aria-label={isMenuOpen ? 'Close menu' : 'Open menu'}
            >
              <span className="sr-only">Open main menu</span>
              {isMenuOpen ? (
                <FontAwesomeIcon icon={faTimes} className="block h-5 w-5" />
              ) : (
                <FontAwesomeIcon icon={faBars} className="block h-5 w-5" />
              )}
            </button>
          </div>

          {/* Desktop navigation */}
          <nav className="hidden md:flex items-center space-x-6 lg:space-x-8 scroll-optimized-child">
            <button 
              onClick={() => scrollToSection('hero')}
              className="text-black hover:text-primary font-medium transition-colors"
            >
              Home
            </button>
            <button 
              onClick={() => scrollToSection('how-it-works')}
              className="text-black hover:text-primary font-medium transition-colors"
            >
              How It Works
            </button>
            <button 
              onClick={() => scrollToSection('daily-menu')}
              className="text-black hover:text-primary font-medium transition-colors"
            >
              Menu
            </button>
            <button 
              onClick={() => scrollToSection('subscription-plans')}
              className="text-black hover:text-primary font-medium transition-colors"
            >
              Pricing
            </button>
            <button 
              onClick={() => scrollToSection('delivery-map')}
              className="text-black hover:text-primary font-medium transition-colors"
            >
              Delivery Areas
            </button>
            <button 
              onClick={() => handleExternalLink('https://mynakitchen.in/SignUp')}
              className="bg-accent text-white px-6 py-2.5 rounded-full font-medium shadow-sm hover:bg-warmOrange-600 transition-colors"
              aria-label="Sign up for Myna Kitchen"
            >
              Sign Up
            </button>
          </nav>
        </div>

        {/* Mobile navigation */}
        <div className={`pt-4 pb-3 border-t border-tertiary/20 md:hidden transition-all duration-200 ${isMenuOpen ? 'block opacity-100' : 'hidden opacity-0'}`}>
          <div className="space-y-1">
            <button 
              onClick={() => scrollToSection('hero')}
              className="block w-full text-left px-3 py-2 rounded-md text-base font-medium text-black hover:bg-sage-50 transition-colors min-h-[44px]"
            >
              Home
            </button>
            <button 
              onClick={() => scrollToSection('how-it-works')}
              className="block w-full text-left px-3 py-2 rounded-md text-base font-medium text-black hover:bg-sage-50 transition-colors min-h-[44px]"
            >
              How It Works
            </button>
            <button 
              onClick={() => scrollToSection('daily-menu')}
              className="block w-full text-left px-3 py-2 rounded-md text-base font-medium text-black hover:bg-sage-50 transition-colors min-h-[44px]"
            >
              Menu
            </button>
            <button 
              onClick={() => scrollToSection('subscription-plans')}
              className="block w-full text-left px-3 py-2 rounded-md text-base font-medium text-black hover:bg-sage-50 transition-colors min-h-[44px]"
            >
              Pricing
            </button>
            <button 
              onClick={() => scrollToSection('delivery-map')}
              className="block w-full text-left px-3 py-2 rounded-md text-base font-medium text-black hover:bg-sage-50 transition-colors min-h-[44px]"
            >
              Delivery Areas
            </button>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
