import React, { useRef } from "react";
import { motion, useInView } from "framer-motion";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faUtensils, 
  faCalendarCheck, 
  faTruckFast, 
  faHeart,
  faArrowRight,
  faStar,
  faSparkles
} from '@fortawesome/free-solid-svg-icons';

export default function HowItWorks() {
  const containerRef = useRef(null);
  const isInView = useInView(containerRef, { once: true, amount: 0.2 });

  const steps = [
    {
      number: "01",
      title: "Choose Your Plan",
      description: "Browse our curated meal plans designed for your lifestyle. From busy professionals to food enthusiasts, we've crafted the perfect experience for you.",
      icon: faUtensils,
      highlight: "Zero commitment",
      color: "from-purple-500 to-pink-500",
      bgColor: "bg-gradient-to-br from-purple-50 to-pink-50"
    },
    {
      number: "02", 
      title: "Set Your Schedule",
      description: "Complete control over when and where your meals arrive. Our smart scheduling adapts to your rhythm, not the other way around.",
      icon: faCalendarCheck,
      highlight: "Total flexibility",
      color: "from-blue-500 to-cyan-500",
      bgColor: "bg-gradient-to-br from-blue-50 to-cyan-50"
    },
    {
      number: "03",
      title: "We Deliver Fresh",
      description: "Our culinary team prepares each meal with love, delivering restaurant-quality food that arrives perfectly fresh at your doorstep.",
      icon: faTruckFast,
      highlight: "Always punctual",
      color: "from-green-500 to-emerald-500",
      bgColor: "bg-gradient-to-br from-green-50 to-emerald-50"
    },
    {
      number: "04",
      title: "Savor & Share",
      description: "Unbox your culinary journey and indulge in flavors that feel like home. Your experience shapes our future - every review matters.",
      icon: faHeart,
      highlight: "Community driven",
      color: "from-orange-500 to-red-500",
      bgColor: "bg-gradient-to-br from-orange-50 to-red-50"
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { 
      opacity: 0, 
      y: 60,
      scale: 0.8
    },
    visible: { 
      opacity: 1, 
      y: 0,
      scale: 1,
      transition: {
        type: "spring",
        stiffness: 100,
        damping: 12
      }
    }
  };

  return (
    <section id="how-it-works" className="py-20 md:py-32 bg-gradient-to-br from-slate-50 via-white to-blue-50 overflow-hidden section-fade relative">
      {/* Floating Background Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <motion.div
          className="absolute top-20 left-10 w-72 h-72 bg-gradient-to-r from-purple-200/30 to-pink-200/30 rounded-full blur-3xl"
          animate={{
            x: [0, 100, 0],
