import React, { useState, useRef, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faChevronLeft, 
  faChevronRight, 
  faChevronDown, 
  faChevronUp,
  faStar
} from '@fortawesome/free-solid-svg-icons';
import './DailyMenu.css';

// Sample menu items data
const MENU_ITEMS = [
  // Greatest Hits
  {
    id: "meal-1",
    name: "Creamy Basil Pesto Chicken Pasta",
    description: "with Parmesan",
    image: "https://images.unsplash.com/photo-1563379926898-05f4575a45d8?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 120,
    prepTime: "25-30 min",
    category: "Greatest Hits"
  },
  {
    id: "meal-2",
    name: "BBQ Pork Tacos",
    description: "with chipotle ranch slaw and crispy onions",
    image: "https://images.unsplash.com/photo-1565299585323-38d6b0865b47?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 120,
    prepTime: "20-25 min",
    category: "Greatest Hits"
  },
  {
    id: "meal-3",
    name: "Lasagna-Style Bolognese Gemelli",
    description: "with ricotta",
    image: "https://images.unsplash.com/photo-1618889482923-38250401a84e?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 120,
    prepTime: "25-30 min",
    category: "Greatest Hits"
  },
  {
    id: "meal-4",
    name: "One-Pan Korean-Style BBQ Meatballs",
    description: "with bok choy and rice",
    image: "https://images.unsplash.com/photo-1695653422259-8a84deb3ef1d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 120,
    prepTime: "30-35 min",
    category: "Greatest Hits"
  },

  // Supermeals
  {
    id: "meal-5",
    name: "Protein-Packed Quinoa Power Bowl",
    description: "with grilled chicken, avocado and seeds",
    image: "https://images.unsplash.com/photo-1543339308-43e59d6b73a6?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 150,
    prepTime: "20-25 min",
    category: "Supermeals"
  },
  {
    id: "meal-6",
    name: "Salmon Superfood Salad",
    description: "with kale, berries and omega-rich dressing",
    image: "https://images.unsplash.com/photo-1546069901-ba9599a7e63c?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 150,
    prepTime: "15-20 min",
    category: "Supermeals"
  },
  {
    id: "meal-7",
    name: "Mediterranean Nutrient Bowl",
    description: "with falafel, hummus and ancient grains",
    image: "https://images.unsplash.com/photo-1512621776951-a57141f2eefd?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 150,
    prepTime: "20-25 min",
    category: "Supermeals"
  },
  {
    id: "meal-8",
    name: "Antioxidant-Rich Berry Smoothie Bowl",
    description: "with Greek yogurt and chia seeds",
    image: "https://images.unsplash.com/photo-1553530666-ba11a90a0819?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 150,
    prepTime: "10-15 min",
    category: "Supermeals"
  },

  // Easy Meals
  {
    id: "meal-9",
    name: "Simple Veggie Pasta",
    description: "with olive oil and parmesan",
    image: "https://images.unsplash.com/photo-1473093295043-cdd812d0e601?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 120,
    prepTime: "15-20 min",
    category: "Easy Meals"
  },
  {
    id: "meal-10",
    name: "Quick Chicken Stir Fry",
    description: "with seasonal vegetables",
    image: "https://images.unsplash.com/photo-1512058454905-6b841e7ad132?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 120,
    prepTime: "15-20 min",
    category: "Easy Meals"
  },
  {
    id: "meal-11",
    name: "Classic Butter Paneer",
    description: "with jeera rice and raita",
    image: "https://images.unsplash.com/photo-1565557623262-b51c2513a641?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 120,
    prepTime: "20-25 min",
    category: "Easy Meals"
  },
  {
    id: "meal-12",
    name: "Comforting Khichdi Bowl",
    description: "with pickle and ghee",
    image: "https://images.unsplash.com/photo-1585937421612-70a008356c36?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 120,
    prepTime: "20-25 min",
    category: "Easy Meals"
  },

  // Day Starters
  {
    id: "meal-13",
    name: "Overnight Oats",
    description: "with fresh fruits and nuts",
    image: "https://images.unsplash.com/photo-1586511925558-a4c6376fe65f?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 100,
    prepTime: "5-10 min",
    category: "Day Starters"
  },
  {
    id: "meal-14",
    name: "Avocado Toast",
    description: "with poached eggs on multigrain bread",
    image: "https://images.unsplash.com/photo-1525351484163-7529414344d8?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 100,
    prepTime: "10-15 min",
    category: "Day Starters"
  },
  {
    id: "meal-15",
    name: "South Indian Breakfast Platter",
    description: "with idli, dosa, and sambar",
    image: "https://images.unsplash.com/photo-1630383249896-461df3d6d5fc?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 100,
    prepTime: "15-20 min",
    category: "Day Starters"
  },
  {
    id: "meal-16",
    name: "Power Protein Smoothie",
    description: "with banana, berries, and whey protein",
    image: "https://images.unsplash.com/photo-1577805947697-89e18249d767?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 100,
    prepTime: "5-10 min",
    category: "Day Starters"
  }
];

const DailyMenu = () => {
  const [activeCategory, setActiveCategory] = useState("Greatest Hits");
  const [isExpanded, setIsExpanded] = useState(false);
  const scrollRef = useRef(null);
  const carouselRef = useRef(null);

  // Categories based on our menu items
  const categories = ["Greatest Hits", "Supermeals", "Easy Meals", "Day Starters"];

  const filteredItems = MENU_ITEMS.filter(item => item.category === activeCategory);

  const scroll = (direction) => {
    if (scrollRef.current) {
      const { current } = scrollRef;
      const scrollAmount = direction === "left" ? -300 : 300;
      current.scrollBy({ left: scrollAmount, behavior: "smooth" });
    }
  };

  // Apply scroll animation for menu cards
  useEffect(() => {
    const handleScroll = () => {
      if (!carouselRef.current) return;

      const cards = carouselRef.current.querySelectorAll('.menu-card');
      const carouselRect = carouselRef.current.getBoundingClientRect();
      const centerX = carouselRect.left + carouselRect.width / 2;

      cards.forEach((card) => {
        const cardEl = card;
        const cardRect = cardEl.getBoundingClientRect();
        const cardCenterX = cardRect.left + cardRect.width / 2;
        const distanceFromCenter = Math.abs(centerX - cardCenterX);
        const maxDistance = window.innerWidth / 2;

        // Calculate scale based on distance from center
        let scale = 1 - (distanceFromCenter / maxDistance) * 0.15;
        scale = Math.max(0.85, scale); // Don't scale below 0.85

        // Calculate opacity based on distance from center
        let opacity = 1 - (distanceFromCenter / maxDistance) * 0.5;
        opacity = Math.max(0.6, opacity); // Don't fade below 0.6

        // Apply transformations
        cardEl.style.transform = `scale(${scale})`;
        cardEl.style.opacity = opacity.toString();
      });
    };

    const scrollContainer = scrollRef.current;
    if (scrollContainer) {
      scrollContainer.addEventListener('scroll', handleScroll);
      // Initial calculation
      handleScroll();

      return () => {
        scrollContainer.removeEventListener('scroll', handleScroll);
      };
    }
  }, [activeCategory]);

  // Price indicator based on category
  const getCategoryPrice = () => {
    switch(activeCategory) {
      case "Supermeals": return "₹150";
      case "Easy Meals": return "₹120";
      case "Day Starters": return "₹100";
      default: return "₹120";
    }
  };

  return (
    <section id="daily-combos" className="py-16 md:py-24 bg-white overflow-hidden">
      <div className="container mx-auto px-4 md:px-6">
        <motion.div
          className="text-center max-w-3xl mx-auto mb-12"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.7, ease: [0.22, 1, 0.36, 1] }}
        >
          <h2 className="text-3xl md:text-5xl font-bold mb-6 text-gray-900 tracking-tight">
            Explore The Menu
          </h2>
          <p className="text-xl text-gray-600 leading-relaxed mb-4">
            Find your new favorite meal with delicious options starting at {getCategoryPrice()} per serving
          </p>
          <div className="bg-gray-50 p-5 rounded-xl border border-gray-100 shadow-sm">
            <h3 className="font-medium text-lg text-gray-900 mb-2">Preorder Pricing</h3>
            <p className="text-gray-600 text-base mb-3">
              Order 4 hours in advance for your mealtime and enjoy our best rates
            </p>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="bg-white p-3 rounded-lg border border-primary/20">
                <span className="font-semibold text-primary block">Supermeals</span>
                <span className="text-xl font-bold">₹150</span>
              </div>
              <div className="bg-white p-3 rounded-lg border border-primary/20">
                <span className="font-semibold text-primary block">Meals</span>
                <span className="text-xl font-bold">₹130</span>
              </div>
              <div className="bg-white p-3 rounded-lg border border-primary/20">
                <span className="font-semibold text-primary block">Day Starters</span>
                <span className="text-xl font-bold">₹100</span>
              </div>
            </div>
            <p className="text-gray-500 text-sm mt-3">*Desserts and Juices prices may vary. Subscribers get Supermeals and Desserts included with their plan at no extra cost.</p>
          </div>
        </motion.div>

        {/* Menu Navigation - Modern tabs */}
        <motion.div
          className="flex justify-center mb-10 overflow-x-auto"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.7, delay: 0.2, ease: [0.22, 1, 0.36, 1] }}
        >
          <div className="inline-flex rounded-lg bg-gray-100 p-1.5 shadow-inner">
            {categories.map((category, index) => (
              <motion.button
                key={index}
                className={`px-6 py-3 rounded-lg font-medium transition-all ${
                  activeCategory === category 
                    ? "bg-primary text-white shadow-md" 
                    : "text-gray-700 hover:bg-gray-200"
                }`}
                onClick={() => setActiveCategory(category)}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.98 }}
                transition={{ duration: 0.2 }}
              >
                {category}
              </motion.button>
            ))}
          </div>
        </motion.div>

        {/* Menu Carousel with 3D-like effects */}
        <div ref={carouselRef} className="relative mb-16">
          <motion.div
            className="relative"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.7, delay: 0.4 }}
          >
            <div 
              ref={scrollRef}
              className="overflow-x-auto pb-8 scrollbar-hide"
              style={{ scrollbarWidth: 'none', msOverflowStyle: 'none' }}
            >
              <div className="flex space-x-6 px-12" style={{ width: "max-content" }}>
                {filteredItems.map((item, index) => (
                  <motion.div
                    key={index}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5, delay: index * 0.1 }}
                  >
                    <MenuItemCard item={item} />
                  </motion.div>
                ))}
              </div>
            </div>

            {/* Navigation Arrows with animation */}
            <motion.button
              type="button"
              onClick={() => scroll("left")}
              className="absolute top-1/2 left-2 -translate-y-1/2 bg-white rounded-full p-3 shadow-lg z-10 hover:bg-gray-50 transition-colors"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.95 }}
            >
              <FontAwesomeIcon icon={faChevronLeft} className="text-gray-800 w-5 h-5" />
            </motion.button>
            <motion.button
              type="button"
              onClick={() => scroll("right")}
              className="absolute top-1/2 right-2 -translate-y-1/2 bg-white rounded-full p-3 shadow-lg z-10 hover:bg-gray-50 transition-colors"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.95 }}
            >
              <FontAwesomeIcon icon={faChevronRight} className="text-gray-800 w-5 h-5" />
            </motion.button>
          </motion.div>
        </div>

        {/* Expandable Full Menu Section */}
        <div className="mt-8">
          <motion.div
            className="text-center"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.7, delay: 0.6 }}
          >
            <button
              onClick={() => setIsExpanded(!isExpanded)}
              className="bg-primary hover:bg-primary/90 text-white font-medium px-8 py-3 rounded-full shadow-md transition-all hover:shadow-lg hover:-translate-y-1 flex items-center gap-2 mx-auto"
            >
              {isExpanded ? 'Show Less' : 'Explore Full Menu'}
              <FontAwesomeIcon icon={isExpanded ? faChevronUp : faChevronDown} className="w-4 h-4" />
            </button>
          </motion.div>

          <AnimatePresence>
            {isExpanded && (
              <motion.div
                initial={{ opacity: 0, height: 0 }}
                animate={{ opacity: 1, height: "auto" }}
                exit={{ opacity: 0, height: 0 }}
                transition={{ duration: 0.5 }}
                className="overflow-hidden mt-12"
              >
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {MENU_ITEMS.map((item, index) => (
                    <motion.div
                      key={index}
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ duration: 0.5, delay: index * 0.03 }}
                    >
                      <ExpandedMenuItemCard item={item} />
                    </motion.div>
                  ))}
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </div>
    </section>
  );
};

function MenuItemCard({ item }) {
  return (
    <motion.div 
      className="w-64 md:w-72 bg-white rounded-xl overflow-hidden shadow-lg menu-card transform transition-all duration-300"
      whileHover={{ 
        y: -10, 
        boxShadow: "0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)" 
      }}
    >
      <div className="relative h-48 overflow-hidden">
        <img 
          src={item.image} 
          alt={item.name} 
          className="w-full h-full object-cover transform transition-transform duration-500 hover:scale-110"
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent"></div>
