// Helper functions for menu data manipulation
export const getItemsByCategory = (category) => {
  return ALL_MENU_ITEMS.filter(item => item.category === category);
};

export const getVegetarianItems = () => {
  return ALL_MENU_ITEMS.filter(item => item.isVegetarian);
};

export const getItemsByAllergen = (allergen) => {
  return ALL_MENU_ITEMS.filter(item => !item.allergens.includes(allergen));
};

export const getCategoryPrice = (category) => {
  return CATEGORY_PRICES[category] || { min: 0, max: 0 };
};
