import React, { useCallback, useEffect, useRef } from 'react';
import { motion } from 'framer-motion';
import './ProblemStatement.css';

const ProblemStatement = () => {
  const sectionRef = useRef(null);

  const problems = [
    {
      id: 'busy',
      title: 'Too Busy to Cook?',
      description: 'Your schedule is packed. Between work, commute, and life, who has time to plan, shop, and cook?',
      icon: '⏰',
      fact: 'Average person works 8+ hours daily'
    },
    {
      id: 'junk',
      title: 'Stuck with Junk Food?',
      description: 'Quick fixes lead to unhealthy choices. Fast food becomes the norm when convenience matters most.',
      icon: '🍔',
      fact: '70% of daily intake is processed food'
    },
    {
      id: 'expensive',
      title: 'Tired of Overspending on Food?',
      description: 'Ordering out daily burns through your budget. Groceries expire unused. Money down the drain.',
      icon: '💸',
      fact: 'Spend ₹500+ daily on food delivery'
    },
    {
      id: 'away',
      title: 'Away from Home?',
      description: 'Living away from family means no home-cooked meals. Missing that comfort and nutrition.',
      icon: '🏠',
      fact: 'Missing the comfort of home-cooked meals'
    }
  ];

  // Optimized animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        duration: 0.3,
        staggerChildren: 0.1,
        ease: "easeOut"
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.4,
        ease: "easeOut"
      }
    }
  };

  // Add animation complete handler
  const handleAnimationComplete = useCallback(() => {
    if (sectionRef.current) {
      const elements = sectionRef.current.querySelectorAll('.problem-item, .problem-icon-large');
      elements.forEach(el => el.classList.add('animation-complete'));
    }
  }, []);

  return (
    <>
      {/* Problem Statement Section */}
      <section id="problem-statement" className="problem-section" ref={sectionRef}>
        <div className="container mx-auto px-4 md:px-6">
          {/* Problem Section Header */}
          <motion.div 
            className="text-center mb-16"
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, margin: "-20px" }}
            onAnimationComplete={handleAnimationComplete}
          >
            <motion.h2 
              className="problem-title"
              variants={itemVariants}
            >
              We Know Your <span style={{color: '#D08C60'}}>Daily Struggle</span>
            </motion.h2>
            <motion.p 
              className="problem-subtitle"
              variants={itemVariants}
            >
              Modern life shouldn't mean compromising on nutrition and taste
            </motion.p>
          </motion.div>

          {/* Problems List - Use CSS animations for performance */}
          <div className="problems-list">
            {problems.map((problem, index) => (
              <div
                key={problem.id}
                className="problem-item"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div 
                  className="problem-icon-large"
                  style={{ animationDelay: `${0.2 + index * 0.1}s` }}
                >
                  {problem.icon}
                </div>

                <div className="problem-content-text">
                  <h3 className="problem-title-large">
                    {problem.title}
                  </h3>

                  <p className="problem-description-text">
                    {problem.description}
                  </p>

                  <div className="problem-fact">
                    {problem.fact}
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Transition to Solution - Simplified animation */}
          <motion.div 
            className="problem-cta mt-12"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true, margin: "-20px" }}
            transition={{ duration: 0.4, ease: "easeOut" }}
          >
            <h3 className="cta-title">Ready to Break Free from These Problems?</h3>
            <p className="cta-description">Let us handle your daily meals while you focus on what matters most</p>
          </motion.div>
        </div>
      </section>

      {/* Features Section - Optimized Design */}
      <section id="features" className="solution-section">
        <div className="container mx-auto px-4 md:px-6">
          {/* Unified Header Section */}
          <div className="unified-hero-section">
            {/* Header Section */}
            <motion.div 
              className="text-center mb-8"
              variants={containerVariants}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true, margin: "-20px" }}
            >
              <motion.h2 
                className="hero-title"
                variants={itemVariants}
              >
                Myna <span className="text-primary">Kitchen</span>
              </motion.h2>
              <motion.h3 
                className="hero-subtitle"
                variants={itemVariants}
              >
                Daily Meals Delivered
              </motion.h3>
              <motion.p 
                className="hero-description"
                variants={itemVariants}
              >
                With us you no longer have to run a kitchen, manage groceries or worry about your daily food.
              </motion.p>

              {/* New text addition */}
              <motion.p 
                className="hero-description-sub mt-4 text-lg md:text-xl text-gray-700 font-medium"
                variants={itemVariants}
              >
                Fresh homely meals delivered to you day by day, meal by meal
              </motion.p>

              {/* Pricing Text and Button */}
              <motion.div 
                className="hero-pricing-section mt-6 mb-12"
                variants={itemVariants}
              >
                <motion.p 
                  className="text-lg md:text-xl text-gray-700 mb-4 font-medium"
                  variants={itemVariants}
                >
                  Starting at just <span className="text-primary font-bold text-2xl">₹120</span> per meal
                </motion.p>

                {/* Pricing Hero Card - Tokyo Style */}
                <motion.div 
                  className="pricing-hero-card"
                  variants={itemVariants}
                >
                  <div className="pricing-content">
                    <p className="pricing-description-top">
                      No more cooking, no more groceries
                    </p>

                    <div className="pricing-badge">Starting From</div>

                    <div className="pricing-main">
                      <span className="price-amount">₹120</span>
                      <span className="price-period">/ meal</span>
                    </div>

                    <button className="pricing-cta-button">
                      Order Now
                    </button>
                  </div>
                </motion.div>
              </motion.div>
            </motion.div>

            {/* Features Grid - Use CSS Grid for better performance */}
            <div className="features-grid">
              {[
                {
                  icon: 'meals',
                  title: 'Daily Fresh Meals',
                  description: 'Nutritious, home-cooked meals delivered fresh to your doorstep every day'
                },
                {
                  icon: 'time',
                  title: 'Save Time',
                  description: 'No more meal planning, grocery shopping, or cooking. Get your time back for what matters'
                },
                {
                  icon: 'budget',
                  title: 'Budget-Friendly',
                  description: 'Affordable pricing that costs less than ordering out or buying groceries that expire'
                },
                {
                  icon: 'home',
                  title: 'Home-Cooked Taste',
                  description: 'Experience the comfort and nutrition of authentic home-cooked meals every day'
                }
              ].map((feature, index) => (
                <div key={feature.icon} className="feature-card">
                  <div className={`feature-accent feature-accent-${index + 1}`}></div>

                  <div className="feature-icon">
                    <div className={`modern-icon icon-${feature.icon}`}></div>
                  </div>

                  <div className="feature-content">
                    <h3 className="feature-title">{feature.title}</h3>
                    <p className="feature-description">{feature.description}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default ProblemStatement;
