import React, { useState, useRef } from "react";
import { motion, useInView } from "framer-motion";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown } from '@fortawesome/free-solid-svg-icons';

const FAQ_ITEMS = [
  {
    id: "faq-1",
    question: "How does meal delivery work?",
    answer: "We deliver fresh, home-style meals to your doorstep daily. Choose your preferred delivery time during signup, and our team will ensure your meals arrive hot and ready to enjoy."
  },
  {
    id: "faq-2", 
    question: "Can I customize my meal preferences?",
    answer: "Absolutely! You can select vegetarian or non-vegetarian options, specify dietary restrictions, and choose from our rotating weekly menu that caters to various tastes and preferences."
  },
  {
    id: "faq-3",
    question: "How do I pause or cancel my subscription?",
    answer: "You can pause or cancel your subscription anytime through your account dashboard. For pauses, we need 24 hours notice. Cancellations can be done instantly with no penalties."
  },
  {
    id: "faq-4",
    question: "What are your delivery areas?",
    answer: "We currently deliver across OMR, Chennai, and surrounding areas. Enter your pincode during signup to confirm if we deliver to your location."
  },
  {
    id: "faq-5",
    question: "Are the meals prepared fresh daily?",
    answer: "Yes! All our meals are prepared fresh on the day of delivery using high-quality ingredients. We don't use any preservatives and ensure maximum freshness and taste."
  },
  {
    id: "faq-6",
    question: "What happens if I'm not home during delivery?",
    answer: "Our delivery team will contact you before arrival. If you're not available, we can leave the meal with a trusted neighbor or reschedule for a more convenient time."
  },
  {
    id: "faq-7",
    question: "Do you offer trial packages?",
    answer: "Yes! New customers can try our service with a special trial package. Plus, get 10% off your first order when you sign up for any of our meal plans."
  }
];

const FAQ = () => {
  const sectionRef = useRef(null);
  const isInView = useInView(sectionRef, { once: true, amount: 0.2 });

  return (
    <section 
      className="py-20 md:py-28 overflow-hidden section-fade" 
      style={{ 
        backgroundColor: '#F5F1EB'
      }}
    >
      {/* Background decorative elements */}
      <div className="absolute inset-0 opacity-5">
        <div className="absolute top-20 right-20 w-96 h-96 bg-gray-400 rounded-full blur-3xl"></div>
        <div className="absolute bottom-20 left-10 w-80 h-80 bg-gray-400 rounded-full blur-3xl"></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-64 h-64 bg-gray-400 rounded-full blur-3xl"></div>
      </div>

      <div ref={sectionRef} className="container mx-auto px-4 md:px-6 relative z-10">
        {/* Header */}
        <motion.div
          className="text-center max-w-4xl mx-auto mb-16"
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
          transition={{ duration: 0.8 }}
        >
          <h2 className="text-4xl md:text-6xl lg:text-7xl font-bold text-gray-900 mb-8 tracking-tight">
            WE GOT ANSWERS
          </h2>
        </motion.div>

        {/* FAQ List */}
        <div className="max-w-4xl mx-auto">
          {FAQ_ITEMS.map((item, index) => (
            <motion.div
              key={item.id}
              initial={{ opacity: 0, y: 20 }}
              animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
              transition={{ duration: 0.6, delay: index * 0.1 }}
            >
              <FAQItem 
                question={item.question} 
                answer={item.answer}
                isLast={index === FAQ_ITEMS.length - 1}
              />
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default FAQ;

function FAQItem({ question, answer, isLast }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className={`py-6 ${!isLast ? 'border-b border-gray-300/50' : ''}`}>
      <button
        className="flex w-full items-center justify-between text-left group"
        onClick={() => setIsOpen(!isOpen)}
      >
        <h3 className="text-lg md:text-xl font-medium text-gray-900 pr-8 group-hover:text-primary transition-colors duration-300">
          {question}
        </h3>

        <motion.div
          animate={{ rotate: isOpen ? 180 : 0 }}
          transition={{ duration: 0.3 }}
          className="flex-shrink-0"
        >
          <FontAwesomeIcon 
            icon={faChevronDown} 
            className="text-gray-600 text-lg group-hover:text-primary transition-colors duration-300" 
          />
        </motion.div>
      </button>

      <motion.div
        initial={{ height: 0, opacity: 0 }}
        animate={{ 
          height: isOpen ? "auto" : 0,
          opacity: isOpen ? 1 : 0
        }}
        transition={{ duration: 0.4, ease: "easeInOut" }}
        className="overflow-hidden"
      >
        <div className="pt-4 pr-8">
          <motion.p 
            className="text-gray-700 leading-relaxed text-base md:text-lg"
            initial={{ y: -10 }}
            animate={{ y: isOpen ? 0 : -10 }}
            transition={{ duration: 0.4, delay: isOpen ? 0.1 : 0 }}
          >
            {answer}
          </motion.p>
        </div>
      </motion.div>
    </div>
  );
}
