import React, { useRef } from 'react';
import { motion, useInView } from 'framer-motion';
import './Features.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBowlFood, faTruck, faCalendarCheck } from '@fortawesome/free-solid-svg-icons';

const Features = () => {
  // Create refs for each feature section
  const ref1 = useRef(null);
  const ref2 = useRef(null);
  const ref3 = useRef(null);

  // Track if sections are in view
  const isInView1 = useInView(ref1, { once: true, amount: 0.3 });
  const isInView2 = useInView(ref2, { once: true, amount: 0.3 });
  const isInView3 = useInView(ref3, { once: true, amount: 0.3 });

  return (
    <section id="features" className="py-20 md:py-32 bg-white overflow-hidden">
      <div className="container mx-auto px-4 md:px-6">
        <div className="text-center max-w-3xl mx-auto mb-16 section-fade">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6 }}
          >
            <h2 className="text-3xl md:text-5xl font-bold mb-6 text-gray-900 tracking-tight">
              Our Food Philosophy
            </h2>
            <p className="text-xl text-gray-600 leading-relaxed">
              Homely, fresh, delivered to your doorstep with passion and care.
            </p>
          </motion.div>
        </div>

        {/* Feature sections with animation */}
        <div className="space-y-32 md:space-y-48">
          {/* Feature 1 */}
          <div ref={ref1} className="relative">
            <motion.div 
              className="flex flex-col md:flex-row items-center justify-between gap-12"
              initial={{ opacity: 0 }}
              animate={isInView1 ? { opacity: 1 } : { opacity: 0 }}
              transition={{ duration: 0.8, delay: 0.1 }}
            >
              <motion.div 
                className="w-full md:w-1/2 order-2 md:order-1"
                initial={{ x: -100 }}
                animate={isInView1 ? { x: 0 } : { x: -100 }}
                transition={{ duration: 0.8, delay: 0.3 }}
              >
                <div className="text-primary mb-4">
                  <FontAwesomeIcon icon={faBowlFood} className="w-10 h-10" />
                </div>
                <h3 className="font-semibold text-2xl md:text-3xl mb-4">Meals and Supermeals</h3>
                <p className="text-gray-600 text-lg leading-relaxed">
                  Enjoy a perfectly balanced selection of meals throughout the day. From nutritionally dense dishes to satisfying comfort foods, we have your day covered with delicious options that are prepared fresh daily.
                </p>
                <div className="mt-6 flex flex-wrap items-center gap-2">
                  <span className="px-3 py-1 rounded-full bg-primary/10 text-primary font-medium text-sm">
                    Fresh ingredients
                  </span>
                  <span className="px-3 py-1 rounded-full bg-primary/10 text-primary font-medium text-sm">
                    Balanced nutrition
                  </span>
                </div>
              </motion.div>

              <motion.div 
                className="w-full md:w-1/2 order-1 md:order-2 relative"
                initial={{ scale: 0.8, opacity: 0 }}
                animate={isInView1 ? { scale: 1, opacity: 1 } : { scale: 0.8, opacity: 0 }}
                transition={{ duration: 0.8, delay: 0.4 }}
              >
                <div className="relative rounded-2xl overflow-hidden shadow-2xl aspect-video">
                  <img 
