import React from 'react';
import { motion } from 'framer-motion';
import './ProblemStatement.css';

const ProblemStatement = () => {
  const problems = [
    {
      id: 'busy',
      title: 'Too Busy to Cook?',
      description: 'Your schedule is packed. Between work, commute, and life, who has time to plan, shop, and cook?',
      icon: '⏰',
      fact: 'Average person works 8+ hours daily'
    },
    {
      id: 'junk',
      title: 'Stuck with Junk Food?',
      description: 'Quick fixes lead to unhealthy choices. Fast food becomes the norm when convenience matters most.',
      icon: '🍔',
      fact: '70% of daily intake is processed food'
    },
    {
      id: 'expensive',
      title: 'Tired of Overspending on Food?',
      description: 'Ordering out daily burns through your budget. Groceries expire unused. Money down the drain.',
      icon: '💸',
      fact: 'Spend ₹500+ daily on food delivery'
    },
    {
      id: 'away',
      title: 'Away from Home?',
      description: 'Living away from family means no home-cooked meals. Missing that comfort and nutrition.',
      icon: '🏠',
      fact: 'Missing the comfort of home-cooked meals'
    }
  ];

  // Optimized animation variants for better performance
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        duration: 0.3,
        ease: "easeOut",
        staggerChildren: 0.08
      }
    }
  };

  const itemVariants = {
    hidden: { 
      opacity: 0, 
      y: 20
    },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.4,
        ease: "easeOut"
      }
    }
  };

  const iconVariants = {
    hidden: { 
      opacity: 0, 
      scale: 0.8,
      rotate: -30
    },
    visible: {
      opacity: 1,
      scale: 1,
      rotate: 0,
      transition: {
        duration: 0.3,
        ease: "easeOut"
      }
    }
  };

  return (
    <>
      {/* Problem Statement Section */}
      <section id="problem-statement" className="problem-section">
        <div className="container mx-auto px-4 md:px-6">
          {/* Problem Section Header */}
          <motion.div 
            className="text-center mb-16"
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, margin: "-100px" }}
          >
            <motion.h2 
              className="problem-title"
              variants={itemVariants}
            >
              We Know Your <span style={{color: '#D08C60'}}>Daily Struggle</span>
            </motion.h2>
            <motion.p 
              className="problem-subtitle"
              variants={itemVariants}
            >
              Modern life shouldn't mean compromising on nutrition and taste
            </motion.p>
          </motion.div>

          {/* Problems List */}
          <motion.div 
            className="problems-list"
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, margin: "-50px" }}
          >
            {problems.map((problem, index) => (
              <motion.div
                key={problem.id}
                className="problem-item"
                variants={itemVariants}
              >
                <motion.div 
                  className="problem-icon-large"
                  variants={iconVariants}
                >
                  {problem.icon}
                </motion.div>

                <div className="problem-content-text">
                  <motion.h3 
                    className="problem-title-large"
                    variants={itemVariants}
                  >
                    {problem.title}
                  </motion.h3>

                  <motion.p 
                    className="problem-description-text"
                    variants={itemVariants}
                  >
                    {problem.description}
                  </motion.p>

                  <motion.div 
                    className="problem-fact"
                    variants={itemVariants}
                  >
                    {problem.fact}
                  </motion.div>
                </div>
              </motion.div>
            ))}
          </motion.div>

          {/* Transition to Solution - Reduced spacing */}
          <motion.div 
            className="problem-cta mt-12"
            variants={itemVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, margin: "-100px" }}
          >
            <h3 className="cta-title">Ready to Break Free from These Problems?</h3>
            <p className="cta-description">Let us handle your daily meals while you focus on what matters most</p>
          </motion.div>
        </div>
      </section>

      {/* Features Section - Unified Design */}
      <section id="features" className="solution-section">
        <div className="container mx-auto px-4 md:px-6">
          {/* Unified Header Section */}
          <div className="unified-hero-section">
            {/* Header Section - Reduced spacing */}
            <motion.div 
              className="text-center mb-8"
              variants={containerVariants}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true, margin: "-100px" }}
            >
              <motion.h2 
                className="hero-title"
                variants={itemVariants}
              >
                Myna <span className="text-primary">Kitchen</span>
              </motion.h2>
              <motion.h3 
                className="hero-subtitle"
                variants={itemVariants}
              >
                Daily Meals Delivered
              </motion.h3>
              <motion.p 
                className="hero-description"
                variants={itemVariants}
              >
                With us you no longer have to run a kitchen, manage groceries or worry about your daily food.
              </motion.p>

              {/* New text addition */}
              <motion.p 
                className="hero-description-sub mt-4 text-lg md:text-xl text-gray-700 font-medium"
                variants={itemVariants}
              >
                Fresh homely meals delivered to you day by day, meal by meal
              </motion.p>

              {/* Pricing Text and Button - Reduced spacing */}
              <motion.div 
                className="hero-pricing-section mt-6 mb-12"
                variants={itemVariants}
              >
                <motion.p 
                  className="hero-pricing-text text-xl md:text-2xl font-semibold text-gray-800 mb-4"
                  variants={itemVariants}
                >
                  Starting at just ₹150/day
                </motion.p>

                {/* Compact Hero Pricing Card */}
                <motion.div 
                  className="pricing-hero-card"
                  variants={itemVariants}
                  whileHover={{ 
                    y: -2,
                    transition: { duration: 0.1, ease: "easeOut" }
                  }}
                >
                  <div className="pricing-content">
                    <p className="pricing-description-top">Premium home-style meals</p>
                    <div className="pricing-badge">Most Popular</div>

                    <div className="pricing-main">
                      <span className="price-amount">₹150</span>
                      <span className="price-period">/day</span>
                    </div>

                    <motion.button 
                      className="pricing-cta-button"
                      whileHover={{ 
                        y: -1,
                        transition: { duration: 0.1, ease: "easeOut" }
                      }}
                      whileTap={{ 
                        y: 0,
                        transition: { duration: 0.05, ease: "easeOut" }
                      }}
                    >
                      Order Now
                    </motion.button>
                  </div>
                </motion.div>
              </motion.div>
            </motion.div>
          </div>

          {/* Features Grid - Modern Tokyo Style */}
          <motion.div 
            className="features-grid"
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, margin: "-50px" }}
          >
            {[
              {
                icon: 'meals',
                title: 'Daily Fresh Meals',
                description: 'Chef-prepared meals delivered fresh to your doorstep every single day',
                accent: 'accent-1'
              },
              {
                icon: 'time',
                title: 'Save Your Time',
                description: 'No planning, shopping, or cooking required. Focus on what matters most to you',
                accent: 'accent-2'
              },
              {
                icon: 'budget',
                title: 'Budget Friendly',
                description: 'Quality meals at affordable prices. Save money compared to daily ordering',
                accent: 'accent-3'
              },
              {
                icon: 'home',
                title: 'Homely Taste',
                description: 'Experience the comfort and nutrition of home-cooked meals away from home',
                accent: 'accent-4'
              }
            ].map((feature, index) => (
              <motion.div
                key={feature.icon}
                className="feature-card"
                variants={itemVariants}
                whileHover={{ 
                  y: -4,
                  transition: { duration: 0.1, ease: "easeOut" }
                }}
              >
                <div className={`feature-accent ${feature.accent}`}></div>

                <motion.div 
                  className="feature-icon"
                  variants={iconVariants}
                >
                  <div className={`modern-icon icon-${feature.icon}`}></div>
                </motion.div>

                <div className="feature-content">
                  <h3 className="feature-title">{feature.title}</h3>
                  <p className="feature-description">{feature.description}</p>
                </div>
              </motion.div>
            ))}
          </motion.div>
        </div>
      </section>
    </>
  );
};

export default ProblemStatement;
