import React, { useState, useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes } from '@fortawesome/free-solid-svg-icons';
import { useThrottledScroll } from '../hooks/useThrottledScroll';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [isVisible, setIsVisible] = useState(true);
  const lastScrollYRef = useRef(0);

  // Fixed scroll handler using useRef to prevent memory leaks
  const handleScroll = () => {
    const currentScrollY = window.scrollY;
    const lastScrollY = lastScrollYRef.current;

    // Determine if we've scrolled enough to show shadow
    setScrolled(currentScrollY > 10);

    // Determine scroll direction and visibility
    if (currentScrollY < 10) {
      // Always show header at the top of the page
      setIsVisible(true);
    } else if (currentScrollY > lastScrollY && currentScrollY > 80) {
      // Scrolling down & past 80px - hide header
      setIsVisible(false);
    } else if (currentScrollY < lastScrollY - 5) {
      // Scrolling up with some threshold - show header
      setIsVisible(true);
    }

    // Update the ref with current scroll position
    lastScrollYRef.current = currentScrollY;
  };

  // Use throttled scroll hook to prevent performance issues
  useThrottledScroll(handleScroll, 16); // 60fps throttling

  const scrollToSection = (sectionId) => {
    try {
      const section = document.getElementById(sectionId);
      if (section) {
        window.scrollTo({
          top: section.offsetTop - 60,
          behavior: 'smooth'
        });
        setIsMenuOpen(false);
      }
    } catch (error) {
      console.error('Error scrolling to section:', error);
      // Fallback: try simple scroll
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  // Safe external link handler
  const openExternalLink = (url) => {
    try {
      const newWindow = window.open(url, '_blank', 'noopener,noreferrer');
      if (newWindow) {
        newWindow.opener = null; // Extra security measure
      }
    } catch (error) {
      console.error('Error opening external link:', error);
      // Fallback: navigate in current window
      window.location.href = url;
    }
  };

  return (
    <header className={`fixed top-0 left-0 right-0 z-50 bg-white transition-all duration-500 ease-in-out ${
      scrolled ? 'shadow-lg backdrop-blur-sm' : 'shadow-sm'
    } ${
      isVisible ? 'translate-y-0' : '-translate-y-full'
    } safe-area-top`}>
      <div className="container mx-auto px-4 md:px-6 py-2 md:py-3">
        <div className="flex items-center justify-between min-h-[50px]">
          <button 
            onClick={() => scrollToSection('hero')} 
            className="flex items-center flex-shrink-0 touch-target"
            aria-label="Go to home section"
          >
            <img 
              src="/images/myna-logo.jpg" 
              alt="Myna Kitchen Logo" 
              className="h-10 w-auto sm:h-12 md:h-14 lg:h-16 xl:h-18 object-contain transition-all duration-200"
              style={{ 
                maxWidth: 'min(250px, 40vw)',
                height: 'clamp(2.5rem, 8vw, 4.5rem)'
              }}
              onError={(e) => {
                console.error('Logo image failed to load');
                e.target.style.display = 'none';
              }}
            />
          </button>

          {/* Mobile menu button and sign up */}
          <div className="flex md:hidden items-center space-x-3">
            <button 
              onClick={() => openExternalLink('https://mynakitchen.in/SignUp')}
              className="btn-touch bg-accent text-white px-3 py-1.5 rounded-full text-responsive-sm font-medium shadow-sm hover:bg-warmOrange-600 transition-colors"
              aria-label="Sign up for Myna Kitchen"
            >
              Sign Up
            </button>
            <button 
              type="button" 
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="touch-target-large flex items-center justify-center p-2 rounded-md text-tertiary hover:text-primary hover:bg-sage-50 focus:outline-none"
              aria-expanded={isMenuOpen}
              aria-label={isMenuOpen ? 'Close menu' : 'Open menu'}
            >
              <span className="sr-only">Open main menu</span>
              {isMenuOpen ? (
                <FontAwesomeIcon icon={faTimes} className="block h-5 w-5" />
              ) : (
                <FontAwesomeIcon icon={faBars} className="block h-5 w-5" />
              )}
            </button>
          </div>

          {/* Desktop navigation */}
          <nav className="hidden md:flex items-center space-x-6 lg:space-x-8">
            <button 
              onClick={() => scrollToSection('hero')}
              className="touch-target text-black hover:text-primary font-medium transition-colors"
            >
              Home
            </button>
            <button 
              onClick={() => scrollToSection('how-it-works')}
              className="touch-target text-black hover:text-primary font-medium transition-colors"
            >
              How It Works
            </button>
            <button 
              onClick={() => scrollToSection('daily-menu')}
              className="touch-target text-black hover:text-primary font-medium transition-colors"
            >
              Menu
            </button>
            <button 
              onClick={() => scrollToSection('subscription-plans')}
              className="touch-target text-black hover:text-primary font-medium transition-colors"
            >
              Pricing
            </button>
            <button 
              onClick={() => scrollToSection('delivery-map')}
              className="touch-target text-black hover:text-primary font-medium transition-colors"
            >
              Delivery Areas
            </button>
            <button 
              onClick={() => openExternalLink('https://mynakitchen.in/SignUp')}
              className="btn-touch bg-accent text-white px-6 py-2.5 rounded-full font-medium shadow-sm hover:bg-warmOrange-600 transition-colors"
              aria-label="Sign up for Myna Kitchen"
            >
              Sign Up
            </button>
          </nav>
        </div>

        {/* Enhanced Mobile navigation with better touch targets */}
        <div className={`pt-4 pb-3 border-t border-tertiary/20 md:hidden transition-all duration-300 ${isMenuOpen ? 'block opacity-100' : 'hidden opacity-0'}`}>
          <div className="space-y-1">
            <button 
              onClick={() => scrollToSection('hero')}
              className="touch-target-large block w-full text-left px-3 py-2 rounded-md text-responsive-base font-medium text-black hover:bg-sage-50 transition-colors"
            >
              Home
            </button>
            <button 
              onClick={() => scrollToSection('how-it-works')}
              className="touch-target-large block w-full text-left px-3 py-2 rounded-md text-responsive-base font-medium text-black hover:bg-sage-50 transition-colors"
            >
              How It Works
            </button>
            <button 
              onClick={() => scrollToSection('daily-menu')}
              className="touch-target-large block w-full text-left px-3 py-2 rounded-md text-responsive-base font-medium text-black hover:bg-sage-50 transition-colors"
            >
              Menu
            </button>
            <button 
              onClick={() => scrollToSection('subscription-plans')}
              className="touch-target-large block w-full text-left px-3 py-2 rounded-md text-responsive-base font-medium text-black hover:bg-sage-50 transition-colors"
            >
              Pricing
            </button>
            <button 
              onClick={() => scrollToSection('delivery-map')}
              className="touch-target-large block w-full text-left px-3 py-2 rounded-md text-responsive-base font-medium text-black hover:bg-sage-50 transition-colors"
            >
              Delivery Areas
            </button>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
