import React from 'react';
import { motion } from 'framer-motion';

const SubscriptionPlans = () => {
  const pricingPlans = [
    {
      id: 'one-meal',
      title: 'One Meal a Day',
      price: 120,
      description: 'Lunch or Dinner',
      popular: false,
      mealBoxes: 1,
      gradient: 'from-orange-400 to-red-500'
    },
    {
      id: 'two-meals',
      title: 'Two Meals a Day',
      price: 220,
      description: 'Lunch and Dinner',
      popular: true,
      mealBoxes: 2,
      gradient: 'from-red-500 to-pink-600'
    },
    {
      id: 'family-plan',
      title: 'Family Plan',
      price: 440,
      description: 'Two meals for two people',
      popular: false,
      mealBoxes: 3,
      gradient: 'from-pink-500 to-purple-600'
    }
  ];

  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      window.scrollTo({
        top: section.offsetTop - 80,
        behavior: 'smooth'
      });
    }
  };

  const formatPrice = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0
    }).format(amount);
  };

  return (
    <section id="subscription-plans" className="py-0 pb-20 md:pb-28 overflow-hidden section-fade" style={{backgroundColor: '#f9f5f0'}}>
      <div className="container mx-auto px-4 md:px-6">


        <div className="max-w-4xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-8">
          {pricingPlans.map((plan, index) => (
            <motion.div
              key={plan.id}
              className={`bg-white rounded-2xl shadow-lg overflow-hidden relative transition-transform hover:scale-105 ${
                plan.popular ? "md:-mt-4 md:mb-4" : ""
              }`}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
            >
              {plan.popular && (
                <div className="absolute top-0 right-0 bg-accent text-white text-xs font-semibold px-4 py-1 uppercase rounded-bl-lg">
                  Popular
                </div>
              )}
              <div className="p-8 text-center border-b border-gray-100">
                <h3 className="text-2xl font-semibold mb-2">{plan.title}</h3>
                <div className="text-primary text-4xl font-bold">
                  {formatPrice(plan.price)}<span className="text-gray-400 text-lg font-normal">/day</span>
                </div>
                <p className="text-gray-600 mt-2">{plan.description}</p>
              </div>
              <div className="p-8">
                <ul className="space-y-4">
                  {plan.features.map((feature, idx) => (
                    <li key={idx} className="flex items-center">
                      <FontAwesomeIcon icon={faCheck} className="text-primary mr-3 h-4 w-4" />
                      <span>{feature}</span>
                    </li>
                  ))}
                </ul>
                <div className="mt-8 text-center">
                  <button
                    onClick={() => scrollToSection('daily-combos')}
                    className={`w-full py-3 rounded-full font-medium shadow-md transition-all ${
                      plan.popular 
                        ? "bg-accent hover:bg-orange-600 text-white" 
                        : "bg-primary hover:bg-primary/90 text-white"
                    }`}
                  >
                    Get Started
                  </button>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default SubscriptionPlans;
