import React, { useRef } from "react";
import { motion, useInView } from "framer-motion";

export default function HowItWorks() {
  const containerRef = useRef(null);
  const isInView = useInView(containerRef, { once: true, amount: 0.2 });

  const handleWhatsAppContact = () => {
    // Replace with your actual WhatsApp number
    const phoneNumber = '+918123456789'; // Update this with your actual WhatsApp number
    const message = encodeURIComponent('Hi! I\'m interested in Myna Kitchen\'s meal plans and would like to discuss my requirements to choose the best plan for me.');
    const whatsappUrl = `https://wa.me/${phoneNumber}?text=${message}`;

    window.open(whatsappUrl, '_blank');
  };

  const steps = [
    {
      number: "01",
      title: "Browse & Choose",
      subtitle: "Discover Your Perfect Meal",
      description: "Explore our curated collection of delicious meal plans designed by nutritionists. From comfort food to healthy options - find what makes your taste buds happy.",
      gradient: "from-orange-400 via-red-400 to-pink-500",
      bgGradient: "from-orange-50 to-pink-50",
      illustration: (
        <div className="relative w-20 h-20 mx-auto">
          <div className="absolute inset-0 bg-gradient-to-br from-orange-200 to-pink-200 rounded-3xl rotate-6 animate-pulse"></div>
          <div className="relative bg-white rounded-2xl shadow-xl p-3 grid grid-cols-2 gap-2">
            <div className="bg-gradient-to-br from-orange-400 to-red-400 rounded-lg flex items-center justify-center text-white text-lg font-bold shadow-md">ü•ó</div>
            <div className="bg-gradient-to-br from-green-400 to-emerald-400 rounded-lg flex items-center justify-center text-white text-lg font-bold shadow-md">üçõ</div>
            <div className="bg-gradient-to-br from-blue-400 to-cyan-400 rounded-lg flex items-center justify-center text-white text-lg font-bold shadow-md">üçú</div>
            <div className="bg-gradient-to-br from-purple-400 to-pink-400 rounded-lg flex items-center justify-center text-white text-lg font-bold shadow-md">ü•ò</div>
          </div>
          <div className="absolute -top-2 -right-2 w-6 h-6 bg-yellow-400 rounded-full flex items-center justify-center text-xs animate-bounce">‚ú®</div>
        </div>
      ),
      icon: "üçΩÔ∏è"
    },
    {
      number: "02", 
      title: "Connect on WhatsApp",
      subtitle: "Let's Talk Food!",
      description: "Jump on WhatsApp with our friendly food experts. Share your preferences, allergies, and goals - we'll craft the perfect meal journey just for you.",
      gradient: "from-green-400 via-emerald-400 to-teal-500",
      bgGradient: "from-green-50 to-emerald-50",
      illustration: (
        <div className="relative w-20 h-20 mx-auto">
          <div className="absolute inset-0 bg-gradient-to-br from-green-200 to-emerald-200 rounded-full animate-ping opacity-75"></div>
          <div className="relative bg-gradient-to-br from-green-500 to-emerald-600 rounded-2xl shadow-2xl p-4 flex items-center justify-center">
            <div className="relative">
              <div className="w-12 h-12 bg-white rounded-full flex items-center justify-center shadow-inner">
                <span className="text-2xl">üí¨</span>
              </div>
              <div className="absolute -top-1 -right-1 w-4 h-4 bg-yellow-400 rounded-full animate-pulse flex items-center justify-center">
                <div className="w-2 h-2 bg-white rounded-full"></div>
              </div>
            </div>
          </div>
          <div className="absolute top-0 left-0 w-4 h-4 bg-blue-400 rounded-full animate-bounce delay-100"></div>
          <div className="absolute bottom-2 right-0 w-3 h-3 bg-pink-400 rounded-full animate-bounce delay-300"></div>
        </div>
      ),
      hasButton: true,
      icon: "üí¨"
    },
    {
      number: "03",
      title: "Sign Up & Select",
      subtitle: "Make It Official",
      description: "Quick signup, choose your plan, set delivery preferences. Our smart system learns from your choices to make every meal better than the last.",
      gradient: "from-purple-400 via-violet-400 to-indigo-500",
      bgGradient: "from-purple-50 to-indigo-50",
      illustration: (
        <div className="relative w-20 h-20 mx-auto">
          <div className="absolute inset-0 bg-gradient-to-br from-purple-200 to-indigo-200 rounded-2xl rotate-12 animate-pulse"></div>
          <div className="relative bg-gradient-to-br from-purple-500 to-indigo-600 rounded-2xl shadow-2xl p-4 flex flex-col items-center justify-center">
            <div className="space-y-1 mb-2">
              <div className="w-10 h-1 bg-white/80 rounded-full"></div>
              <div className="w-8 h-1 bg-white/60 rounded-full"></div>
              <div className="w-6 h-1 bg-white/40 rounded-full"></div>
            </div>
            <div className="w-8 h-8 bg-gradient-to-br from-green-400 to-emerald-500 rounded-full flex items-center justify-center shadow-lg">
              <span className="text-white text-sm font-bold">‚úì</span>
            </div>
          </div>
          <div className="absolute -top-2 -left-2 w-5 h-5 bg-yellow-400 rounded-full animate-spin"></div>
        </div>
      ),
      icon: "üìù"
    },
    {
      number: "04",
      title: "Enjoy Daily Deliveries",
      subtitle: "Food Magic at Your Door",
      description: "Sit back and enjoy as fresh, hot meals arrive at your doorstep daily. Each bite crafted with love, delivered with care, perfect every time.",
      gradient: "from-red-400 via-rose-400 to-pink-500",
      bgGradient: "from-red-50 to-pink-50",
      illustration: (
        <div className="relative w-20 h-20 mx-auto">
          <div className="absolute inset-0 bg-gradient-to-br from-red-200 to-pink-200 rounded-3xl -rotate-6 animate-pulse"></div>
          <div className="relative bg-gradient-to-br from-red-500 to-pink-600 rounded-2xl shadow-2xl p-3 flex items-center justify-center">
            <div className="relative bg-white rounded-xl p-2 shadow-inner">
              <span className="text-2xl">üöö</span>
            </div>
          </div>
          <div className="absolute top-0 right-0 w-6 h-6 bg-yellow-400 rounded-full animate-bounce flex items-center justify-center">
            <span className="text-xs">üî•</span>
          </div>
          <div className="absolute -bottom-1 -left-1 w-4 h-4 bg-green-400 rounded-full animate-pulse"></div>
        </div>
      ),
      icon: "üöÄ"
    }
  ];

  return (
    <section id="how-it-works" className="py-20 md:py-32 relative overflow-hidden">
      {/* Dynamic Background */}
      <div className="absolute inset-0 bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100"></div>
      <div className="absolute top-0 left-0 w-72 h-72 bg-purple-200 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob"></div>
      <div className="absolute top-0 right-0 w-72 h-72 bg-yellow-200 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob animation-delay-2000"></div>
      <div className="absolute -bottom-32 left-20 w-72 h-72 bg-pink-200 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob animation-delay-4000"></div>

      <div className="container mx-auto px-4 md:px-6 relative z-10">
        {/* Enhanced Header */}
        <motion.div 
          className="text-center mb-20"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            whileInView={{ opacity: 1, scale: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6, delay: 0.2 }}
            className="inline-flex items-center bg-gradient-to-r from-purple-600 to-blue-600 text-white px-8 py-3 rounded-full mb-8 shadow-xl"
          >
            <span className="text-2xl mr-3">‚ú®</span>
            <span className="font-bold text-lg">Simple Magic</span>
          </motion.div>

          <motion.h2 
            className="text-5xl md:text-7xl font-black text-transparent bg-clip-text bg-gradient-to-r from-gray-900 via-purple-900 to-gray-900 mb-6 leading-tight"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.3 }}
          >
            How It Works
          </motion.h2>

          <motion.p 
            className="text-xl md:text-2xl text-gray-600 max-w-4xl mx-auto leading-relaxed font-medium"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            From your first browse to daily doorstep deliveries - we've turned meal planning into a delightful experience ‚ú®
          </motion.p>
        </motion.div>

        {/* Dynamic Cards Grid */}
        <div ref={containerRef} className="max-w-7xl mx-auto">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {steps.map((step, index) => (
              <motion.div
                key={index}
                className="relative group"
                initial={{ opacity: 0, y: 50, rotateY: -15 }}
                whileInView={{ opacity: 1, y: 0, rotateY: 0 }}
                viewport={{ once: true }}
                transition={{ 
                  duration: 0.8, 
                  delay: index * 0.2,
                  type: "spring",
                  stiffness: 100
                }}
                whileHover={{ 
                  y: -10, 
                  rotateY: 5,
                  transition: { duration: 0.3 } 
                }}
              >
                {/* Glowing background effect */}
                <div className={`absolute inset-0 bg-gradient-to-br ${step.gradient} rounded-3xl blur opacity-0 group-hover:opacity-20 transition-all duration-500 scale-105`}></div>

                {/* Main Card */}
                <div className={`relative bg-gradient-to-br ${step.bgGradient} backdrop-blur-sm rounded-3xl p-8 shadow-2xl border border-white/50 overflow-hidden transform transition-all duration-500`}>
                  {/* Decorative elements */}
                  <div className="absolute top-0 right-0 w-20 h-20 bg-white/10 rounded-full blur-2xl"></div>
                  <div className="absolute bottom-0 left-0 w-16 h-16 bg-white/5 rounded-full blur-xl"></div>

                  {/* Step indicator */}
                  <div className="flex items-center justify-between mb-6">
                    <div className={`w-16 h-16 bg-gradient-to-br ${step.gradient} rounded-2xl shadow-xl flex items-center justify-center transform group-hover:scale-110 group-hover:rotate-6 transition-all duration-300`}>
                      <span className="text-white font-black text-xl">{step.number}</span>
                    </div>
                    <div className="text-4xl group-hover:animate-bounce">
                      {step.icon}
                    </div>
                  </div>

                  {/* Illustration */}
                  <div className="mb-8 transform group-hover:scale-105 transition-transform duration-300">
                    {step.illustration}
                  </div>

                  {/* Content */}
                  <div className="space-y-4">
                    <div>
                      <h3 className="text-2xl font-black text-gray-900 mb-1 group-hover:text-transparent group-hover:bg-clip-text group-hover:bg-gradient-to-r group-hover:from-gray-900 group-hover:to-purple-600 transition-all duration-300">
                        {step.title}
                      </h3>
                      <p className={`text-sm font-bold text-transparent bg-clip-text bg-gradient-to-r ${step.gradient} uppercase tracking-wider`}>
                        {step.subtitle}
                      </p>
                    </div>

                    <p className="text-gray-700 leading-relaxed font-medium">
                      {step.description}
                    </p>

                    {/* WhatsApp Button */}
                    {step.hasButton && (
                      <motion.button
                        onClick={handleWhatsAppContact}
                        className="w-full py-4 bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 text-white font-bold rounded-2xl shadow-xl hover:shadow-2xl transition-all duration-300 flex items-center justify-center gap-3 group/btn"
                        whileHover={{ scale: 1.02 }}
                        whileTap={{ scale: 0.98 }}
                      >
                        <span className="text-xl group-hover/btn:animate-pulse">üí¨</span>
                        <span className="group-hover/btn:tracking-wider transition-all duration-300">Start WhatsApp Chat</span>
                      </motion.button>
                    )}
                  </div>

                  {/* Bottom accent */}
                  <div className={`mt-6 pt-4 border-t border-white/30 flex items-center justify-center space-x-2 opacity-70 group-hover:opacity-100 transition-all duration-300`}>
                    <div className={`w-2 h-2 rounded-full bg-gradient-to-r ${step.gradient}`}></div>
                    <span className="text-xs font-bold text-gray-600 uppercase tracking-widest">
                      {index === 0 && "Explore Options"}
                      {index === 1 && "Personal Touch"}
                      {index === 2 && "Quick Setup"}
                      {index === 3 && "Daily Joy"}
                    </span>
                    <div className={`w-2 h-2 rounded-full bg-gradient-to-r ${step.gradient}`}></div>
                  </div>
                </div>

                {/* Connecting line/arrow */}
                {index < steps.length - 1 && (
                  <div className="hidden lg:block absolute top-1/2 -right-4 transform -translate-y-1/2 z-20">
                    <motion.div
                      initial={{ opacity: 0, scale: 0, rotate: -180 }}
                      animate={isInView ? { opacity: 1, scale: 1, rotate: 0 } : { opacity: 0, scale: 0, rotate: -180 }}
                      transition={{ duration: 0.6, delay: index * 0.2 + 0.5 }}
                      className="w-12 h-12 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full flex items-center justify-center shadow-xl border-4 border-white group-hover:scale-110 transition-all duration-300"
                    >
                      <span className="text-white font-bold text-lg">‚Üí</span>
                    </motion.div>
                  </div>
                )}
              </motion.div>
            ))}
          </div>
        </div>

        {/* Enhanced CTA */}
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 50 }}
          transition={{ duration: 0.8, delay: 1.2 }}
          className="text-center mt-20"
        >
          <div className="bg-gradient-to-br from-white to-gray-50 backdrop-blur-xl rounded-3xl p-10 max-w-2xl mx-auto shadow-2xl border border-white/50 relative overflow-hidden">
            <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-purple-500 via-pink-500 to-red-500"></div>

            <div className="text-6xl mb-6 animate-bounce">üöÄ</div>
            <h3 className="text-3xl font-black text-transparent bg-clip-text bg-gradient-to-r from-gray-900 to-purple-600 mb-4">
              Ready for Food Magic?
            </h3>
            <p className="text-gray-600 mb-8 text-lg font-medium">
              Join thousands of happy food lovers enjoying fresh, delicious meals delivered daily ‚ú®
            </p>

            <motion.button 
              onClick={() => {
                const section = document.getElementById('subscription-plans');
                if (section) {
                  window.scrollTo({
                    top: section.offsetTop - 80,
                    behavior: 'smooth'
                  });
                }
              }}
              className="bg-gradient-to-r from-purple-600 via-pink-600 to-red-600 hover:from-purple-700 hover:via-pink-700 hover:to-red-700 text-white font-black text-xl px-12 py-4 rounded-2xl shadow-xl hover:shadow-2xl transition-all duration-300 transform hover:scale-105"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Start Your Food Journey üçΩÔ∏è
            </motion.button>
          </div>
        </motion.div>
      </div>

      {/* Custom CSS for animations */}
      <style jsx>{`
        @keyframes blob {
          0% { transform: translate(0px, 0px) scale(1); }
          33% { transform: translate(30px, -50px) scale(1.1); }
          66% { transform: translate(-20px, 20px) scale(0.9); }
          100% { transform: translate(0px, 0px) scale(1); }
        }
        .animate-blob {
          animation: blob 7s infinite;
        }
        .animation-delay-2000 {
          animation-delay: 2s;
        }
        .animation-delay-4000 {
          animation-delay: 4s;
        }
      `}</style>
    </section>
  );
}
