import React, { useEffect } from 'react';
import './App.css';
import Header from './components/Header';
import HeroSection from './components/HeroSection';
import ProblemStatement from './components/ProblemStatement';
// import Features from './components/Features';
import HowItWorks from './components/HowItWorks';
import SubscriptionPlans from './components/SubscriptionPlans';
import DailyMenu from './components/DailyMenu';
import SatisfactionGuaranteed from './components/SatisfactionGuaranteed';
import Testimonials from './components/Testimonials';
import DeliveryMap from './components/DeliveryMap';
import FAQ from './components/FAQ';
import Footer from './components/Footer';
import { createScrollObserver } from './lib/utils';

function App() {
  useEffect(() => {
    // Mark document as JS-enabled for CSS transitions
    document.body.classList.add('js-enabled');

    // Add intersection observer for fade-in animations with a lower threshold
    const observer = createScrollObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('active');
          // Unobserve after activating to improve performance
          observer.unobserve(entry.target);
        }
      });
    }, { threshold: 0.05 }); // Lower threshold to make it activate earlier

    // Apply to any .section-fade elements
    const fadeElements = document.querySelectorAll('.section-fade');

    // Short timeout to ensure elements are in the DOM
    setTimeout(() => {
      fadeElements.forEach(section => {
        observer.observe(section);
      });
    }, 100);

    return () => {
      observer.disconnect();
    };
  }, []);

  return (
    <div className="font-sans text-gray-800 bg-white overflow-x-hidden min-h-screen">
      <Header />
      <main className="relative">
        <HeroSection />
        <ProblemStatement />
        {/* <Features /> */}
        <DailyMenu />
        <SubscriptionPlans />
        <Testimonials />
        <HowItWorks />
        <SatisfactionGuaranteed />
        <DeliveryMap />
        <FAQ />
      </main>
      <Footer />
    </div>
  );
}

export default App;
