import React, { useState, useRef, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faChevronLeft, 
  faChevronRight, 
  faChevronDown, 
  faChevronUp,
  faStar,
  faUsers
} from '@fortawesome/free-solid-svg-icons';
import './DailyMenu.css';

// Sample menu items data
const MENU_ITEMS = [
  // Greatest Hits
  {
    id: "meal-1",
    name: "Creamy Basil Pesto Chicken Pasta",
    description: "with Parmesan",
    image: "https://images.unsplash.com/photo-1563379926898-05f4575a45d8?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 130,
    category: "Greatest Hits"
  },
  {
    id: "meal-2",
    name: "BBQ Pork Tacos",
    description: "with chipotle ranch slaw and crispy onions",
    image: "https://images.unsplash.com/photo-1565299585323-38d6b0865b47?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 130,
    category: "Greatest Hits"
  },
  {
    id: "meal-3",
    name: "Lasagna-Style Bolognese Gemelli",
    description: "with ricotta",
    image: "https://images.unsplash.com/photo-1618889482923-38250401a84e?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 130,
    category: "Greatest Hits"
  },
  {
    id: "meal-4",
    name: "One-Pan Korean-Style BBQ Meatballs",
    description: "with bok choy and rice",
    image: "https://images.unsplash.com/photo-1695653422259-8a84deb3ef1d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 130,
    category: "Greatest Hits"
  },

  // Supermeals
  {
    id: "meal-5",
    name: "Protein-Packed Quinoa Power Bowl",
    description: "with grilled chicken, avocado and seeds",
    image: "https://images.unsplash.com/photo-1543339308-43e59d6b73a6?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 150,
    category: "Supermeals"
  },
  {
    id: "meal-6",
    name: "Salmon Superfood Salad",
    description: "with kale, berries and omega-rich dressing",
    image: "https://images.unsplash.com/photo-1546069901-ba9599a7e63c?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 150,
    category: "Supermeals"
  },
  {
    id: "meal-7",
    name: "Mediterranean Nutrient Bowl",
    description: "with falafel, hummus and ancient grains",
    image: "https://images.unsplash.com/photo-1512621776951-a57141f2eefd?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 150,
    category: "Supermeals"
  },
  {
    id: "meal-8",
    name: "Antioxidant-Rich Berry Smoothie Bowl",
    description: "with Greek yogurt and chia seeds",
    image: "https://images.unsplash.com/photo-1553530666-ba11a90a0819?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 150,
    category: "Supermeals"
  },

  // Easy Meals
  {
    id: "meal-9",
    name: "Simple Veggie Pasta",
    description: "with olive oil and parmesan",
    image: "https://images.unsplash.com/photo-1473093295043-cdd812d0e601?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 130,
    category: "Easy Meals"
  },
  {
    id: "meal-10",
    name: "Quick Chicken Stir Fry",
    description: "with seasonal vegetables",
    image: "https://images.unsplash.com/photo-1512058454905-6b841e7ad132?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 130,
    category: "Easy Meals"
  },
  {
    id: "meal-11",
    name: "Classic Butter Paneer",
    description: "with jeera rice and raita",
    image: "https://images.unsplash.com/photo-1565557623262-b51c2513a641?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 130,
    category: "Easy Meals"
  },
  {
    id: "meal-12",
    name: "Comforting Khichdi Bowl",
    description: "with pickle and ghee",
    image: "https://images.unsplash.com/photo-1585937421612-70a008356c36?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 130,
    category: "Easy Meals"
  },

  // Day Starters
  {
    id: "meal-13",
    name: "Overnight Oats",
    description: "with fresh fruits and nuts",
    image: "https://images.unsplash.com/photo-1586511925558-a4c6376fe65f?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 100,
    category: "Day Starters"
  },
  {
    id: "meal-14",
    name: "Avocado Toast",
    description: "with poached eggs on multigrain bread",
    image: "https://images.unsplash.com/photo-1525351484163-7529414344d8?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 100,
    category: "Day Starters"
  },
  {
    id: "meal-15",
    name: "South Indian Breakfast Platter",
    description: "with idli, dosa, and sambar",
    image: "https://images.unsplash.com/photo-1630383249896-461df3d6d5fc?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 100,
    category: "Day Starters"
  },
  {
    id: "meal-16",
    name: "Power Protein Smoothie",
    description: "with banana, berries, and whey protein",
    image: "https://images.unsplash.com/photo-1577805947697-89e18249d767?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    price: 100,
    category: "Day Starters"
  }
];

const DailyMenu = () => {
  const [activeCategory, setActiveCategory] = useState("Greatest Hits");
  const [isExpanded, setIsExpanded] = useState(false);
  const scrollRef = useRef(null);
  const containerRef = useRef(null);
  const [currentPage, setCurrentPage] = useState(0);
  const [itemsPerPage, setItemsPerPage] = useState(3); // Default for desktop

  // Categories based on our menu items
  const categories = ["Greatest Hits", "Supermeals", "Easy Meals", "Day Starters"];

  const filteredItems = MENU_ITEMS.filter(item => item.category === activeCategory);
  const pageCount = Math.ceil(filteredItems.length / itemsPerPage);

  // Calculate visible items for current page
  const visibleItems = filteredItems.slice(
    currentPage * itemsPerPage, 
    (currentPage + 1) * itemsPerPage
  );

  // Update itemsPerPage based on screen size
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 640) {
        setItemsPerPage(1);
      } else if (window.innerWidth < 1024) {
        setItemsPerPage(2);
      } else {
        setItemsPerPage(3);
      }
    };

    handleResize(); // Set initial value
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Reset current page when category changes
  useEffect(() => {
    setCurrentPage(0);
  }, [activeCategory]);

  // Smooth scroll to next/previous page
  const navigatePage = (direction) => {
    if (direction === "next" && currentPage < pageCount - 1) {
      setCurrentPage(prev => prev + 1);
    } else if (direction === "prev" && currentPage > 0) {
      setCurrentPage(prev => prev - 1);
    }
  };

  // Price indicator based on category
  const getCategoryPrice = () => {
    switch(activeCategory) {
      case "Supermeals": return "₹150";
      case "Easy Meals": return "₹130";
      case "Day Starters": return "₹100";
      default: return "₹130";
    }
  };

  return (
    <section id="daily-menu" className="py-16 md:py-20 bg-gradient-to-b from-white to-gray-50 overflow-hidden section-fade relative">
      {/* Subtle gradient accents */}
      <div className="gradient-accent accent-1"></div>
      <div className="gradient-accent accent-2"></div>

      <div className="container mx-auto px-4 md:px-6">
        {/* Top heading section with title */}
        <motion.div
          className="text-center mb-10"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold mb-4 text-gray-900 tracking-tight">
            Today's Menu
          </h2>
          <p className="text-lg text-gray-600 max-w-2xl mx-auto">
            Delicious, nutritious meals prepared fresh daily by our expert chefs. Preorder 4 hours prior to your mealtime for these special prices.
          </p>
        </motion.div>

        {/* Enhanced tab navigation */}
        <div className="flex justify-center mb-10">
          <div className="inline-flex bg-gray-100 p-1 rounded-full shadow-inner">
            {categories.map((category, index) => (
              <button
                key={index}
                className={`px-4 py-2 md:px-6 rounded-full text-sm md:text-base font-medium transition-all ${
                  activeCategory === category 
                    ? "bg-white text-gray-900 shadow-sm" 
                    : "text-gray-500 hover:text-gray-700"
                }`}
                onClick={() => setActiveCategory(category)}
              >
                {category}
              </button>
            ))}
          </div>
        </div>

        {/* Pricing panel - redesigned as horizontal cards */}
        <div className="grid grid-cols-3 gap-3 md:gap-5 mb-10">
          <motion.div 
            className={`pricing-card rounded-xl overflow-hidden shadow-sm border ${activeCategory === "Supermeals" ? "border-primary ring-2 ring-primary/20" : "border-gray-200"}`}
            whileHover={{ y: -4, transition: { duration: 0.2 } }}
            initial={{ opacity: 0, y: 10 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.3, delay: 0 }}
          >
            <div className="h-full flex flex-col bg-white">
              <div className="p-3 md:p-4">
                <div className="text-sm text-gray-600 mb-1">Supermeals</div>
                <div className="text-lg md:text-2xl font-bold">₹150</div>
              </div>
            </div>
          </motion.div>

          <motion.div 
            className={`pricing-card rounded-xl overflow-hidden shadow-sm border ${activeCategory === "Easy Meals" ? "border-primary ring-2 ring-primary/20" : "border-gray-200"}`}
            whileHover={{ y: -4, transition: { duration: 0.2 } }}
            initial={{ opacity: 0, y: 10 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.3, delay: 0.1 }}
          >
            <div className="h-full flex flex-col bg-white">
              <div className="p-3 md:p-4">
                <div className="text-sm text-gray-600 mb-1">Meals</div>
                <div className="text-lg md:text-2xl font-bold">₹130</div>
              </div>
            </div>
          </motion.div>

          <motion.div 
            className={`pricing-card rounded-xl overflow-hidden shadow-sm border ${activeCategory === "Day Starters" ? "border-primary ring-2 ring-primary/20" : "border-gray-200"}`}
            whileHover={{ y: -4, transition: { duration: 0.2 } }}
            initial={{ opacity: 0, y: 10 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.3, delay: 0.2 }}
          >
            <div className="h-full flex flex-col bg-white">
              <div className="p-3 md:p-4">
                <div className="text-sm text-gray-600 mb-1">Day Starters</div>
                <div className="text-lg md:text-2xl font-bold">₹100</div>
              </div>
            </div>
          </motion.div>
        </div>

        <div className="text-center text-xs text-gray-500 mb-8">
          * Subscribers receive Supermeals and Desserts as part of their daily plan at no extra cost.
        </div>

        {/* Menu Grid with improved layout */}
        <div ref={containerRef} className="relative mb-8">
          <motion.div
            className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 md:gap-6"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            {visibleItems.map((item, index) => (
              <motion.div
                key={item.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.4, delay: index * 0.05 }}
              >
                <MenuItemCard item={item} />
              </motion.div>
            ))}
          </motion.div>
        </div>

        {/* Navigation controls - redesigned to be more prominent */}
        <div className="flex justify-center items-center mb-6">
          <div className="flex items-center space-x-4">
            <motion.button
              type="button"
              onClick={() => navigatePage("prev")}
              className={`rounded-full w-10 h-10 flex items-center justify-center ${
                currentPage === 0 
                  ? 'bg-gray-100 text-gray-400 cursor-not-allowed' 
                  : 'bg-gray-200 text-gray-800 hover:bg-gray-300'
              }`}
              whileHover={currentPage > 0 ? { scale: 1.05 } : {}}
              whileTap={currentPage > 0 ? { scale: 0.95 } : {}}
            >
              <FontAwesomeIcon icon={faChevronLeft} className="w-4 h-4" />
            </motion.button>

            <div className="flex space-x-2">
              {Array.from({ length: pageCount }).map((_, index) => (
                <button
                  key={index}
                  onClick={() => setCurrentPage(index)}
                  className={`w-2.5 h-2.5 rounded-full transition-colors ${
                    currentPage === index ? 'bg-primary' : 'bg-gray-300'
                  }`}
                  aria-label={`Go to page ${index + 1}`}
                />
              ))}
            </div>

            <motion.button
              type="button"
              onClick={() => navigatePage("next")}
              className={`rounded-full w-10 h-10 flex items-center justify-center ${
                currentPage >= pageCount - 1 
                  ? 'bg-gray-100 text-gray-400 cursor-not-allowed' 
                  : 'bg-gray-200 text-gray-800 hover:bg-gray-300'
              }`}
              whileHover={currentPage < pageCount - 1 ? { scale: 1.05 } : {}}
              whileTap={currentPage < pageCount - 1 ? { scale: 0.95 } : {}}
            >
              <FontAwesomeIcon icon={faChevronRight} className="w-4 h-4" />
            </motion.button>
          </div>
        </div>

        {/* Explore Full Menu Button - Made more prominent */}
        <div className="flex justify-center">
          <motion.button
            onClick={() => setIsExpanded(!isExpanded)}
            className="bg-gradient-to-r from-primary to-primary/90 hover:from-primary/90 hover:to-primary text-white font-medium px-6 py-3 rounded-full shadow-md transition-all flex items-center justify-center gap-2"
            whileHover={{ scale: 1.03 }}
            whileTap={{ scale: 0.98 }}
          >
            {isExpanded ? 'Show Less' : 'Explore Full Menu'}
            <FontAwesomeIcon icon={isExpanded ? faChevronUp : faChevronDown} className="w-4 h-4" />
          </motion.button>
        </div>

        {/* Full Expanded Menu */}
        <AnimatePresence>
          {isExpanded && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: "auto" }}
              exit={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.5 }}
              className="overflow-hidden mt-12"
            >
              <h3 className="text-2xl font-semibold mb-6 text-center">Complete Menu</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 md:gap-6">
