import React from 'react';
import { motion } from 'framer-motion';
import './ProblemStatement.css';

const ProblemStatement = () => {
  const problems = [
    {
      id: 'busy',
      title: 'Too Busy to Cook',
      description: 'Your schedule is packed. Between work, commute, and life, who has time to plan, shop, and cook?',
      icon: '‚è∞',
      fact: 'Average person works 8+ hours daily'
    },
    {
      id: 'junk',
      title: 'Stuck with Junk Food',
      description: 'Quick fixes lead to unhealthy choices. Fast food becomes the norm when convenience matters most.',
      icon: 'üçî',
      fact: '70% of daily intake is processed food'
    },
    {
      id: 'expensive',
      title: 'Expensive & Wasteful',
      description: 'Ordering out daily burns through your budget. Groceries expire unused. Money down the drain.',
      icon: 'üí∏',
      fact: 'Spend ‚Çπ500+ daily on food delivery'
    },
    {
      id: 'away',
      title: 'Away from Home',
      description: 'Living away from family means no home-cooked meals. Missing that comfort and nutrition.',
      icon: 'üè†',
      fact: 'Missing the comfort of home-cooked meals'
    }
  ];

  const solutionFeatures = [
    {
      iconClass: 'icon-meals',
      title: 'Daily Meals Delivered',
      description: 'Fresh, home-style meals prepared daily and delivered right to your doorstep',
      gradient: 'from-blue-500 to-purple-600',
      bgColor: 'bg-blue-50',
      iconBg: 'bg-blue-100'
    },
    {
      iconClass: 'icon-time',
      title: 'Save Your Time',
      description: 'No more planning, shopping, or cooking. We handle everything while you focus on what matters',
      gradient: 'from-green-500 to-teal-600',
      bgColor: 'bg-green-50',
      iconBg: 'bg-green-100'
    },
    {
      iconClass: 'icon-budget',
      title: 'Budget-Friendly',
      description: 'Starting at ‚Çπ80/day - more affordable than ordering out, with zero waste',
      gradient: 'from-yellow-500 to-orange-600',
      bgColor: 'bg-yellow-50',
      iconBg: 'bg-yellow-100'
    },
    {
      iconClass: 'icon-home',
      title: 'Home-Style Comfort',
      description: 'Taste the comfort of home-cooked meals, even when you\'re away from family',
      gradient: 'from-pink-500 to-red-600',
      bgColor: 'bg-pink-50',
      iconBg: 'bg-pink-100'
    }
  ];

  return (
    <>
      {/* Problem Statement Section */}
      <section id="problem-statement" className="problem-section">
        <div className="container mx-auto px-4 md:px-6">
          {/* Problem Section Header */}
          <motion.div 
            className="text-center mb-16"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true, margin: "-50px" }}
            transition={{ duration: 0.4, ease: "easeOut" }}
          >
            <motion.h2 
              className="problem-title"
              initial={{ opacity: 0, y: 15 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.4, delay: 0.1, ease: "easeOut" }}
            >
              We Know Your <span className="text-red-600">Daily Struggle</span>
            </motion.h2>
            <motion.p 
              className="problem-subtitle"
              initial={{ opacity: 0, y: 15 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.4, delay: 0.2, ease: "easeOut" }}
            >
              Modern life shouldn't mean compromising on nutrition and taste
            </motion.p>
          </motion.div>

          {/* Problems List */}
          <div className="problems-list">
            {problems.map((problem, index) => (
              <motion.div
                key={problem.id}
                className="problem-item"
                initial={{ opacity: 0, x: index % 2 === 0 ? -30 : 30 }}
                whileInView={{ opacity: 1, x: 0 }}
                viewport={{ once: true, margin: "-30px" }}
                transition={{ duration: 0.4, delay: index * 0.1, ease: "easeOut" }}
              >
                <motion.div 
                  className="problem-icon-large"
                  initial={{ scale: 0.8, rotate: -90 }}
                  whileInView={{ scale: 1, rotate: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.3, delay: 0.2 + index * 0.1, ease: "easeOut" }}
                >
                  {problem.icon}
                </motion.div>

                <div className="problem-content-text">
                  <motion.h3 
                    className="problem-title-large"
                    initial={{ opacity: 0, y: 15 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    transition={{ duration: 0.3, delay: 0.25 + index * 0.1, ease: "easeOut" }}
                  >
                    {problem.title}
                  </motion.h3>

                  <motion.p 
                    className="problem-description-text"
                    initial={{ opacity: 0, y: 15 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    transition={{ duration: 0.3, delay: 0.3 + index * 0.1, ease: "easeOut" }}
                  >
                    {problem.description}
                  </motion.p>

                  <motion.div 
                    className="problem-fact"
                    initial={{ opacity: 0, scale: 0.9 }}
                    whileInView={{ opacity: 1, scale: 1 }}
                    viewport={{ once: true }}
                    transition={{ duration: 0.25, delay: 0.35 + index * 0.1, ease: "easeOut" }}
                  >
                    {problem.fact}
                  </motion.div>
                </div>
              </motion.div>
            ))}
          </div>

          {/* Transition to Solution */}
          <motion.div 
            className="problem-cta"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true, margin: "-50px" }}
            transition={{ duration: 0.4, delay: 0.4, ease: "easeOut" }}
          >
            <h3 className="cta-title">Ready to Break Free from These Problems?</h3>
            <p className="cta-description">Let us handle your daily meals while you focus on what matters most</p>
          </motion.div>
        </div>
      </section>

      {/* Features Section - Unified Design */}
      <section id="features" className="solution-section">
        <div className="container mx-auto px-4 md:px-6">
          {/* Unified Header and Pricing Section */}
          <div className="unified-hero-section">
            {/* Header Section */}
            <motion.div 
              className="text-center mb-12"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true, margin: "-50px" }}
              transition={{ duration: 0.4, ease: "easeOut" }}
            >
              <motion.h2 
                className="hero-title"
                initial={{ opacity: 0, y: 15 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.4, delay: 0.1, ease: "easeOut" }}
              >
                Myna <span className="text-primary">Kitchen</span>
              </motion.h2>
              <motion.h3 
                className="hero-subtitle"
                initial={{ opacity: 0, y: 15 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.4, delay: 0.15, ease: "easeOut" }}
              >
                Daily Meals Delivered
              </motion.h3>
              <motion.p 
                className="hero-description"
                initial={{ opacity: 0, y: 15 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.4, delay: 0.2, ease: "easeOut" }}
              >
                With us you no longer have to run a kitchen, manage groceries or worry about your daily food.
              </motion.p>
            </motion.div>

            {/* Pricing Highlight - Integrated Design */}
            <motion.div 
              className="pricing-hero-card"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true, margin: "-50px" }}
              transition={{ duration: 0.4, delay: 0.25, ease: "easeOut" }}
            >
              <div className="pricing-content">
                <p className="pricing-description-top">Fresh meals delivered to your doorstep</p>
                <div className="pricing-badge">Starting at</div>
                <div className="pricing-main">
                  <span className="price-amount">‚Çπ80</span>
                  <span className="price-period">/day</span>
                </div>

                <motion.button 
                  className="pricing-cta-button"
                  whileHover={{ scale: 1.02, transition: { duration: 0.15 } }}
                  whileTap={{ scale: 0.98, transition: { duration: 0.1 } }}
                  onClick={() => {
                    const section = document.getElementById('subscription-plans');
                    if (section) {
                      window.scrollTo({
                        top: section.offsetTop - 80,
                        behavior: 'smooth'
                      });
                    }
                  }}
                >
                  Check Plans and Pricing
                </motion.button>
              </div>
            </motion.div>
          </div>

          {/* Solution Features Grid - Redesigned */}
          <div className="features-grid">
            {solutionFeatures.map((feature, index) => (
              <motion.div
                key={index}
                className="feature-card"
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true, margin: "-30px" }}
                transition={{ duration: 0.3, delay: index * 0.05, ease: "easeOut" }}
                whileHover={{ y: -4, transition: { duration: 0.15, ease: "easeOut" } }}
              >
                <div className="feature-icon">
                  <div className={`modern-icon ${feature.iconClass}`}></div>
                </div>
                <div className="feature-content">
                  <h3 className="feature-title">{feature.title}</h3>
                  <p className="feature-description">{feature.description}</p>
                </div>
                <div className={`feature-accent feature-accent-${index + 1}`}></div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>
    </>
  );
};

export default ProblemStatement;
