import React from 'react';
import { motion } from 'framer-motion';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheck } from '@fortawesome/free-solid-svg-icons';

const SubscriptionPlans = () => {
  const pricingPlans = [
    {
      id: 'one-meal',
      title: 'One Meal a Day',
      price: 120,
      description: 'Lunch or Dinner',
      popular: false,
      features: [
        'Choose your meal of the day',
        'Free delivery',
        'Minimum commitment'
      ]
    },
    {
      id: 'two-meals',
      title: 'Two Meals a Day',
      price: 220,
      description: 'Lunch and Dinner',
      popular: true,
      features: [
        'Intermittent fasting friendly',
        'Non-veg & veg options',
        'Free delivery',
        'Premium menu options',
        'Priority customer support'
      ]
    },
    {
      id: 'family-plan',
      title: 'Family Plan',
      price: 440,
      description: 'Two meals for two people',
      popular: false,
      features: [
        'Perfect for couples',
        'Customizable meal preferences',
        'Free delivery',
        'Special weekend menu',
        'Premium customer support'
      ]
    }
  ];

  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      window.scrollTo({
        top: section.offsetTop - 80,
        behavior: 'smooth'
      });
    }
  };

  const formatPrice = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0
    }).format(amount);
  };

