import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faChevronLeft, 
  faChevronRight, 
  faStar,
  faUsers,
  faCircle,
  faLeaf,
  faExclamationTriangle
} from '@fortawesome/free-solid-svg-icons';
import { loadImageSafely, formatPrice } from '../lib/utils';

/**
 * Individual menu item card component
 * Handles image navigation, error states, and accessibility
 */
function MenuItemCard({ item }) {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [imageLoading, setImageLoading] = useState(true);
  const [imageError, setImageError] = useState(false);

  // Safe navigation functions with bounds checking
  const nextImage = () => {
    if (item.images && item.images.length > 0) {
      setCurrentImageIndex((prevIndex) => 
        prevIndex === item.images.length - 1 ? 0 : prevIndex + 1
      );
    }
  };

  const prevImage = () => {
    if (item.images && item.images.length > 0) {
      setCurrentImageIndex((prevIndex) => 
        prevIndex === 0 ? item.images.length - 1 : prevIndex - 1
      );
    }
  };

  const goToImage = (index) => {
    if (item.images && index >= 0 && index < item.images.length) {
      setCurrentImageIndex(index);
    }
  };

  const handleImageError = (e) => {
    console.warn(`Image failed to load: ${item.images[currentImageIndex]}`);
    setImageError(true);
    setImageLoading(false);
    // Set fallback image
    e.target.src = '/images/placeholder-meal.jpg';
  };

  const handleImageLoad = () => {
    setImageLoading(false);
    setImageError(false);
  };

  // Keyboard navigation for accessibility
  const handleKeyPress = (e, action) => {
    if (e.key === 'Enter' || e.key === ' ') {
      e.preventDefault();
      action();
    }
  };

  if (!item) {
    return null;
  }

  const hasMultipleImages = item.images && item.images.length > 1;
  const currentImage = item.images?.[currentImageIndex] || '/images/placeholder-meal.jpg';

  return (
    <motion.div
      layout
      className="bg-white rounded-xl border-2 border-black shadow-lg hover:shadow-xl transition-all duration-300 overflow-hidden"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      transition={{ duration: 0.3 }}
    >
      {/* Image Section */}
      <div className="relative w-full h-48 md:h-56 lg:h-64 overflow-hidden bg-gray-100">
        {/* Loading state */}
        {imageLoading && (
          <div className="absolute inset-0 flex items-center justify-center bg-gray-200">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-accent"></div>
          </div>
        )}

        {/* Main image */}
        <img
          src={currentImage}
          alt={`${item.name} - Image ${currentImageIndex + 1}`}
          className={`w-full h-full object-cover transition-opacity duration-300 ${
            imageLoading ? 'opacity-0' : 'opacity-100'
          }`}
          onLoad={handleImageLoad}
          onError={handleImageError}
          loading="lazy"
        />

        {/* Navigation arrows for multiple images */}
        {hasMultipleImages && !imageLoading && (
          <AnimatePresence>
            <motion.button
              onClick={prevImage}
              onKeyPress={(e) => handleKeyPress(e, prevImage)}
              className="absolute left-2 top-1/2 transform -translate-y-1/2 bg-black/70 hover:bg-black/90 text-white p-2 rounded-full transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-white"
              aria-label="Previous image"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              <FontAwesomeIcon icon={faChevronLeft} className="w-4 h-4" />
            </motion.button>

            <motion.button
              onClick={nextImage}
              onKeyPress={(e) => handleKeyPress(e, nextImage)}
              className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-black/70 hover:bg-black/90 text-white p-2 rounded-full transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-white"
              aria-label="Next image"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              <FontAwesomeIcon icon={faChevronRight} className="w-4 h-4" />
            </motion.button>
          </AnimatePresence>
        )}

        {/* Image indicators */}
        {hasMultipleImages && (
          <div className="absolute bottom-2 left-1/2 transform -translate-x-1/2 flex space-x-2">
            {item.images.map((_, index) => (
              <button
                key={index}
                onClick={() => goToImage(index)}
                onKeyPress={(e) => handleKeyPress(e, () => goToImage(index))}
                className={`w-2 h-2 rounded-full transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-white ${
                  index === currentImageIndex ? 'bg-white' : 'bg-white/50'
                }`}
                aria-label={`Go to image ${index + 1}`}
              />
            ))}
          </div>
        )}

        {/* Category badge */}
        <div className="absolute top-3 left-3">
          <span className="bg-accent text-white px-3 py-1 rounded-full text-xs font-semibold">
            {item.category}
          </span>
        </div>

        {/* Vegetarian indicator */}
        {item.isVegetarian && (
          <div className="absolute top-3 right-3">
            <div className="bg-green-500 text-white p-1.5 rounded-full" title="Vegetarian">
              <FontAwesomeIcon icon={faLeaf} className="w-3 h-3" />
            </div>
          </div>
        )}
      </div>

      {/* Content Section */}
      <div className="p-4 space-y-3">
        {/* Title and Price */}
        <div className="flex justify-between items-start gap-3">
          <h3 className="font-bold text-lg text-black leading-tight flex-1">
            {item.name}
          </h3>
          <span className="text-accent font-black text-xl whitespace-nowrap">
            {formatPrice(item.price)}
          </span>
        </div>

        {/* Description */}
        <p className="text-gray-600 text-sm leading-relaxed line-clamp-2">
          {item.description}
        </p>

        {/* Nutrition Info (if available) */}
        {item.nutritionInfo && (
          <div className="grid grid-cols-4 gap-2 pt-2 border-t border-gray-100">
            <div className="text-center">
              <div className="text-xs text-gray-500">Calories</div>
              <div className="font-semibold text-sm">{item.nutritionInfo.calories}</div>
            </div>
            <div className="text-center">
              <div className="text-xs text-gray-500">Protein</div>
              <div className="font-semibold text-sm">{item.nutritionInfo.protein}g</div>
            </div>
            <div className="text-center">
              <div className="text-xs text-gray-500">Carbs</div>
              <div className="font-semibold text-sm">{item.nutritionInfo.carbs}g</div>
            </div>
            <div className="text-center">
              <div className="text-xs text-gray-500">Fat</div>
              <div className="font-semibold text-sm">{item.nutritionInfo.fat}g</div>
            </div>
          </div>
        )}

        {/* Allergen warnings */}
        {item.allergens && item.allergens.length > 0 && (
          <div className="flex items-center gap-2 pt-2">
            <FontAwesomeIcon icon={faExclamationTriangle} className="text-orange-500 w-3 h-3" />
            <span className="text-xs text-gray-600">
              Contains: {item.allergens.join(', ')}
            </span>
          </div>
        )}

        {/* Error state for image */}
        {imageError && (
          <div className="text-xs text-red-500 text-center">
            📷 Image couldn't load
          </div>
        )}
      </div>
    </motion.div>
  );
}

export default MenuItemCard;
