import React, { useEffect, useState, useRef, useCallback } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faLocationDot, 
  faSearch, 
  faBuildingUser, 
  faCircleCheck, 
  faMapPin,
  faDirections,
  faChevronUp,
  faChevronDown,
  faChevronRight,
  faChevronLeft,
  faCompass,
  faTruck,
  faInfoCircle,
  faRupeeSign
} from '@fortawesome/free-solid-svg-icons';
import "./DeliveryMap.css";

// Tech parks data
const TECH_PARKS = [
  {
    id: 1,
    name: "Tidel Park",
    address: "CSIR Rd, Taramani, Chennai, Tamil Nadu 600113",
    coordinates: [12.9906, 80.2487]
  },
  {
    id: 2,
    name: "RMZ Millenia",
    address: "Phase 2, Campus 4B, No.143, MGR Rd, Perungudi, Chennai, Tamil Nadu 600096",
    coordinates: [12.9714, 80.2420]
  },
  {
    id: 3,
    name: "Ramanujan IT City",
    address: "Rajiv Gandhi Salai, Taramani, Chennai, Tamil Nadu 600113",
    coordinates: [12.9862, 80.2418]
  },
  {
    id: 4,
    name: "TRIL Infopark",
    address: "Ramanujan IT City, Rajiv Gandhi Salai, Taramani, Chennai, Tamil Nadu 600113",
    coordinates: [12.9876, 80.2431]
  },
  {
    id: 5,
    name: "Ascendas IT Park",
    address: "Taramani Rd, Taramani, Chennai, Tamil Nadu 600113",
    coordinates: [12.9845, 80.2402]
  },
  {
    id: 6,
    name: "Siruseri IT Park",
    address: "Siruseri, Chennai, Tamil Nadu 603103",
    coordinates: [12.8254, 80.2181]
  },
  {
    id: 7,
    name: "Prestige Cyber Towers",
    address: "Old Mahabalipuram Road, Kandanchavadi, Chennai, Tamil Nadu 600096",
    coordinates: [12.9599, 80.2456]
  },
  {
    id: 8,
    name: "Ericsson India Global Services",
    address: "MGR Road, Sholinganallur, Chennai, Tamil Nadu 600119",
    coordinates: [12.9004, 80.2276]
  },
  {
    id: 9,
    name: "Elcot SEZ",
    address: "Sholinganallur, Chennai, Tamil Nadu 600119",
    coordinates: [12.8884, 80.2270]
  },
  {
    id: 10,
    name: "DLF IT Park",
    address: "Mount Poonamallee Road, Manapakkam, Chennai, Tamil Nadu 600089",
    coordinates: [13.0199, 80.1821]
  },
  {
    id: 11,
    name: "One Indiabulls Park",
    address: "Egattur Village, Navallur, Chennai, Tamil Nadu 603103",
    coordinates: [12.8399, 80.2265]
  },
  {
    id: 12,
    name: "Thoraipakkam IT Park",
    address: "Thoraipakkam, Chennai, Tamil Nadu 600097",
    coordinates: [12.9384, 80.2339]
  },
  {
    id: 13,
    name: "Porur",
    address: "Porur, Chennai, Tamil Nadu 600116",
    coordinates: [13.0356, 80.1559]
  },
  {
    id: 14,
    name: "Velachery",
    address: "Velachery, Chennai, Tamil Nadu 600042",
    coordinates: [12.9755, 80.2200]
  },
  {
    id: 15,
    name: "Pallikaranai",
    address: "Pallikaranai, Chennai, Tamil Nadu 600100",
    coordinates: [12.9471, 80.2044]
  },
  {
    id: 16,
    name: "Adyar",
    address: "Adyar, Chennai, Tamil Nadu 600020",
    coordinates: [13.0067, 80.2567]
  }
];

const DeliveryMap = () => {
  const [mapLoaded, setMapLoaded] = useState(false);
  const [activeLocationId, setActiveLocationId] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [isListExpanded, setIsListExpanded] = useState(false);
  const mapRef = useRef(null);
  const mapInstanceRef = useRef(null);

  // Create an updated list of OMR coordinates including all tech parks for a more accurate route
  const omrCoordinates = [...TECH_PARKS].map(park => park.coordinates).sort((a, b) => {
    // Sort from north to south roughly (higher to lower latitude values)
    return b[0] - a[0];
  });

  // Filter locations based on search query
  const filteredLocations = TECH_PARKS.filter(park => 
    park.name.toLowerCase().includes(searchQuery.toLowerCase()) || 
    park.address.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Define initMap with useCallback to memoize it
  const initMap = useCallback(() => {
    if (!window.L || !document.getElementById('map')) return;

    // Check if map is already initialized and clean it up first
    if (mapInstanceRef.current) {
      mapInstanceRef.current.remove();
      mapInstanceRef.current = null;
    }

    const L = window.L;

    // Center on a point in the middle of OMR
    const chennaiCenter = [12.9400, 80.2300]; // Adjusted to better center the OMR corridor

    // Create the map with an elegant, minimal style
    const map = L.map('map', {
      zoomControl: false, // We'll add our own zoom control in a better position
      scrollWheelZoom: false, // Disable scroll wheel zoom for better UX on embedded maps
      attributionControl: false // Hide attribution initially for cleaner look
    }).setView(chennaiCenter, 12);

    // Store map instance in ref for later access
    mapInstanceRef.current = map;

    // Add custom attribution control in bottom-right
    L.control.attribution({
      position: 'bottomright',
      prefix: '© Myna Kitchen'
    }).addTo(map);

    // Add zoom control in bottom-right
    L.control.zoom({
      position: 'bottomright'
    }).addTo(map);

    // Add an elegant tile layer with minimal styling
    L.tileLayer('https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png', {
      attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>',
      subdomains: 'abcd',
      maxZoom: 19
    }).addTo(map);

    // Create a custom icon for markers that fits our design - using amber color theme
    const customIcon = L.divIcon({
      className: 'custom-map-marker',
      html: `<div style="background-color: #D4930D; width: 12px; height: 12px; border-radius: 50%; border: 2px solid white; box-shadow: 0 0 0 2px rgba(212, 147, 13, 0.4);"></div>`,
      iconSize: [18, 18],
      iconAnchor: [9, 9]
    });

    // Create a larger icon for hover/active state
    const activeIcon = L.divIcon({
      className: 'custom-map-marker-active',
      html: `<div style="background-color: #D4930D; width: 16px; height: 16px; border-radius: 50%; border: 3px solid white; box-shadow: 0 0 0 4px rgba(212, 147, 13, 0.5);"></div>`,
      iconSize: [24, 24],
      iconAnchor: [12, 12]
    });

    // Store markers for later reference
    const markers = [];

    // Add markers for each tech park
    TECH_PARKS.forEach((park) => {
      const marker = L.marker(park.coordinates, { icon: customIcon }).addTo(map);

      marker.bindPopup(`
        <div style="padding: 8px;">
          <h4 style="margin: 0 0 4px 0; font-weight: bold; color: #D4930D;">${park.name}</h4>
          <p style="margin: 0; font-size: 12px; color: #666;">${park.address}</p>
          <div style="margin-top: 8px; display: flex; align-items: center; font-size: 11px; color: #10b981;">
            <span style="margin-right: 4px;">✓</span>
            <span>Free Delivery Available</span>
          </div>
        </div>
      `, {
        className: 'custom-popup'
      });

      // Add hover effects
      marker.on('mouseover', function() {
        this.setIcon(activeIcon);
      });

      marker.on('mouseout', function() {
        this.setIcon(customIcon);
      });

      markers.push({ marker, park });
    });

    // Center on an active location if one is selected
    if (activeLocationId) {
      const selectedPark = TECH_PARKS.find(park => park.id === activeLocationId);
      if (selectedPark) {
        map.setView(selectedPark.coordinates, 14);
      }
    }

    setMapLoaded(true);
  }, [activeLocationId]);

  // Load Leaflet once and then use it
  useEffect(() => {
    const loadLeaflet = async () => {
      if (window.L) {
        // Leaflet is already loaded
        handleLeafletInit();
        return;
      }

      try {
        // Load Leaflet CSS
        const cssLink = document.createElement('link');
        cssLink.rel = 'stylesheet';
        cssLink.href = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.css';
        cssLink.integrity = 'sha256-p4NxAoJBhIIN+hmNHrzRCf9tD/miZyoHS5obTRR9BMY=';
        cssLink.crossOrigin = '';
        document.head.appendChild(cssLink);

        // Load Leaflet JS
        const script = document.createElement('script');
        script.src = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.js';
        script.integrity = 'sha256-20nQCchB9co0qIjJZRGuk2/Z9VM+kNiyxNV1lvTlZBo=';
        script.crossOrigin = '';
        script.onload = handleLeafletInit;
        script.onerror = () => console.error('Failed to load Leaflet');
        document.head.appendChild(script);
      } catch (error) {
        console.error('Failed to load Leaflet:', error);
      }
    };

    const handleLeafletInit = () => {
      // Wait for the next tick to ensure DOM is ready
      setTimeout(initMap, 100);
    };

    // Clean up if map already exists
    if (mapInstanceRef.current) {
      mapInstanceRef.current.remove();
      mapInstanceRef.current = null;
    }

    loadLeaflet();

    return () => {
      if (mapInstanceRef.current) {
        mapInstanceRef.current.remove();
        mapInstanceRef.current = null;
      }
    };
  }, [initMap]);

  // Update map marker when activeLocationId changes
  useEffect(() => {
    if (mapInstanceRef.current && window.L && activeLocationId) {
      const selectedPark = TECH_PARKS.find(park => park.id === activeLocationId);
      if (selectedPark) {
        const L = window.L;
        const marker = L.marker(selectedPark.coordinates).addTo(mapInstanceRef.current);
        marker.bindPopup(`<b>${selectedPark.name}</b><br>${selectedPark.address}`).openPopup();

        // Add cleanup to remove the marker when selection changes
        return () => {
          if (mapInstanceRef.current) {
            mapInstanceRef.current.removeLayer(marker);
          }
        };
      }
    }
  }, [activeLocationId, mapLoaded]);

  // Function to handle clicking on a tech park in the list
  const handleParkClick = (parkId) => {
    setActiveLocationId(parkId);
    if (mapRef.current && mapRef.current.centerOnLocation) {
      mapRef.current.centerOnLocation(parkId);
    }
  };

  return (
    <section id="delivery-map" className="py-12 md:py-20 bg-white overflow-hidden relative section-fade">
      <div className="container mx-auto px-4 md:px-6">
        {/* Header Section */}
        <div className="text-center mb-12 md:mb-16">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6 }}
          >
            <h2 className="text-3xl md:text-5xl font-bold mb-4 md:mb-6 text-gray-900 tracking-tight">
              Find Us Nearby
            </h2>
            <p className="text-lg md:text-xl text-gray-600 max-w-3xl mx-auto mb-6 md:mb-8">
              We deliver to all major tech parks and office areas. Check if your workplace is covered in our delivery zone.
            </p>

            {/* Delivery Information - Prominently displayed */}
            <motion.div 
              className="bg-gradient-to-r from-emerald-50 to-blue-50 border border-emerald-200 rounded-2xl p-4 md:p-6 max-w-4xl mx-auto"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.6, delay: 0.2 }}
            >
              <div className="flex flex-col md:flex-row items-center justify-center gap-4 md:gap-6">
                <div className="flex items-center gap-3">
                  <div className="bg-emerald-500 p-3 rounded-full">
                    <FontAwesomeIcon icon={faTruck} className="w-5 h-5 text-white" />
                  </div>
                  <div className="text-left">
                    <h3 className="font-bold text-emerald-800 text-lg md:text-xl">
                      Free Delivery Zone
                    </h3>
                    <p className="text-emerald-700 text-sm md:text-base">
                      Within 8 kms from Thoraipakkam
                    </p>
                  </div>
                </div>

                <div className="hidden md:block w-px h-12 bg-emerald-300"></div>
                <div className="block md:hidden w-full h-px bg-emerald-300"></div>

                <div className="flex items-center gap-3">
                  <div className="bg-amber-500 p-3 rounded-full">
                    <FontAwesomeIcon icon={faRupeeSign} className="w-5 h-5 text-white" />
                  </div>
                  <div className="text-left">
                    <h3 className="font-bold text-amber-800 text-lg md:text-xl">
                      Beyond 8 kms
                    </h3>
                    <p className="text-amber-700 text-sm md:text-base">
                      Delivery charges may apply
                    </p>
                  </div>
                </div>
              </div>

              <div className="mt-4 md:mt-6 pt-4 md:pt-6 border-t border-emerald-200">
                <div className="flex flex-col md:flex-row items-center justify-center gap-2 md:gap-4">
                  <div className="flex items-center gap-2 text-gray-600">
                    <FontAwesomeIcon icon={faInfoCircle} className="w-4 h-4 text-blue-500" />
                    <span className="text-sm md:text-base font-medium">Check prices below*</span>
                  </div>
                  <div className="hidden md:block text-gray-300">|</div>
                  <div className="flex items-center gap-2 text-gray-600">
                    <FontAwesomeIcon icon={faCircleCheck} className="w-4 h-4 text-emerald-500" />
                    <span className="text-sm md:text-base">Orders before 10 AM delivered by lunch</span>
                  </div>
                </div>
              </div>
            </motion.div>
          </motion.div>
        </div>

        {/* Main Content */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 md:gap-8">
          {/* Map Column - Expanded when list is minimized */}
          <motion.div
            className={`relative order-2 lg:order-1 ${isListExpanded ? 'lg:col-span-2' : 'lg:col-span-3'}`}
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.7 }}
          >
            <div className="bg-white rounded-2xl shadow-lg overflow-hidden h-full border border-gray-100">
              {/* Map Title Bar - Improved mobile design */}
              <div className="bg-gradient-to-r from-amber-700 to-amber-800 text-white p-3 md:p-4 flex items-center justify-between">
                <div className="flex items-center">
                  <FontAwesomeIcon icon={faDirections} className="w-4 h-4 md:w-5 md:h-5 mr-2" />
                  <h3 className="font-semibold text-sm md:text-base">Delivery Coverage Map</h3>
                </div>
                <div className="flex items-center gap-2 md:gap-3">
                  <div className="bg-white/20 backdrop-blur-sm rounded-full px-2 md:px-3 py-1 text-xs font-medium">
                    {TECH_PARKS.length} locations
                  </div>
                  <button 
                    onClick={() => setIsListExpanded(!isListExpanded)}
                    className="lg:hidden flex items-center justify-center p-2 bg-white/20 backdrop-blur-sm rounded-full"
                    aria-label={isListExpanded ? "Hide location list" : "Show location list"}
                  >
                    <FontAwesomeIcon icon={isListExpanded ? faChevronDown : faChevronUp} className="w-3 h-3" />
                  </button>
                </div>
              </div>

              {/* Map Container - Improved mobile aspect ratio */}
              <div className="aspect-[4/3] md:aspect-[16/9] relative">
                <div id="map" className="absolute inset-0"></div>
                {!mapLoaded && (
                  <div className="absolute inset-0 flex items-center justify-center bg-gray-50">
                    <div className="text-center text-gray-500">
                      <div className="w-8 h-8 md:w-12 md:h-12 mb-3 md:mb-4 mx-auto border-3 md:border-4 border-gray-300 border-t-amber-600 rounded-full animate-spin"></div>
                      <p className="text-sm md:text-base">Loading map...</p>
                    </div>
                  </div>
                )}

                {/* Map custom controls - Mobile optimized */}
                <div className="absolute top-2 md:top-4 left-2 md:left-4 z-10">
                  <div className="bg-white rounded-lg shadow-md p-1 md:p-2 flex flex-col space-y-1 md:space-y-2">
                    <button 
                      className="p-2 hover:bg-amber-50 rounded-md transition-colors focus:outline-none text-amber-700" 
                      title="Center map"
                      onClick={() => mapInstanceRef.current?.setView([12.9400, 80.2300], 12)}
                    >
                      <FontAwesomeIcon icon={faMapPin} className="w-4 h-4 md:w-5 md:h-5" />
                    </button>
                    <button 
                      className="p-2 hover:bg-amber-50 rounded-md transition-colors focus:outline-none text-amber-700" 
                      title="Show all locations"
                      onClick={() => setActiveLocationId(null)}
                    >
                      <FontAwesomeIcon icon={faBuildingUser} className="w-4 h-4 md:w-5 md:h-5" />
                    </button>
                    <button 
                      className="p-2 hover:bg-amber-50 rounded-md transition-colors focus:outline-none hidden lg:block text-amber-700" 
                      title={isListExpanded ? "Hide location list" : "Show location list"}
                      onClick={() => setIsListExpanded(!isListExpanded)}
                    >
                      <FontAwesomeIcon icon={isListExpanded ? faChevronRight : faChevronLeft} className="w-4 h-4 md:w-5 md:h-5" />
                    </button>
                  </div>
                </div>

                {/* Floating location button on map (visible when list is collapsed) */}
                {!isListExpanded && (
                  <div className="absolute bottom-2 md:bottom-4 right-2 md:right-4 z-10 lg:block hidden">
                    <button
                      onClick={() => setIsListExpanded(true)}
                      className="bg-amber-700 shadow-lg rounded-full p-2 md:p-3 flex items-center gap-2 hover:bg-amber-800 transition-colors text-white"
                    >
                      <FontAwesomeIcon icon={faCompass} className="w-4 h-4 md:w-5 md:h-5" />
                      <span className="font-medium text-sm md:text-base">View Locations</span>
                    </button>
                  </div>
                )}
              </div>

              {/* Map info bar - Mobile optimized */}
              <div className="p-3 md:p-4 bg-gray-50 border-t border-gray-100 flex flex-col md:flex-row justify-between items-start md:items-center gap-2 md:gap-0">
                <div className="flex items-center text-xs md:text-sm text-gray-500">
                  <FontAwesomeIcon icon={faLocationDot} className="w-3 h-3 md:w-4 md:h-4 mr-1 text-amber-700" />
                  <span>OMR Tech Corridor, Chennai</span>
                </div>
                <div className="flex items-center text-xs md:text-sm font-medium text-emerald-600">
                  <FontAwesomeIcon icon={faCircleCheck} className="w-3 h-3 md:w-4 md:h-4 mr-1" />
                  <span>Free delivery within 8km radius</span>
                </div>
              </div>
            </div>
          </motion.div>

          {/* Location List Column - Mobile optimized */}
          <AnimatePresence>
            {isListExpanded && (
              <motion.div
                className="order-1 lg:order-2"
                initial={{ opacity: 0, x: 30 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: 30 }}
                transition={{ duration: 0.3 }}
              >
                <div className="bg-white rounded-2xl p-4 md:p-6 shadow-lg h-full border border-gray-100 flex flex-col">
                  <div className="flex items-center justify-between mb-4 md:mb-6">
                    <h3 className="text-xl md:text-2xl font-bold">Delivery Locations</h3>
                    <button
                      onClick={() => setIsListExpanded(false)}
                      className="p-2 hover:bg-amber-50 rounded-full transition-colors text-amber-700"
                      aria-label="Minimize location list"
                    >
                      <FontAwesomeIcon icon={faChevronRight} className="w-4 h-4" />
                    </button>
                  </div>

                  {/* Search box - Mobile optimized */}
                  <div className="relative mb-4 md:mb-6">
                    <div className="absolute inset-y-0 left-0 flex items-center pl-3 pointer-events-none">
                      <FontAwesomeIcon icon={faSearch} className="w-4 h-4 text-gray-400" />
                    </div>
                    <input 
                      type="text" 
                      className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-xl focus:ring-amber-500 focus:border-amber-500 block w-full pl-10 p-3 transition-colors" 
                      placeholder="Search by name or address"
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                    />
                  </div>

                  {/* Location list with scroll - Mobile optimized */}
                  <div className="flex-1 overflow-y-auto pr-1 md:pr-2 space-y-2 md:space-y-3">
                    {filteredLocations.length > 0 ? (
                      filteredLocations.map((park) => (
                        <motion.div
                          key={park.id}
                          className={`p-3 md:p-4 rounded-xl cursor-pointer transition-all ${
                            activeLocationId === park.id
                              ? 'bg-amber-50 border-amber-500 ring-2 ring-amber-200'
                              : 'bg-gray-50 hover:bg-gray-100 border-transparent'
                          } border`}
                          onClick={() => handleParkClick(park.id)}
                          whileHover={{ scale: 1.01 }}
                          whileTap={{ scale: 0.99 }}
                        >
                          <div className="flex items-start">
                            <div className="mr-3 mt-1 flex-shrink-0">
                              <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                                activeLocationId === park.id
                                  ? 'bg-amber-600 text-white'
                                  : 'bg-gray-200 text-gray-600'
                              }`}>
                                <FontAwesomeIcon icon={faBuildingUser} className="w-4 h-4" />
                              </div>
                            </div>
                            <div className="flex-1 min-w-0">
                              <h4 className="font-semibold text-gray-900 text-sm md:text-base truncate">{park.name}</h4>
                              <p className="text-xs md:text-sm text-gray-500 mt-1 line-clamp-2">{park.address}</p>
                              <div className="flex items-center mt-2 text-xs text-emerald-600">
                                <FontAwesomeIcon icon={faCircleCheck} className="w-3 h-3 mr-1" />
                                <span>Delivery Available</span>
                              </div>
                            </div>
                          </div>
                        </motion.div>
                      ))
                    ) : (
                      <div className="text-center py-8 md:py-10 text-gray-500">
                        <FontAwesomeIcon icon={faSearch} className="w-10 h-10 md:w-12 md:h-12 mb-3 opacity-20" />
                        <p className="text-sm md:text-base">No locations found matching your search.</p>
                        <button 
                          onClick={() => setSearchQuery("")}
                          className="mt-3 text-amber-600 text-sm hover:underline"
                        >
                          Clear search
                        </button>
                      </div>
                    )}
                  </div>

                  {/* Delivery info - Enhanced mobile design */}
                  <div className="mt-4 md:mt-6 bg-gradient-to-r from-emerald-50 to-blue-50 p-4 rounded-xl border border-emerald-100">
                    <h4 className="font-bold mb-3 flex items-center text-emerald-800 text-sm md:text-base">
                      <FontAwesomeIcon icon={faTruck} className="w-4 h-4 mr-2 text-emerald-600" />
                      Delivery Information
                    </h4>
                    <div className="space-y-2 text-xs md:text-sm">
                      <div className="flex items-start gap-2">
                        <FontAwesomeIcon icon={faCircleCheck} className="w-3 h-3 text-emerald-500 mt-0.5 flex-shrink-0" />
                        <span className="text-gray-700">
                          <strong>Free delivery</strong> within 8 kms from Thoraipakkam
                        </span>
                      </div>
                      <div className="flex items-start gap-2">
                        <FontAwesomeIcon icon={faRupeeSign} className="w-3 h-3 text-amber-500 mt-0.5 flex-shrink-0" />
                        <span className="text-gray-700">
                          Delivery charges apply for locations beyond 8 kms
                        </span>
                      </div>
                      <div className="flex items-start gap-2">
                        <FontAwesomeIcon icon={faInfoCircle} className="w-3 h-3 text-blue-500 mt-0.5 flex-shrink-0" />
                        <span className="text-gray-600">
                          Orders before 10 AM delivered by lunch time
                        </span>
                      </div>
                    </div>
                    <div className="mt-3 pt-3 border-t border-emerald-200">
                      <p className="text-xs text-gray-500">
                        Don't see your location? <span className="text-amber-600 font-medium">Contact us</span> to check delivery availability.
                      </p>
                    </div>
                  </div>
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </div>
    </section>
  );
};

export default DeliveryMap;
