import React, { useRef } from "react";
import { motion, useInView } from "framer-motion";

export default function HowItWorks() {
  const containerRef = useRef(null);
  const isInView = useInView(containerRef, { once: true, amount: 0.2 });

  const handleWhatsAppContact = () => {
    // Replace with your actual WhatsApp number
    const phoneNumber = '+918123456789'; // Update this with your actual WhatsApp number
    const message = encodeURIComponent('Hi! I\'m interested in Myna Kitchen\'s meal plans and would like to discuss my requirements to choose the best plan for me.');
    const whatsappUrl = `https://wa.me/${phoneNumber}?text=${message}`;

    window.open(whatsappUrl, '_blank');
  };

  const steps = [
    {
      number: "01",
      title: "Browse & Choose",
      description: "Explore our delicious meal plans and find the perfect fit for your lifestyle and dietary needs.",
      accent: "#8B4513", // Brown
      illustration: (
        <div className="w-16 h-16 mx-auto mb-4">
          <div className="grid grid-cols-2 gap-1 h-full">
            <div className="bg-orange-200 border border-orange-400 rounded flex items-center justify-center">
              <span className="text-xs">🥗</span>
            </div>
            <div className="bg-green-200 border border-green-400 rounded flex items-center justify-center">
              <span className="text-xs">🍛</span>
            </div>
            <div className="bg-blue-200 border border-blue-400 rounded flex items-center justify-center">
              <span className="text-xs">🍜</span>
            </div>
            <div className="bg-yellow-200 border border-yellow-400 rounded flex items-center justify-center">
              <span className="text-xs">🥘</span>
            </div>
          </div>
        </div>
      )
    },
    {
      number: "02", 
      title: "Connect on WhatsApp",
      description: "Get in touch with our team to discuss your requirements and get personalized meal recommendations.",
      accent: "#059669", // Green
      illustration: (
        <div className="w-16 h-16 mx-auto mb-4 bg-green-100 border-2 border-green-400 rounded-2xl flex items-center justify-center">
          <div className="relative">
            <div className="w-8 h-8 bg-green-500 rounded-full flex items-center justify-center">
              <span className="text-white text-xs font-bold">💬</span>
            </div>
            <div className="absolute -top-1 -right-1 w-3 h-3 bg-green-600 rounded-full animate-pulse"></div>
          </div>
        </div>
      ),
      hasButton: true
    },
    {
      number: "03",
      title: "Sign Up & Select",
      description: "Complete your registration and choose your preferred plan with flexible scheduling options.",
      accent: "#7C3AED", // Purple
      illustration: (
        <div className="w-16 h-16 mx-auto mb-4">
          <div className="bg-purple-100 border-2 border-purple-400 rounded-xl h-full flex flex-col justify-center items-center">
            <div className="w-8 h-1 bg-purple-400 rounded mb-1"></div>
            <div className="w-6 h-1 bg-purple-300 rounded mb-1"></div>
            <div className="w-4 h-4 bg-purple-500 rounded flex items-center justify-center">
              <span className="text-white text-xs">✓</span>
            </div>
          </div>
        </div>
      )
    },
    {
      number: "04",
      title: "Enjoy Daily Deliveries",
      description: "Start your subscription and enjoy fresh, tasty meals delivered to your doorstep every day.",
      accent: "#DC2626", // Red
      illustration: (
        <div className="w-16 h-16 mx-auto mb-4">
          <div className="relative bg-red-100 border-2 border-red-400 rounded-2xl h-full flex items-center justify-center">
            <div className="w-8 h-8 bg-red-500 rounded-full flex items-center justify-center">
              <span className="text-white text-xs">🚚</span>
            </div>
            <div className="absolute top-2 right-2 w-2 h-2 bg-red-600 rounded-full"></div>
            <div className="absolute bottom-2 left-2 w-1 h-1 bg-red-400 rounded-full"></div>
          </div>
        </div>
      )
    }
  ];

  return (
    <section id="how-it-works" className="pt-8 pb-20 md:pb-28 overflow-hidden section-fade" style={{backgroundColor: '#f9f5f0'}}>
      <div className="container mx-auto px-4 md:px-6">
        {/* Section Header */}
        <motion.div 
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <motion.h2 
            className="text-4xl md:text-5xl font-bold text-gray-900 mb-4"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            How It Works
          </motion.h2>
          <motion.p 
            className="text-lg text-gray-600 max-w-2xl mx-auto"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            From browsing to enjoying delicious meals at your doorstep - we've made it incredibly simple in just 4 easy steps.
          </motion.p>
        </motion.div>

        <div ref={containerRef} className="max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {steps.map((step, index) => (
            <motion.div
              key={index}
              className="relative"
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.6, delay: index * 0.15 }}
              whileHover={{ y: -4, transition: { duration: 0.3 } }}
            >
              {/* Main card matching subscription plans style */}
              <div className="bg-white border-2 border-gray-900 shadow-2xl overflow-hidden relative h-full">
                {/* Accent line */}
                <div 
                  className="h-1 w-full" 
                  style={{backgroundColor: step.accent}}
                ></div>

                {/* Header */}
                <div className="p-6 text-center border-b border-gray-200">
                  {/* Step number */}
                  <div className="w-12 h-12 bg-gray-900 text-white rounded-full flex items-center justify-center font-black text-lg mx-auto mb-4">
                    {step.number}
                  </div>

                  <h3 className="text-xl font-black text-gray-900 mb-2 tracking-tight">{step.title}</h3>
                </div>

                {/* Content area */}
                <div className="p-6 flex flex-col">
                  {/* Visual illustration */}
                  <div className="mb-6">
                    {step.illustration}
                  </div>

                  {/* Description */}
                  <p className="text-gray-600 text-sm leading-relaxed mb-6 flex-grow">
                    {step.description}
                  </p>

                  {/* WhatsApp Button for step 2 */}
                  {step.hasButton && (
                    <div className="mb-6">
                      <motion.button
                        onClick={handleWhatsAppContact}
                        className="w-full py-3 bg-green-600 hover:bg-green-700 text-white font-bold text-sm border-2 border-green-600 hover:border-green-700 transition-all flex items-center justify-center gap-2"
                        whileHover={{ scale: 1.02 }}
                        whileTap={{ scale: 0.98 }}
                      >
                        <span className="text-lg">💬</span>
                        Contact on WhatsApp
                      </motion.button>
                    </div>
                  )}

                  {/* Feature highlight */}
                  <div className="border-t border-gray-100 pt-4">
                    <div className="flex items-center justify-center">
                      <div 
                        className="w-2 h-2 rounded-full mr-2" 
                        style={{backgroundColor: step.accent}}
                      ></div>
                      <span className="text-xs text-gray-500 uppercase tracking-widest font-medium">
                        {index === 0 && "Multiple Options"}
                        {index === 1 && "Instant Support"}
                        {index === 2 && "Easy Setup"}
                        {index === 3 && "Fresh Daily"}
                      </span>
                    </div>
                  </div>
                </div>
              </div>

              {/* Connecting arrow for desktop */}
              {index < steps.length - 1 && (
                <div className="hidden lg:block absolute top-1/2 -right-4 transform -translate-y-1/2 z-10">
                  <motion.div
                    initial={{ opacity: 0, x: -10 }}
                    animate={isInView ? { opacity: 1, x: 0 } : { opacity: 0, x: -10 }}
                    transition={{ duration: 0.6, delay: index * 0.15 + 0.3 }}
                    className="w-8 h-8 bg-white border-2 border-gray-900 rounded-full flex items-center justify-center shadow-lg"
                  >
                    <span className="text-gray-900 font-bold">→</span>
                  </motion.div>
                </div>
              )}
            </motion.div>
          ))}
        </div>

        {/* Call to Action */}
        <motion.div
          initial={{ opacity: 0, y: 40 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 40 }}
          transition={{ duration: 0.8, delay: 0.8 }}
          className="text-center mt-16"
        >
          <div className="bg-white border-2 border-gray-900 shadow-2xl p-8 max-w-lg mx-auto">
            <h3 className="text-2xl font-black text-gray-900 mb-4 tracking-tight">Ready to get started?</h3>
            <p className="text-gray-600 mb-6 text-sm">Join thousands of satisfied customers enjoying fresh meals daily.</p>
            <motion.button 
              onClick={() => {
                const section = document.getElementById('subscription-plans');
                if (section) {
                  window.scrollTo({
                    top: section.offsetTop - 80,
                    behavior: 'smooth'
                  });
                }
              }}
              className="w-full py-4 bg-gray-900 text-white font-bold text-lg border-2 border-gray-900 hover:bg-white hover:text-gray-900 transition-all"
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              Choose Your Plan
            </motion.button>
          </div>
        </motion.div>
      </div>
    </section>
  );
}
