import React, { useRef } from "react";
import { motion, useInView } from "framer-motion";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faWhatsapp } from '@fortawesome/free-brands-svg-icons';
import { 
  faSearch, 
  faComments,
  faUserPlus, 
  faUtensils,
  faArrowRight,
  faCheckCircle
} from '@fortawesome/free-solid-svg-icons';

export default function HowItWorks() {
  const containerRef = useRef(null);
  const isInView = useInView(containerRef, { once: true, amount: 0.2 });

  const steps = [
    {
      number: "01",
      title: "Browse & Choose a Plan",
      description: "Explore our delicious meal plans and find the perfect fit for your lifestyle. From single meals to complete daily nutrition packages.",
      icon: faSearch,
      highlight: "Multiple options available",
      cardColor: "from-blue-50 to-indigo-100",
      iconBg: "bg-blue-500",
      accentColor: "text-blue-600"
    },
    {
      number: "02", 
      title: "Connect on WhatsApp",
      description: "Get in touch with our team through WhatsApp. We'll understand your dietary requirements, preferences, and help customize your perfect meal plan.",
      icon: faWhatsapp,
      highlight: "Instant personalized support",
      cardColor: "from-green-50 to-emerald-100",
      iconBg: "bg-green-500",
      accentColor: "text-green-600"
    },
    {
      number: "03",
      title: "Sign Up & Select Plan",
      description: "Complete your registration using our simple form. Choose your preferred plan, set your delivery schedule, and customize your meal preferences.",
      icon: faUserPlus,
      highlight: "Quick & easy setup",
      cardColor: "from-purple-50 to-violet-100",
      iconBg: "bg-purple-500",
      accentColor: "text-purple-600"
    },
    {
      number: "04",
      title: "Enjoy Daily Deliveries",
      description: "Start your subscription and enjoy fresh, tasty meals delivered right to your doorstep every day. Home-style cooking made convenient.",
      icon: faUtensils,
      highlight: "Fresh meals daily",
      cardColor: "from-orange-50 to-amber-100",
      iconBg: "bg-orange-500",
      accentColor: "text-orange-600"
    }
  ];

  return (
    <section id="how-it-works" className="py-20 md:py-28 bg-gradient-to-br from-gray-50 to-white overflow-hidden section-fade">
      <div className="container mx-auto px-4 md:px-6">
        {/* Header Section */}
        <div className="text-center max-w-4xl mx-auto mb-20">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
          >
            <div className="inline-flex items-center bg-primary/10 text-primary px-6 py-3 rounded-full mb-8 font-semibold">
              <FontAwesomeIcon icon={faCheckCircle} className="mr-2" />
              Simple Process
            </div>
            <h2 className="text-4xl md:text-6xl font-bold mb-8 text-gray-900 leading-tight">
              How It Works
            </h2>
            <p className="text-xl md:text-2xl text-gray-600 leading-relaxed max-w-3xl mx-auto">
              From browsing to enjoying delicious meals at your doorstep - we've made it incredibly simple in just 4 easy steps.
            </p>
          </motion.div>
        </div>

        {/* Steps Section */}
        <div ref={containerRef} className="relative">
          <div className="grid grid-cols-1 lg:grid-cols-4 gap-8 lg:gap-6">
            {steps.map((step, index) => (
              <motion.div
                key={index}
                className="relative"
                initial={{ opacity: 0, y: 60 }}
                animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 60 }}
                transition={{ duration: 0.8, delay: index * 0.2 }}
              >
                {/* Connecting Arrow - Desktop Only */}
                {index < steps.length - 1 && (
                  <div className="hidden lg:block absolute top-24 -right-3 z-10">
                    <motion.div
                      initial={{ opacity: 0, x: -20 }}
                      animate={isInView ? { opacity: 1, x: 0 } : { opacity: 0, x: -20 }}
                      transition={{ duration: 0.6, delay: index * 0.2 + 0.4 }}
                      className="w-6 h-6 text-primary/40"
                    >
                      <FontAwesomeIcon icon={faArrowRight} className="w-full h-full" />
                    </motion.div>
                  </div>
                )}

                {/* Step Card */}
                <div className={`relative bg-gradient-to-br ${step.cardColor} rounded-3xl p-8 shadow-xl hover:shadow-2xl transition-all duration-500 group border border-white/50 backdrop-blur-sm`}>
                  {/* Decorative Elements */}
                  <div className="absolute top-4 right-4 w-20 h-20 bg-white/20 rounded-full blur-xl"></div>
                  <div className="absolute bottom-4 left-4 w-16 h-16 bg-white/10 rounded-full blur-lg"></div>

                  {/* Step Number */}
                  <div className="relative mb-6">
                    <div className="w-16 h-16 bg-white rounded-2xl shadow-lg flex items-center justify-center mb-4 group-hover:scale-110 transition-transform duration-300">
                      <span className="text-2xl font-bold text-gray-800">{step.number}</span>
                    </div>
                  </div>

                  {/* Icon */}
                  <div className={`w-20 h-20 ${step.iconBg} rounded-2xl flex items-center justify-center mb-6 shadow-lg group-hover:scale-105 transition-transform duration-300`}>
                    <FontAwesomeIcon 
                      icon={step.icon} 
                      className="w-10 h-10 text-white" 
                    />
                  </div>

                  {/* Content */}
                  <div className="relative z-10">
                    <h3 className="text-2xl font-bold mb-4 text-gray-900 group-hover:text-gray-800 transition-colors">
                      {step.title}
                    </h3>
                    <p className="text-gray-700 mb-6 leading-relaxed text-lg">
                      {step.description}
                    </p>
                  </div>

                  {/* Highlight Badge */}
                  <div className={`inline-flex items-center bg-white/90 backdrop-blur-sm ${step.accentColor} px-4 py-2 rounded-full text-sm font-bold shadow-md border border-white/60`}>
                    <div className={`w-2 h-2 rounded-full mr-2 ${step.iconBg}`}></div>
                    {step.highlight}
                  </div>

                  {/* Hover Effect Overlay */}
                  <div className="absolute inset-0 bg-gradient-to-br from-white/0 to-white/5 rounded-3xl opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                </div>

                {/* Mobile Connecting Line */}
                {index < steps.length - 1 && (
                  <div className="lg:hidden flex justify-center my-8">
                    <motion.div
                      initial={{ opacity: 0, scaleY: 0 }}
                      animate={isInView ? { opacity: 1, scaleY: 1 } : { opacity: 0, scaleY: 0 }}
                      transition={{ duration: 0.6, delay: index * 0.2 + 0.4 }}
                      className="w-0.5 h-12 bg-gradient-to-b from-primary/60 to-primary/20 rounded-full"
                    ></motion.div>
                  </div>
                )}
              </motion.div>
            ))}
          </div>

          {/* Call to Action */}
          <motion.div
            initial={{ opacity: 0, y: 40 }}
            animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 40 }}
            transition={{ duration: 0.8, delay: 1 }}
            className="text-center mt-16"
          >
            <div className="bg-white rounded-3xl p-8 shadow-xl border border-gray-100 max-w-2xl mx-auto">
              <h3 className="text-2xl font-bold text-gray-900 mb-4">Ready to get started?</h3>
              <p className="text-gray-600 mb-6">Join thousands of satisfied customers enjoying fresh, delicious meals every day.</p>
              <button 
                onClick={() => {
                  const section = document.getElementById('subscription-plans');
                  if (section) {
                    window.scrollTo({
                      top: section.offsetTop - 80,
                      behavior: 'smooth'
                    });
                  }
                }}
                className="bg-primary hover:bg-primary/90 text-white font-bold px-8 py-4 rounded-full shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105"
              >
                Choose Your Plan
              </button>
            </div>
          </motion.div>
        </div>
      </div>
    </section>
  );
}
