import React, { useEffect, useCallback } from 'react';
import './App.css';
import Header from './components/Header';
import HeroSection from './components/HeroSection';
import ProblemStatement from './components/ProblemStatement';
// import Features from './components/Features';
import HowItWorks from './components/HowItWorks';
import SubscriptionPlans from './components/SubscriptionPlans';
import DailyMenu from './components/DailyMenu';
import Testimonials from './components/Testimonials';
import DeliveryMap from './components/DeliveryMap';
import FAQ from './components/FAQ';
import Footer from './components/Footer';
import { createScrollObserver } from './lib/utils';

function App() {
  // Optimized scroll observer callback
  const handleIntersection = useCallback((entries) => {
    entries.forEach(entry => {
      if (entry.isIntersecting) {
        entry.target.classList.add('active');
        // Performance optimization: cleanup will-change after animation
        setTimeout(() => {
          entry.target.style.willChange = 'auto';
        }, 600);
      }
    });
  }, []);

  useEffect(() => {
    // Mark document as JS-enabled for CSS transitions
    document.body.classList.add('js-enabled');

    // Optimized intersection observer with better performance settings
    const observer = createScrollObserver(handleIntersection, { 
      threshold: 0.1,
      rootMargin: '50px 0px -50px 0px' // More generous margins for better UX
    });

    // Apply to any .section-fade elements with performance optimization
    const fadeElements = document.querySelectorAll('.section-fade');

    // Use requestAnimationFrame for better performance
    const observeElements = () => {
      fadeElements.forEach(section => {
        observer.observe(section);
      });
    };

    // Delay observation to ensure DOM is ready
    if (fadeElements.length > 0) {
      requestAnimationFrame(observeElements);
    }

    // Cleanup function
    return () => {
      observer.disconnect();
      // Clean up any remaining will-change properties
      fadeElements.forEach(el => {
        if (el.style) {
          el.style.willChange = 'auto';
        }
      });
    };
  }, [handleIntersection]);

  // Performance monitoring (development only)
  useEffect(() => {
    if (process.env.NODE_ENV === 'development') {
      // Monitor for layout thrashing
      let frameCount = 0;
      let lastTime = performance.now();

      const checkPerformance = () => {
        frameCount++;
        const currentTime = performance.now();

        if (currentTime - lastTime >= 1000) {
          const fps = Math.round((frameCount * 1000) / (currentTime - lastTime));
          if (fps < 50) {
            console.warn(`Low FPS detected: ${fps}fps`);
          }
          frameCount = 0;
          lastTime = currentTime;
        }

        requestAnimationFrame(checkPerformance);
      };

      requestAnimationFrame(checkPerformance);
    }
  }, []);

  return (
    <div className="font-sans text-gray-800 bg-white overflow-x-hidden min-h-screen">
      <Header />
      <main className="relative">
        <HeroSection />
        <ProblemStatement />
        {/* <Features /> */}
        <DailyMenu />
        <SubscriptionPlans />
        <HowItWorks />
        <Testimonials />
        <DeliveryMap />
        <FAQ />
      </main>
      <Footer />
    </div>
  );
}

export default App;
