import React, { useState, useRef } from "react";
import { motion, useInView } from "framer-motion";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown, faClock, faTruck, faMapMarkerAlt, faRupeeSign } from '@fortawesome/free-solid-svg-icons';

const FAQ_ITEMS = [
  {
    id: "faq-1",
    question: "How does meal delivery work?",
    answer: "We deliver fresh, home-style meals to your doorstep daily. Choose your preferred delivery time during signup, and our team will ensure your meals arrive hot and ready to enjoy.",
    icon: faTruck,
    color: "from-slate-600 to-slate-700"
  },
  {
    id: "faq-2", 
    question: "What are your delivery timings?",
    answer: "We deliver meals during three time slots: Breakfast between 7:30 AM - 9:00 AM, Lunch between 11:30 AM - 1:00 PM, and Dinner between 7:00 PM - 8:30 PM.",
    icon: faClock,
    color: "from-stone-600 to-stone-700"
  },
  {
    id: "faq-3",
    question: "How much are the delivery charges?",
    answer: "Delivery is free within 8 km. For distances between 8-12 km, we charge ₹10 per delivery. For 12-16 km, it's ₹15 per delivery, and for 16-20 km, it's ₹20 per delivery.",
    icon: faRupeeSign,
    color: "from-zinc-600 to-zinc-700"
  },
  {
    id: "faq-4",
    question: "How do I place a preorder?",
    answer: "Follow our daily menu through our WhatsApp group to stay updated on available meals. Once you see the menu, simply place your order through your personalized user link for hassle-free ordering.",
    icon: faMapMarkerAlt,
    color: "from-neutral-600 to-neutral-700"
  },
  {
    id: "faq-5",
    question: "What if I'm not available during delivery?",
    answer: "You can change your delivery location or cancel your order up to 4 hours before the scheduled delivery time. Alternatively, you can opt to have your food delivered in a delivery bag outside your gate for convenience.",
    icon: faClock,
    color: "from-gray-600 to-gray-700"
  },
  {
    id: "faq-6", 
    question: "Can I customize my meal preferences?",
    answer: "Absolutely! You can select vegetarian or non-vegetarian options, specify dietary restrictions, and choose from our rotating weekly menu that caters to various tastes and preferences.",
    icon: faTruck,
    color: "from-slate-600 to-slate-700"
  },
  {
    id: "faq-7",
    question: "How do I pause or cancel my subscription?",
    answer: "You can pause or cancel your subscription anytime through your account dashboard. For pauses, we need 24 hours notice. Cancellations can be done instantly with no penalties.",
    icon: faMapMarkerAlt,
    color: "from-stone-600 to-stone-700"
  },
  {
    id: "faq-8",
    question: "What are your delivery areas?",
    answer: "We currently deliver across OMR, Chennai, and surrounding areas. Enter your pincode during signup to confirm if we deliver to your location.",
    icon: faMapMarkerAlt,
    color: "from-zinc-600 to-zinc-700"
  },
  {
    id: "faq-9",
    question: "Are the meals prepared fresh daily?",
    answer: "Yes! All our meals are prepared fresh on the day of delivery using high-quality ingredients. We don't use any preservatives and ensure maximum freshness and taste.",
    icon: faTruck,
    color: "from-neutral-600 to-neutral-700"
  },
  {
    id: "faq-10",
    question: "Do you offer trial packages?",
    answer: "Yes! New customers can try our service with a special trial package. Plus, get 10% off your first order when you sign up for any of our meal plans.",
    icon: faRupeeSign,
    color: "from-gray-600 to-gray-700"
  }
];

const FAQ = () => {
  const sectionRef = useRef(null);
  const isInView = useInView(sectionRef, { once: true, amount: 0.2 });

  return (
    <section 
      className="py-24 md:py-32 overflow-hidden section-fade" 
      style={{ 
        backgroundColor: '#FAFAF9'
      }}
    >
      {/* Minimal background elements */}
      <div className="absolute inset-0 opacity-30">
        <div className="absolute top-1/4 right-1/4 w-px h-96 bg-gray-200"></div>
        <div className="absolute bottom-1/4 left-1/4 w-96 h-px bg-gray-200"></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-2 h-2 bg-gray-300 rounded-full"></div>
      </div>

      <div ref={sectionRef} className="container mx-auto px-4 md:px-6 relative z-10">
        {/* Header */}
        <motion.div
          className="text-center max-w-4xl mx-auto mb-20"
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
          transition={{ duration: 0.8 }}
        >
          <h2 className="text-3xl md:text-4xl lg:text-5xl font-light text-gray-900 mb-6 tracking-wide leading-tight">
            よくある質問
          </h2>
          <div className="w-16 h-px bg-gray-400 mx-auto mb-6"></div>
          <p className="text-base md:text-lg text-gray-600 font-light leading-relaxed">
            Everything you need to know about our meal delivery service
          </p>
        </motion.div>

        {/* FAQ Grid */}
        <div className="max-w-7xl mx-auto grid gap-8 md:grid-cols-2 lg:gap-10">
          {FAQ_ITEMS.map((item, index) => (
            <motion.div
              key={item.id}
              initial={{ opacity: 0, y: 20 }}
              animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
              transition={{ duration: 0.6, delay: index * 0.05 }}
              className="h-fit"
            >
              <FAQItem 
                question={item.question} 
                answer={item.answer}
                icon={item.icon}
                color={item.color}
              />
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default FAQ;

function FAQItem({ question, answer, icon, color }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <motion.div 
      className="group cursor-pointer"
      whileHover={{ y: -2 }}
      transition={{ duration: 0.2, ease: "easeOut" }}
    >
      <div className="bg-white rounded-none border-l-4 border-gray-200 shadow-sm hover:shadow-md transition-all duration-300 overflow-hidden backdrop-blur-sm">
        <button
          className="flex w-full items-start justify-between text-left p-8 group-hover:bg-gray-50/50 transition-colors duration-300"
          onClick={() => setIsOpen(!isOpen)}
        >
          <div className="flex items-start gap-6 flex-1">
            <div className={`flex-shrink-0 w-10 h-10 bg-gradient-to-br ${color} rounded-sm flex items-center justify-center shadow-sm`}>
              <FontAwesomeIcon 
                icon={icon} 
                className="text-white text-sm" 
              />
            </div>
            <div className="flex-1 min-w-0">
              <h3 className="text-lg md:text-xl font-normal text-gray-900 group-hover:text-gray-700 transition-colors duration-300 leading-snug tracking-wide">
                {question}
              </h3>
            </div>
          </div>

          <motion.div
            animate={{ rotate: isOpen ? 180 : 0 }}
            transition={{ duration: 0.3, ease: "easeInOut" }}
            className="flex-shrink-0 ml-6"
          >
            <FontAwesomeIcon 
              icon={faChevronDown} 
              className="text-gray-400 text-sm group-hover:text-gray-600 transition-colors duration-300" 
            />
          </motion.div>
        </button>

        <motion.div
          initial={{ height: 0, opacity: 0 }}
          animate={{ 
            height: isOpen ? "auto" : 0,
            opacity: isOpen ? 1 : 0
          }}
          transition={{ duration: 0.4, ease: [0.04, 0.62, 0.23, 0.98] }}
          className="overflow-hidden"
        >
          <div className="px-8 pb-8 bg-gray-50/30">
            <div className="pl-16 border-l border-gray-100">
              <motion.p 
                className="text-gray-700 leading-relaxed text-base font-light tracking-wide"
                initial={{ y: -5, opacity: 0 }}
                animate={{ y: isOpen ? 0 : -5, opacity: isOpen ? 1 : 0 }}
                transition={{ duration: 0.4, delay: isOpen ? 0.1 : 0 }}
              >
                {answer}
              </motion.p>
            </div>
          </div>
        </motion.div>
      </div>
    </motion.div>
  );
}
