import React, { useState, useRef } from "react";
import { motion, useInView } from "framer-motion";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown, faClock, faTruck, faMapMarkerAlt, faRupeeSign } from '@fortawesome/free-solid-svg-icons';

const FAQ_ITEMS = [
  {
    id: "faq-1",
    question: "How does meal delivery work?",
    answer: "We deliver fresh, home-style meals to your doorstep daily. Choose your preferred delivery time during signup, and our team will ensure your meals arrive hot and ready to enjoy.",
    icon: faTruck,
    gradient: "from-slate-500 to-slate-700"
  },
  {
    id: "faq-2", 
    question: "What are your delivery timings?",
    answer: "We deliver meals during three time slots: Breakfast between 7:30 AM - 9:00 AM, Lunch between 11:30 AM - 1:00 PM, and Dinner between 7:00 PM - 8:30 PM.",
    icon: faClock,
    gradient: "from-blue-500 to-blue-700"
  },
  {
    id: "faq-3",
    question: "How much are the delivery charges?",
    answer: "Delivery is free within 8 km. For distances between 8-12 km, we charge ₹10 per delivery. For 12-16 km, it's ₹15 per delivery, and for 16-20 km, it's ₹20 per delivery.",
    icon: faRupeeSign,
    gradient: "from-emerald-500 to-emerald-700"
  },
  {
    id: "faq-4",
    question: "How do I place a preorder?",
    answer: "Follow our daily menu through our WhatsApp group to stay updated on available meals. Once you see the menu, simply place your order through your personalized user link for hassle-free ordering.",
    icon: faMapMarkerAlt,
    gradient: "from-violet-500 to-violet-700"
  },
  {
    id: "faq-5",
    question: "What if I'm not available during delivery?",
    answer: "You can change your delivery location or cancel your order up to 4 hours before the scheduled delivery time. Alternatively, you can opt to have your food delivered in a delivery bag outside your gate for convenience.",
    icon: faClock,
    gradient: "from-amber-500 to-amber-700"
  },
  {
    id: "faq-6", 
    question: "Can I customize my meal preferences?",
    answer: "Absolutely! You can select vegetarian or non-vegetarian options, specify dietary restrictions, and choose from our rotating weekly menu that caters to various tastes and preferences.",
    icon: faTruck,
    gradient: "from-pink-500 to-pink-700"
  },
  {
    id: "faq-7",
    question: "How do I pause or cancel my subscription?",
    answer: "You can pause or cancel your subscription anytime through your account dashboard. For pauses, we need 24 hours notice. Cancellations can be done instantly with no penalties.",
    icon: faMapMarkerAlt,
    gradient: "from-indigo-500 to-indigo-700"
  },
  {
    id: "faq-8",
    question: "What are your delivery areas?",
    answer: "We currently deliver across OMR, Chennai, and surrounding areas. Enter your pincode during signup to confirm if we deliver to your location.",
    icon: faMapMarkerAlt,
    gradient: "from-teal-500 to-teal-700"
  },
  {
    id: "faq-9",
    question: "Are the meals prepared fresh daily?",
    answer: "Yes! All our meals are prepared fresh on the day of delivery using high-quality ingredients. We don't use any preservatives and ensure maximum freshness and taste.",
    icon: faTruck,
    gradient: "from-cyan-500 to-cyan-700"
  },
  {
    id: "faq-10",
    question: "Do you offer trial packages?",
    answer: "Yes! New customers can try our service with a special trial package. Plus, get 10% off your first order when you sign up for any of our meal plans.",
    icon: faRupeeSign,
    gradient: "from-rose-500 to-rose-700"
  }
];

const FAQ = () => {
  const sectionRef = useRef(null);
  const isInView = useInView(sectionRef, { once: true, amount: 0.2 });

  return (
    <section 
      className="py-20 md:py-28 overflow-hidden section-fade relative" 
      style={{ 
        background: 'linear-gradient(135deg, #f8fafc 0%, #f1f5f9 50%, #e2e8f0 100%)'
      }}
    >
      {/* Tokyo-style background elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute top-20 right-20 w-96 h-96 bg-gradient-to-br from-slate-200/30 to-gray-300/20 rounded-full blur-3xl"></div>
        <div className="absolute bottom-20 left-10 w-80 h-80 bg-gradient-to-br from-blue-200/20 to-indigo-300/20 rounded-full blur-3xl"></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-64 h-64 bg-gradient-to-br from-purple-200/20 to-pink-300/20 rounded-full blur-3xl"></div>
      </div>

      {/* Subtle grid pattern */}
      <div 
        className="absolute inset-0 opacity-[0.02]"
        style={{
          backgroundImage: 'radial-gradient(circle at 1px 1px, rgba(0,0,0,0.15) 1px, transparent 0)',
          backgroundSize: '20px 20px'
        }}
      ></div>

      <div ref={sectionRef} className="container mx-auto px-4 md:px-6 relative z-10">
        {/* Header */}
        <motion.div
          className="text-center max-w-4xl mx-auto mb-20"
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
          transition={{ duration: 0.8 }}
        >
          <h2 className="text-4xl md:text-6xl lg:text-7xl font-light text-slate-800 mb-6 tracking-wide">
            よくある質問
          </h2>
          <h3 className="text-2xl md:text-3xl font-medium text-slate-700 mb-8 tracking-tight">
            Frequently Asked Questions
          </h3>
          <p className="text-lg md:text-xl text-slate-600 font-light leading-relaxed">
            Everything you need to know about our authentic meal delivery service
          </p>
        </motion.div>

        {/* FAQ Grid */}
        <div className="max-w-7xl mx-auto grid gap-8 md:grid-cols-2 lg:gap-10">
          {FAQ_ITEMS.map((item, index) => (
            <motion.div
              key={item.id}
              initial={{ opacity: 0, y: 30, scale: 0.95 }}
              animate={isInView ? { opacity: 1, y: 0, scale: 1 } : { opacity: 0, y: 30, scale: 0.95 }}
              transition={{ duration: 0.7, delay: index * 0.08, ease: "easeOut" }}
              className="h-fit"
            >
              <FAQItem 
                question={item.question} 
                answer={item.answer}
                icon={item.icon}
                gradient={item.gradient}
              />
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default FAQ;

function FAQItem({ question, answer, icon, gradient }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <motion.div 
      className="group relative"
      whileHover={{ y: -4 }}
      transition={{ duration: 0.3, ease: "easeOut" }}
    >
      {/* Glass-morphism container */}
      <div className="relative backdrop-blur-xl bg-white/70 border border-white/30 rounded-3xl shadow-lg hover:shadow-2xl transition-all duration-500 overflow-hidden">
        {/* Subtle gradient overlay */}
        <div className="absolute inset-0 bg-gradient-to-br from-white/40 via-transparent to-slate-100/20 opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>

        {/* Main content */}
        <div className="relative z-10">
          <button
            className="flex w-full items-start justify-between text-left p-8 group/button"
            onClick={() => setIsOpen(!isOpen)}
          >
            <div className="flex items-start gap-6 flex-1">
              {/* Icon with Tokyo-style design */}
              <div className={`flex-shrink-0 w-14 h-14 bg-gradient-to-br ${gradient} rounded-2xl flex items-center justify-center shadow-lg group-hover:shadow-xl transition-all duration-300 group-hover:scale-110`}>
                <FontAwesomeIcon 
                  icon={icon} 
                  className="text-white text-lg drop-shadow-sm" 
                />
              </div>

              <div className="flex-1 min-w-0">
                <h3 className="text-lg md:text-xl font-medium text-slate-800 group-hover/button:text-slate-900 transition-colors duration-300 leading-tight mb-1">
                  {question}
                </h3>
                <div className="w-12 h-0.5 bg-gradient-to-r from-slate-300 to-transparent rounded-full"></div>
              </div>
            </div>

            {/* Chevron with Tokyo-style animation */}
            <motion.div
              animate={{ rotate: isOpen ? 180 : 0 }}
              transition={{ duration: 0.3, ease: "easeInOut" }}
              className="flex-shrink-0 ml-6 w-10 h-10 bg-slate-100/50 rounded-full flex items-center justify-center group-hover/button:bg-slate-200/60 transition-colors duration-300"
            >
              <FontAwesomeIcon 
                icon={faChevronDown} 
                className="text-slate-500 text-sm" 
              />
            </motion.div>
          </button>

          {/* Answer section with smooth expansion */}
          <motion.div
            initial={{ height: 0, opacity: 0 }}
            animate={{ 
              height: isOpen ? "auto" : 0,
              opacity: isOpen ? 1 : 0
            }}
            transition={{ duration: 0.5, ease: "easeInOut" }}
            className="overflow-hidden"
          >
            <div className="px-8 pb-8">
              <div className="pl-20">
                <motion.div
                  initial={{ y: -20, opacity: 0 }}
                  animate={{ 
                    y: isOpen ? 0 : -20,
                    opacity: isOpen ? 1 : 0 
                  }}
                  transition={{ duration: 0.4, delay: isOpen ? 0.1 : 0 }}
                  className="relative"
                >
                  {/* Subtle accent line */}
                  <div className="absolute -left-4 top-0 bottom-0 w-0.5 bg-gradient-to-b from-slate-300 via-slate-200 to-transparent rounded-full"></div>

                  <p className="text-slate-700 leading-relaxed text-base md:text-lg font-light">
                    {answer}
                  </p>
                </motion.div>
              </div>
            </div>
          </motion.div>
        </div>
      </div>
    </motion.div>
  );
}
