import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronLeft, faChevronRight, faStar } from '@fortawesome/free-solid-svg-icons';
import './HeroSection.css';

const HeroSection = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);

  // Food images for the carousel
  const foodImages = [
    {
      src: "https://images.unsplash.com/photo-1540189549336-e6e99c3679fe?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
      alt: "Food plate with vegetables",
      price: "₹140",
      rating: 4.8
    },
    {
      src: "https://images.unsplash.com/photo-1504674900247-0877df9cc836?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
      alt: "Grilled meat with vegetables",
      price: "₹180",
      rating: 4.7
    },
    {
      src: "https://images.unsplash.com/photo-1565299624946-b28f40a0ae38?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
      alt: "Pizza with toppings",
      price: "₹160",
      rating: 4.9
    },
    {
      src: "https://images.unsplash.com/photo-1546069901-ba9599a7e63c?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
      alt: "Healthy salad bowl",
      price: "₹120",
      rating: 4.6
    }
  ];

  // Handle navigation
  const nextSlide = () => {
    if (isAnimating) return;
    setIsAnimating(true);
    setCurrentSlide((prev) => (prev + 1) % foodImages.length);
    setTimeout(() => setIsAnimating(false), 500);
  };

  const prevSlide = () => {
    if (isAnimating) return;
    setIsAnimating(true);
    setCurrentSlide((prev) => (prev - 1 + foodImages.length) % foodImages.length);
    setTimeout(() => setIsAnimating(false), 500);
  };

  // Auto rotate carousel
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 5000);

    return () => clearInterval(interval);
  }, [currentSlide]);

  // Handle smooth scroll to sections
  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      window.scrollTo({
        top: section.offsetTop - 80,
        behavior: 'smooth'
      });
    }
  };

  return (
    <section id="hero" className="bg-white min-h-screen pt-20 md:pt-24 pb-12 overflow-hidden">
      <div className="container mx-auto px-4">
        <div className="flex flex-col md:flex-row items-center justify-between gap-8 md:gap-12 py-8 md:py-16">
          {/* Left side content */}
          <div className="w-full md:w-1/2 text-center md:text-left space-y-4 md:space-y-6">
            <div className="inline-block px-4 py-1.5 bg-primary bg-opacity-10 text-primary text-sm font-semibold rounded-full">
              FREE DELIVERY
            </div>

            <h2 className="text-2xl md:text-3xl font-medium text-gray-800">
              Delicious Meals. Zero Hassle.
            </h2>

            <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold text-gray-900">
              Food Sorted.<br/>
              <span className="text-gray-800">Life's Sorted.</span>
            </h1>

            <p className="text-lg text-gray-600 max-w-xl">
              With us you no longer have to run a kitchen, manage groceries or worry about your daily food.
            </p>

            <div className="flex flex-col sm:flex-row gap-4 pt-4">
              <button
                onClick={() => scrollToSection('try-meal')}
                className="hero-button hero-button-primary"
              >
                Try a Meal
              </button>

              <button
                onClick={() => scrollToSection('daily-menu')}
                className="hero-button hero-button-secondary"
              >
                Explore Menu
              </button>
            </div>
          </div>

          {/* Right side - Food Card Carousel */}
          <div className="w-full md:w-1/2 relative perspective-1000">
            <div className="food-card-container relative">
              <motion.div
                className="food-card"
                key={`card-${currentSlide}`}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.5 }}
              >
                <div className="food-card-image-container">
                  <img 
                    src={foodImages[currentSlide].src} 
                    alt={foodImages[currentSlide].alt} 
                    className="food-card-image"
                  />
                </div>

                <div className="food-card-overlay">
                  <div className="progress-bar">
                    {foodImages.map((_, index) => (
                      <div 
                        key={index} 
                        className={`progress-dot ${index === currentSlide ? 'active' : ''}`}
                      />
                    ))}
                  </div>

                  <div className="food-card-details">
                    <div className="food-card-price">
                      {foodImages[currentSlide].price}
                    </div>
                    <div className="food-card-rating">
                      <FontAwesomeIcon icon={faStar} className="text-yellow-400 mr-1" />
                      <span>{foodImages[currentSlide].rating}</span>
                    </div>
                  </div>
                </div>

                {/* Navigation Controls */}
                <div className="food-card-nav">
                  <button 
                    className="food-card-nav-button" 
                    onClick={prevSlide}
                    aria-label="Previous image"
                  >
                    <FontAwesomeIcon icon={faChevronLeft} />
                  </button>
                  <button 
                    className="food-card-nav-button" 
                    onClick={nextSlide}
                    aria-label="Next image"
                  >
                    <FontAwesomeIcon icon={faChevronRight} />
                  </button>
                </div>
              </motion.div>

              {/* Card Shadow and 3D effect */}
              <div className="food-card-shadow"></div>
            </div>

            {/* Decoration for 3D effect */}
            <div className="food-card-decoration perspective-decoration"></div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
