import React, { useState } from 'react';
import { motion } from 'framer-motion';

const SubscriptionPlans = () => {
  // State to track selected meal combinations for each plan
  const [selectedMeals, setSelectedMeals] = useState({
    'one-meal': 'lunch-dinner', // Default: lunch or dinner
    'two-meals': 'lunch-dinner', // Default: lunch + dinner
    'three-meals': 'all' // Default: all meals
  });

  // Meal pricing configurations
  const mealPricing = {
    'one-meal': {
      'lunch-dinner': { price: 120, description: 'Lunch or Dinner', meals: ['lunch', 'dinner'] },
      'breakfast': { price: 80, description: 'Breakfast', meals: ['breakfast'] }
    },
    'two-meals': {
      'lunch-dinner': { price: 220, description: 'Lunch + Dinner', meals: ['lunch', 'dinner'] },
      'lunch-breakfast': { price: 200, description: 'Lunch + Breakfast', meals: ['breakfast', 'lunch'] },
      'dinner-breakfast': { price: 200, description: 'Dinner + Breakfast', meals: ['breakfast', 'dinner'] }
    },
    'three-meals': {
      'all': { price: 250, description: 'All Meals', meals: ['breakfast', 'lunch', 'dinner'] }
    }
  };

  const pricingPlans = [
    {
      id: 'one-meal',
      title: 'Single',
      description: 'One meal daily',
      popular: false,
      accent: '#8B4513' // Brown
    },
    {
      id: 'two-meals',
      title: 'Double',
      description: 'Two meals daily',
      popular: true,
      accent: '#DC2626' // Red
    },
    {
      id: 'three-meals',
      title: 'Triple',
      description: 'Three meals daily',
      popular: false,
      accent: '#1F2937' // Dark gray/black
    }
  ];

  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      window.scrollTo({
        top: section.offsetTop - 80,
        behavior: 'smooth'
      });
    }
  };

  const formatPrice = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0
    }).format(amount);
  };

  const renderMealBoxes = (count, accentColor) => {
    return (
      <div className="flex justify-center items-center gap-3 mb-8">
        {Array.from({ length: count }, (_, i) => (
          <motion.div
            key={i}
            className="relative"
            initial={{ opacity: 0, scale: 0.8 }}
            whileInView={{ opacity: 1, scale: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.4, delay: i * 0.1 }}
            whileHover={{ scale: 1.05, y: -2 }}
          >
            {/* Bento box container */}
            <div className="w-20 h-16 bg-white border-2 border-gray-800 rounded-sm shadow-lg relative overflow-hidden">
              {/* Bento box lid line */}
              <div className="absolute top-3 left-0 right-0 h-0.5 bg-gray-800"></div>

              {/* Food compartments */}
              <div className="absolute top-5 left-2 w-6 h-3 rounded-sm" style={{backgroundColor: accentColor, opacity: 0.7}}></div>
              <div className="absolute top-5 right-2 w-6 h-3 bg-gray-200 rounded-sm"></div>
              <div className="absolute bottom-2 left-2 right-2 h-3 bg-gray-100 rounded-sm"></div>

              {/* Chopsticks */}
              <div className="absolute -top-1 -right-1 w-8 h-0.5 bg-amber-700 rounded-full transform rotate-12"></div>
              <div className="absolute -top-0.5 -right-1 w-8 h-0.5 bg-amber-600 rounded-full transform rotate-12"></div>
            </div>
          </motion.div>
        ))}
      </div>
    );
  };

  return (
    <section id="subscription-plans" className="py-0 pb-20 md:pb-28 overflow-hidden section-fade" style={{backgroundColor: '#f9f5f0'}}>
      <div className="container mx-auto px-4 md:px-6">
        <div className="max-w-5xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-8">
          {pricingPlans.map((plan, index) => (
            <motion.div
              key={plan.id}
              className={`relative transition-all duration-300 ${
                plan.popular ? "md:-mt-6 md:mb-6" : ""
              }`}
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.6, delay: index * 0.15 }}
              whileHover={{ y: -4, transition: { duration: 0.3 } }}
            >
              {/* Popular badge */}
              {plan.popular && (
                <motion.div 
                  className="absolute -top-4 left-1/2 transform -translate-x-1/2 z-20"
                  initial={{ opacity: 0, y: -10 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.5, delay: 0.3 }}
                >
                  <div className="bg-red-600 text-white text-xs font-bold px-4 py-2 tracking-wider">
                    人気 POPULAR
                  </div>
                </motion.div>
              )}

              {/* Main card */}
              <div className="bg-white border-2 border-gray-900 shadow-2xl overflow-hidden relative">
                {/* Accent line */}
                <div 
                  className="h-1 w-full" 
                  style={{backgroundColor: plan.accent}}
                ></div>

                {/* Header */}
                <div className="p-8 text-center border-b border-gray-200">
                  <h3 className="text-3xl font-black text-gray-900 mb-2 tracking-tight">{plan.title}</h3>
                  <p className="text-gray-600 text-sm uppercase tracking-widest mb-6">{plan.description}</p>

                  {/* Meal boxes visualization */}
                  {renderMealBoxes(plan.mealBoxes, plan.accent)}

                  {/* Price */}
                  <div className="text-center">
                    <div className="text-5xl font-black text-gray-900 mb-1">
                      {formatPrice(plan.price)}
                    </div>
                    <div className="text-sm text-gray-500 uppercase tracking-widest">per day</div>
                  </div>
                </div>

                {/* Content area */}
                <div className="p-8">
                  {/* Minimal features */}
                  <div className="space-y-3 mb-8">
                    <div className="flex items-center justify-between border-b border-gray-100 pb-2">
                      <span className="text-gray-900 font-medium">Delivery</span>
                      <span className="text-gray-600">Free</span>
                    </div>

                    <div className="flex items-center justify-between border-b border-gray-100 pb-2">
                      <span className="text-gray-900 font-medium">Quality</span>
                      <span className="text-gray-600">Premium</span>
                    </div>

                    <div className="flex items-center justify-between">
                      <span className="text-gray-900 font-medium">Flexibility</span>
                      <span className="text-gray-600">Daily</span>
                    </div>
                  </div>

                  {/* CTA Button */}
                  <motion.button
                    onClick={() => scrollToSection('daily-combos')}
                    className="w-full py-4 font-bold text-lg transition-all border-2 border-gray-900 hover:bg-gray-900 hover:text-white"
                    style={{
                      backgroundColor: plan.popular ? plan.accent : 'white',
                      color: plan.popular ? 'white' : '#1F2937'
                    }}
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    ORDER NOW
                  </motion.button>
                </div>

                {/* Japanese corner decoration */}
                <div className="absolute top-4 right-4 w-6 h-6 border-t-2 border-r-2 border-gray-300"></div>
                <div className="absolute bottom-4 left-4 w-6 h-6 border-b-2 border-l-2 border-gray-300"></div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default SubscriptionPlans;
