import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes } from '@fortawesome/free-solid-svg-icons';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const offset = window.scrollY;
      setScrolled(offset > 10);
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      window.scrollTo({
        top: section.offsetTop - 80,
        behavior: 'smooth'
      });
      setIsMenuOpen(false);
    }
  };

  return (
    <header className={`sticky top-0 z-50 bg-white transition-shadow duration-300 ${scrolled ? 'shadow-md' : 'shadow-sm'}`}>
      <div className="container mx-auto px-4 md:px-6 py-3 md:py-4">
        <div className="flex items-center justify-between min-h-[60px]">
          <button onClick={() => scrollToSection('hero')} className="flex items-center flex-shrink-0">
            <img 
              src="/images/myna-logo.jpg" 
              alt="Myna Kitchen Logo" 
              className="h-12 w-auto sm:h-14 md:h-16 lg:h-18 xl:h-20 object-contain transition-all duration-200"
              style={{ maxWidth: '250px' }}
            />
          </button>

          {/* Mobile menu button */}
          <div className="flex md:hidden items-center space-x-3">
            <button 
              onClick={() => scrollToSection('subscription-plans')}
              className="bg-accent text-white px-3 py-1.5 rounded-full text-sm font-medium shadow-sm hover:bg-warmOrange-600 transition-colors"
            >
              Get Started
            </button>
            <button 
              type="button" 
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="flex items-center justify-center p-2 rounded-md text-tertiary hover:text-primary hover:bg-sage-50 focus:outline-none"
              aria-expanded={isMenuOpen}
            >
              <span className="sr-only">Open main menu</span>
              {isMenuOpen ? (
                <FontAwesomeIcon icon={faTimes} className="block h-5 w-5" />
              ) : (
                <FontAwesomeIcon icon={faBars} className="block h-5 w-5" />
              )}
            </button>
          </div>

          {/* Desktop navigation */}
          <nav className="hidden md:flex items-center space-x-6 lg:space-x-8">
            <button 
              onClick={() => scrollToSection('hero')}
              className="text-black hover:text-primary font-medium transition-colors"
            >
              Home
            </button>
            <button 
              onClick={() => scrollToSection('how-it-works')}
              className="text-black hover:text-primary font-medium transition-colors"
            >
              How It Works
            </button>
            <button 
              onClick={() => scrollToSection('daily-menu')}
              className="text-black hover:text-primary font-medium transition-colors"
            >
              Menu
            </button>
            <button 
              onClick={() => scrollToSection('subscription-plans')}
              className="text-black hover:text-primary font-medium transition-colors"
            >
              Pricing
            </button>
            <button 
              onClick={() => scrollToSection('delivery-map')}
              className="text-black hover:text-primary font-medium transition-colors"
            >
              Delivery Areas
            </button>
            <button 
              onClick={() => scrollToSection('subscription-plans')}
              className="bg-accent text-white px-6 py-2.5 rounded-full font-medium shadow-sm hover:bg-warmOrange-600 transition-colors"
            >
              Get Started
            </button>
          </nav>
        </div>

        {/* Mobile navigation */}
        <div className={`pt-4 pb-3 border-t border-tertiary/20 md:hidden ${isMenuOpen ? 'block' : 'hidden'}`}>
          <div className="space-y-1">
            <button 
              onClick={() => scrollToSection('hero')}
              className="block w-full text-left px-3 py-2 rounded-md text-base font-medium text-black hover:bg-sage-50"
            >
              Home
            </button>
            <button 
              onClick={() => scrollToSection('how-it-works')}
              className="block w-full text-left px-3 py-2 rounded-md text-base font-medium text-black hover:bg-sage-50"
            >
              How It Works
            </button>
            <button 
              onClick={() => scrollToSection('daily-menu')}
              className="block w-full text-left px-3 py-2 rounded-md text-base font-medium text-black hover:bg-sage-50"
            >
              Menu
            </button>
            <button 
              onClick={() => scrollToSection('subscription-plans')}
              className="block w-full text-left px-3 py-2 rounded-md text-base font-medium text-black hover:bg-sage-50"
            >
              Pricing
            </button>
            <button 
              onClick={() => scrollToSection('delivery-map')}
              className="block w-full text-left px-3 py-2 rounded-md text-base font-medium text-black hover:bg-sage-50"
            >
              Delivery Areas
            </button>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
