import React, { useState, useRef, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faChevronLeft, 
  faChevronRight, 
  faChevronDown, 
  faChevronUp,
  faStar,
  faUsers,
  faCircle
} from '@fortawesome/free-solid-svg-icons';
import { ALL_MENU_ITEMS, MENU_CATEGORIES, getCategoryPrice } from '../data/menuItems';
import MenuItemCard from './MenuItemCard';
import { debounce } from '../lib/utils';
import './DailyMenu.css';

const DailyMenu = () => {
  const [currentPage, setCurrentPage] = useState(0);
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [itemsPerPage, setItemsPerPage] = useState(6);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const sectionRef = useRef(null);

  // Filter items based on selected category
  const filteredItems = selectedCategory === 'All' 
    ? ALL_MENU_ITEMS 
    : ALL_MENU_ITEMS.filter(item => item.category === selectedCategory);

  const totalPages = Math.ceil(filteredItems.length / itemsPerPage);
  const startIndex = currentPage * itemsPerPage;
  const currentItems = filteredItems.slice(startIndex, startIndex + itemsPerPage);

  // Responsive items per page calculation
  const updateItemsPerPage = debounce(() => {
    if (typeof window === 'undefined') return;

    const width = window.innerWidth;
    let newItemsPerPage;

    if (width < 640) {
      newItemsPerPage = 2; // Mobile: 2 items
    } else if (width < 768) {
      newItemsPerPage = 4; // Small tablet: 4 items
    } else if (width < 1024) {
      newItemsPerPage = 6; // Tablet: 6 items
    } else {
      newItemsPerPage = 6; // Desktop: 6 items
    }

    if (newItemsPerPage !== itemsPerPage) {
      setItemsPerPage(newItemsPerPage);
      // Reset to first page when changing items per page
      setCurrentPage(0);
    }
  }, 250);

  useEffect(() => {
    updateItemsPerPage();
    window.addEventListener('resize', updateItemsPerPage);
    return () => window.removeEventListener('resize', updateItemsPerPage);
  }, [itemsPerPage]);

  // Safe page navigation with bounds checking
  const navigatePage = (direction) => {
    const newPage = currentPage + direction;
    if (newPage >= 0 && newPage < totalPages) {
      setCurrentPage(newPage);
    }
  };

  // Get category price range
  const getCategoryPriceDisplay = () => {
    if (selectedCategory === 'All') {
      return '₹45 - ₹150';
    }
    const priceRange = getCategoryPrice(selectedCategory);
    return priceRange.min === priceRange.max 
      ? `₹${priceRange.min}` 
      : `₹${priceRange.min} - ₹${priceRange.max}`;
  };

  // Category change handler
  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
    setCurrentPage(0); // Reset to first page
  };

  // Toggle collapse
  const toggleCollapse = () => {
    setIsCollapsed(!isCollapsed);
  };

  // Get available categories
  const categories = ['All', ...Object.values(MENU_CATEGORIES)];

  return (
    <section 
      id="daily-menu" 
      ref={sectionRef}
      className="daily-menu-section py-12 md:py-16 lg:py-20 bg-gradient-to-br from-sage-50 via-white to-gold-50"
    >
      <div className="container mx-auto px-4 md:px-6 lg:px-8">
        {/* Header Section */}
        <motion.div 
          className="text-center mb-8 md:mb-12 lg:mb-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          <h2 className="text-3xl md:text-4xl lg:text-5xl xl:text-6xl font-black text-black mb-4 md:mb-6 tracking-tight leading-tight">
            Today's <span className="text-accent">Menu</span>
          </h2>
          <p className="text-base md:text-lg lg:text-xl text-gray-600 max-w-3xl mx-auto leading-relaxed">
            Discover our carefully curated selection of delicious, nutritious meals made with fresh, 
            locally-sourced ingredients. Each meal is crafted to perfection by our expert chefs.
          </p>
        </motion.div>

        {/* Category Filter */}
        <motion.div 
          className="flex flex-wrap justify-center gap-2 md:gap-4 mb-8"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.2 }}
        >
          {categories.map((category) => (
            <button
              key={category}
              onClick={() => handleCategoryChange(category)}
              className={`px-4 py-2 rounded-full text-sm font-medium transition-all duration-200 ${
                selectedCategory === category
                  ? 'bg-accent text-white shadow-lg'
                  : 'bg-white text-gray-700 hover:bg-gray-50 border border-gray-200'
              }`}
            >
              {category}
            </button>
          ))}
        </motion.div>

        {/* Stats Bar */}
        <motion.div 
          className="flex flex-wrap justify-center items-center gap-4 md:gap-8 mb-8 md:mb-12"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.3 }}
        >
          <div className="flex items-center gap-2 text-gray-600">
            <FontAwesomeIcon icon={faUsers} className="text-accent" />
            <span className="text-sm md:text-base font-medium">
              {filteredItems.length} Items Available
            </span>
          </div>
          <div className="flex items-center gap-2 text-gray-600">
            <FontAwesomeIcon icon={faStar} className="text-accent" />
            <span className="text-sm md:text-base font-medium">
              Premium Quality
            </span>
          </div>
          <div className="flex items-center gap-2 text-gray-600">
            <FontAwesomeIcon icon={faCircle} className="text-accent" />
            <span className="text-sm md:text-base font-medium">
              {getCategoryPriceDisplay()}
            </span>
          </div>
        </motion.div>

        {/* Collapsible Content */}
        <AnimatePresence>
          {!isCollapsed && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: "auto" }}
              exit={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.5, ease: "easeInOut" }}
            >
              {/* Menu Items Grid */}
              <motion.div 
                className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 md:gap-6 lg:gap-8 mb-8 md:mb-12"
                layout
              >
                <AnimatePresence mode="wait">
                  {currentItems.map((item) => (
                    <MenuItemCard key={item.id} item={item} />
                  ))}
                </AnimatePresence>
              </motion.div>

              {/* Navigation Controls */}
              {totalPages > 1 && (
                <motion.div 
                  className="flex justify-center items-center gap-4 md:gap-6"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 0.3 }}
                >
                  <button
                    onClick={() => navigatePage(-1)}
                    disabled={currentPage === 0}
                    className={`flex items-center justify-center w-10 h-10 md:w-12 md:h-12 rounded-full border-2 transition-all duration-200 ${
                      currentPage === 0
                        ? 'border-gray-300 text-gray-300 cursor-not-allowed'
                        : 'border-accent text-accent hover:bg-accent hover:text-white'
                    }`}
                    aria-label="Previous page"
                  >
                    <FontAwesomeIcon icon={faChevronLeft} className="w-4 h-4" />
                  </button>

                  <div className="flex items-center gap-2">
                    <span className="text-sm md:text-base text-gray-600">
                      Page {currentPage + 1} of {totalPages}
                    </span>
                  </div>

                  <button
                    onClick={() => navigatePage(1)}
                    disabled={currentPage === totalPages - 1}
                    className={`flex items-center justify-center w-10 h-10 md:w-12 md:h-12 rounded-full border-2 transition-all duration-200 ${
                      currentPage === totalPages - 1
                        ? 'border-gray-300 text-gray-300 cursor-not-allowed'
                        : 'border-accent text-accent hover:bg-accent hover:text-white'
                    }`}
                    aria-label="Next page"
                  >
                    <FontAwesomeIcon icon={faChevronRight} className="w-4 h-4" />
                  </button>
                </motion.div>
              )}
            </motion.div>
          )}
        </AnimatePresence>

        {/* Collapse Toggle */}
        <motion.div 
          className="text-center mt-8"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.4 }}
        >
          <button
            onClick={toggleCollapse}
            className="inline-flex items-center gap-2 px-6 py-3 bg-accent text-white font-semibold rounded-full hover:bg-warmOrange-600 transition-all duration-200 shadow-lg hover:shadow-xl"
            aria-label={isCollapsed ? 'Show menu' : 'Hide menu'}
          >
            <span>{isCollapsed ? 'Show Full Menu' : 'Hide Menu'}</span>
            <FontAwesomeIcon 
              icon={isCollapsed ? faChevronDown : faChevronUp} 
              className="w-4 h-4" 
            />
          </button>
        </motion.div>
      </div>
    </section>
  );
};

export default DailyMenu;
