import React, { useState, useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes } from '@fortawesome/free-solid-svg-icons';
import { useThrottledScroll } from '../hooks/useThrottledScroll';
import { openSecureLink } from '../lib/utils';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [isVisible, setIsVisible] = useState(true);
  const lastScrollYRef = useRef(0);

  // Fixed scroll handler using useRef to prevent memory leaks
  const handleScroll = () => {
    const currentScrollY = window.scrollY;
    const lastScrollY = lastScrollYRef.current;

    // Determine if we've scrolled enough to show shadow
    setScrolled(currentScrollY > 10);

    // Determine scroll direction and visibility
    if (currentScrollY < 10) {
      // Always show header at the top of the page
      setIsVisible(true);
    } else if (currentScrollY > lastScrollY && currentScrollY > 100) {
      // Scrolling down & past 100px - hide header
      setIsVisible(false);
      // Close mobile menu when hiding header
      setIsMenuOpen(false);
    } else if (currentScrollY < lastScrollY) {
      // Scrolling up - show header
      setIsVisible(true);
    }

    // Update the ref with current scroll position
    lastScrollYRef.current = currentScrollY;
  };

  // Use throttled scroll hook to prevent performance issues
  useThrottledScroll(handleScroll, 16); // 60fps throttling

  const scrollToSection = (sectionId) => {
    try {
      const section = document.getElementById(sectionId);
      if (section) {
        window.scrollTo({
          top: section.offsetTop - 60,
          behavior: 'smooth'
        });
        setIsMenuOpen(false);
      }
    } catch (error) {
      console.error('Error scrolling to section:', error);
      // Fallback: try simple scroll
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  // Handle external link with security measures
  const handleExternalLink = (url) => {
    setIsMenuOpen(false); // Close mobile menu
    openSecureLink(url, {
      onError: (error) => {
        console.error('Failed to open signup link:', error);
        // Could show user notification here
      }
    });
  };

  return (
    <header className={`fixed top-0 left-0 right-0 z-50 bg-white transition-all duration-500 ease-in-out ${
      scrolled ? 'shadow-lg backdrop-blur-sm' : 'shadow-sm'
    } ${
      isVisible ? 'translate-y-0' : '-translate-y-full'
    }`}>
      <div className="container mx-auto px-4 md:px-6 py-2 md:py-3">
        <div className="flex items-center justify-between min-h-[50px]">
          <button 
            onClick={() => scrollToSection('hero')} 
            className="flex items-center flex-shrink-0 focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2 rounded"
            aria-label="Go to home section"
          >
            <img 
              src="/images/myna-logo.jpg" 
              alt="Myna Kitchen Logo" 
              className="h-10 w-auto sm:h-12 md:h-14 lg:h-16 xl:h-18 object-contain transition-all duration-200"
              style={{ maxWidth: '250px' }}
              onError={(e) => {
                console.error('Logo image failed to load');
                e.target.style.display = 'none';
              }}
            />
          </button>

          {/* Mobile menu button and sign up */}
          <div className="flex md:hidden items-center space-x-2 sm:space-x-3">
            {/* Mobile Sign Up Button */}
            <button 
              onClick={() => handleExternalLink('https://mynakitchen.in/SignUp')}
              className="bg-accent text-white px-3 py-2 sm:px-4 sm:py-2 rounded-full text-sm font-medium shadow-sm hover:bg-warmOrange-600 transition-colors min-h-[44px] min-w-[80px] flex items-center justify-center touch-manipulation focus:outline-none focus:ring-2 focus:ring-accent focus:ring-offset-2"
              aria-label="Sign up for Myna Kitchen"
            >
              <span className="text-xs sm:text-sm">Sign Up</span>
            </button>

            {/* Hamburger Menu Button */}
            <button 
              type="button" 
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="flex items-center justify-center p-2 rounded-md text-tertiary hover:text-primary hover:bg-sage-50 focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2 min-h-[44px] min-w-[44px] touch-manipulation transition-colors"
              aria-expanded={isMenuOpen}
              aria-label={isMenuOpen ? 'Close menu' : 'Open menu'}
            >
              <span className="sr-only">Open main menu</span>
              <FontAwesomeIcon 
                icon={isMenuOpen ? faTimes : faBars} 
                className={`block h-5 w-5 transition-transform duration-200 ${
                  isMenuOpen ? 'rotate-90' : 'rotate-0'
                }`} 
              />
            </button>
          </div>

          {/* Desktop navigation */}
          <nav className="hidden md:flex items-center space-x-4 lg:space-x-6 xl:space-x-8">
            <button 
              onClick={() => scrollToSection('hero')}
              className="text-black hover:text-primary font-medium transition-colors px-3 py-2 rounded focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2"
            >
              Home
            </button>
            <button 
              onClick={() => scrollToSection('how-it-works')}
              className="text-black hover:text-primary font-medium transition-colors px-3 py-2 rounded focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2"
            >
              How It Works
            </button>
            <button 
              onClick={() => scrollToSection('daily-menu')}
              className="text-black hover:text-primary font-medium transition-colors px-3 py-2 rounded focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2"
            >
              Menu
            </button>
            <button 
              onClick={() => scrollToSection('subscription-plans')}
              className="text-black hover:text-primary font-medium transition-colors px-3 py-2 rounded focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2"
            >
              Pricing
            </button>
            <button 
              onClick={() => scrollToSection('delivery-map')}
              className="text-black hover:text-primary font-medium transition-colors px-3 py-2 rounded focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2"
            >
              Delivery Areas
            </button>
            <button 
              onClick={() => handleExternalLink('https://mynakitchen.in/SignUp')}
              className="bg-accent text-white px-6 py-2.5 rounded-full font-medium shadow-sm hover:bg-warmOrange-600 transition-colors focus:outline-none focus:ring-2 focus:ring-accent focus:ring-offset-2 touch-manipulation"
              aria-label="Sign up for Myna Kitchen"
            >
              Sign Up
            </button>
          </nav>
        </div>

        {/* Enhanced Mobile navigation with better animations */}
        <div className={`md:hidden overflow-hidden transition-all duration-300 ease-in-out ${
          isMenuOpen 
            ? 'max-h-80 opacity-100 pt-4 pb-3 border-t border-tertiary/20' 
            : 'max-h-0 opacity-0 pt-0 pb-0'
        }`}>
          <div className="space-y-1">
            <button 
              onClick={() => scrollToSection('hero')}
              className="block w-full text-left px-4 py-3 rounded-md text-base font-medium text-black hover:bg-sage-50 transition-colors min-h-[48px] focus:outline-none focus:bg-sage-100 touch-manipulation"
            >
              Home
            </button>
            <button 
              onClick={() => scrollToSection('how-it-works')}
              className="block w-full text-left px-4 py-3 rounded-md text-base font-medium text-black hover:bg-sage-50 transition-colors min-h-[48px] focus:outline-none focus:bg-sage-100 touch-manipulation"
            >
              How It Works
            </button>
            <button 
              onClick={() => scrollToSection('daily-menu')}
              className="block w-full text-left px-4 py-3 rounded-md text-base font-medium text-black hover:bg-sage-50 transition-colors min-h-[48px] focus:outline-none focus:bg-sage-100 touch-manipulation"
            >
              Menu
            </button>
            <button 
              onClick={() => scrollToSection('subscription-plans')}
              className="block w-full text-left px-4 py-3 rounded-md text-base font-medium text-black hover:bg-sage-50 transition-colors min-h-[48px] focus:outline-none focus:bg-sage-100 touch-manipulation"
            >
              Pricing
            </button>
            <button 
              onClick={() => scrollToSection('delivery-map')}
              className="block w-full text-left px-4 py-3 rounded-md text-base font-medium text-black hover:bg-sage-50 transition-colors min-h-[48px] focus:outline-none focus:bg-sage-100 touch-manipulation"
            >
              Delivery Areas
            </button>
          </div>
        </div>
      </div>

      {/* Mobile menu overlay for better UX */}
      {isMenuOpen && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-25 z-40 md:hidden"
          onClick={() => setIsMenuOpen(false)}
          aria-hidden="true"
        />
      )}
    </header>
  );
};

export default Header;
