import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faInstagram, faFacebook, faTwitter, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import './Footer.css';

const Footer = () => {
  const currentYear = new Date().getFullYear();

  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      window.scrollTo({
        top: section.offsetTop - 80,
        behavior: 'smooth'
      });
    }
  };

  return (
    <footer className="modern-footer">
      <div className="footer-container">
        {/* Red Top Section */}
        <div className="footer-top">
          <div className="footer-top-content">
            <div className="founded-info">
              <span className="info-label">YEAR FOUNDED</span>
              <span className="info-value">2025</span>
            </div>
            <div className="location-info">
              <span className="info-label">LOCATION</span>
              <span className="info-value">OMR, Chennai</span>
            </div>
          </div>
        </div>

        {/* Black Bottom Section */}
        <div className="footer-bottom">
          <div className="footer-bottom-content">
            {/* Contact Section */}
            <div className="footer-section">
              <h3>Contact Us</h3>
              <p>Chennai South Chennai</p>
              <p>Phone: +91 7418688269</p>
              <p>Email: kitchenmyna@gmail.com</p>
            </div>

            {/* Connect Section */}
            <div className="footer-section">
              <h3>Connect</h3>
              <div className="social-links">
                <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
                  Instagram
                </a>
                <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">
                  Facebook
                </a>
                <a href="https://twitter.com" target="_blank" rel="noopener noreferrer">
                  Twitter
                </a>
                <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer">
                  LinkedIn
                </a>
              </div>
            </div>
          </div>

          {/* Bottom Credits */}
          <div className="footer-credits">
            <div className="credits-left">
              <p>Made with ❤️ by Myna Team</p>
              <p>© {currentYear} • Privacy Policy • Terms</p>
            </div>

            {/* Navigation Pills */}
            <div className="footer-nav">
              <button 
                onClick={() => scrollToSection('hero')}
                className="nav-pill active"
              >
                Home
              </button>
              <button 
                onClick={() => scrollToSection('subscription-plans')}
                className="nav-pill"
              >
                Plans
              </button>
              <button 
                onClick={() => scrollToSection('daily-menu')}
                className="nav-pill"
              >
                Menu
              </button>
              <button 
                onClick={() => scrollToSection('testimonials')}
                className="nav-pill"
              >
                Reviews
              </button>
            </div>
          </div>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
