import React, { useEffect, useRef, useState } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import './ProblemStatement.css';

const ProblemStatement = () => {
  const solutionRef = useRef(null);
  const [currentTile, setCurrentTile] = useState(0);
  const [isMobile, setIsMobile] = useState(false);

  const { scrollYProgress } = useScroll({
    target: solutionRef,
    offset: ["start 0.5", "end 0.5"]
  });

  // Transform scroll progress to tile progression (0-4) with better mapping
  const tileProgress = useTransform(scrollYProgress, [0, 1], [0, 4]);

  useEffect(() => {
    // Check if mobile
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    checkMobile();
    window.addEventListener('resize', checkMobile);

    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  useEffect(() => {
    const unsubscribe = tileProgress.onChange((latest) => {
      const newTile = Math.min(Math.floor(latest), 4);
      setCurrentTile(newTile);
    });
    return unsubscribe;
  }, [tileProgress]);

  const problems = [
    {
      id: 'busy',
      title: 'Too Busy to Cook',
      description: 'Your schedule is packed. Between work, commute, and life, who has time to plan, shop, and cook?',
      icon: '⏰',
      fact: 'Average person works 8+ hours daily'
    },
    {
      id: 'junk',
      title: 'Stuck with Junk Food',
      description: 'Quick fixes lead to unhealthy choices. Fast food becomes the norm when convenience matters most.',
      icon: '🍔',
      fact: '70% of daily intake is processed food'
    },
    {
      id: 'expensive',
      title: 'Expensive & Wasteful',
      description: 'Ordering out daily burns through your budget. Groceries expire unused. Money down the drain.',
      icon: '💸',
      fact: 'Spend ₹500+ daily on food delivery'
    },
    {
      id: 'away',
      title: 'Away from Home',
      description: 'Living away from family means no home-cooked meals. Missing that comfort and nutrition.',
      icon: '🏠',
      fact: 'Missing the comfort of home-cooked meals'
    }
  ];

  const solutionFeatures = [
    {
      icon: '🍽️',
      title: 'Daily Meals Delivered',
      description: 'Fresh, home-style meals prepared daily and delivered right to your doorstep',
      gradient: 'from-blue-500 to-purple-600',
      bgColor: 'bg-blue-50',
      iconBg: 'bg-blue-100'
    },
    {
      icon: '⏰',
      title: 'Save Your Time',
      description: 'No more planning, shopping, or cooking. We handle everything while you focus on what matters',
      gradient: 'from-green-500 to-teal-600',
      bgColor: 'bg-green-50',
      iconBg: 'bg-green-100'
    },
    {
      icon: '💰',
      title: 'Budget-Friendly',
      description: 'Starting at ₹120/day - more affordable than ordering out, with zero waste',
      gradient: 'from-yellow-500 to-orange-600',
      bgColor: 'bg-yellow-50',
      iconBg: 'bg-yellow-100'
    },
    {
      icon: '🏠',
      title: 'Home-Style Comfort',
      description: 'Taste the comfort of home-cooked meals, even when you\'re away from family',
      gradient: 'from-pink-500 to-red-600',
      bgColor: 'bg-pink-50',
      iconBg: 'bg-pink-100'
    }
  ];

  return (
    <section id="problem-statement" className="problem-section">
      <div className="container mx-auto px-4 md:px-6">
        {/* Problem Section Header */}
        <motion.div 
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <motion.h2 
            className="problem-title"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            We Know Your <span className="text-red-600">Daily Struggle</span>
          </motion.h2>
          <motion.p 
            className="problem-subtitle"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            Modern life shouldn't mean compromising on nutrition and taste
          </motion.p>
        </motion.div>

        {/* Problems List */}
        <div className="problems-list">
          {problems.map((problem, index) => (
            <motion.div
              key={problem.id}
              className="problem-item"
              initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
              whileInView={{ opacity: 1, x: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8, delay: index * 0.2 }}
            >
              <motion.div 
                className="problem-icon-large"
                initial={{ scale: 0, rotate: -180 }}
                whileInView={{ scale: 1, rotate: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6, delay: 0.3 + index * 0.2 }}
              >
                {problem.icon}
              </motion.div>

              <div className="problem-content-text">
                <motion.h3 
                  className="problem-title-large"
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: 0.4 + index * 0.2 }}
                >
                  {problem.title}
                </motion.h3>

                <motion.p 
                  className="problem-description-text"
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: 0.5 + index * 0.2 }}
                >
                  {problem.description}
                </motion.p>

                <motion.div 
                  className="problem-fact"
                  initial={{ opacity: 0, scale: 0.8 }}
                  whileInView={{ opacity: 1, scale: 1 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.5, delay: 0.6 + index * 0.2 }}
                >
                  {problem.fact}
                </motion.div>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Transition to Solution */}
        <motion.div 
          className="problem-cta"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8, delay: 0.8 }}
        >
          <h3 className="cta-title">Ready to Break Free from These Problems?</h3>
          <p className="cta-description">Let us handle your daily meals while you focus on what matters most</p>
        </motion.div>
      </div>

      {/* Modern Sticky Solution Section */}
      <div ref={solutionRef} className="sticky-solution-wrapper">
        {/* Background with gradient */}
        <div className="absolute inset-0 bg-gradient-to-br from-gray-50 via-white to-blue-50"></div>

        {/* Sticky container */}
        <div className="sticky-container">
          <div className="container mx-auto px-4 md:px-6">
            {/* Solution Header - Always visible */}
            <motion.div 
              className="text-center mb-16 relative z-10"
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8 }}
            >
              <motion.h2 
                className="text-3xl md:text-4xl lg:text-5xl font-bold mb-4 text-gray-900 tracking-tight"
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.8, delay: 0.2 }}
              >
                Daily Meals <span className="text-primary">Delivered</span>
              </motion.h2>
              <motion.p 
                className="text-lg text-gray-600 max-w-2xl mx-auto"
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.8, delay: 0.4 }}
              >
                With us you no longer have to run a kitchen, manage groceries or worry about your daily food.
              </motion.p>
            </motion.div>

            {/* Modern Tiles Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-16">
              {solutionFeatures.map((feature, index) => (
                <motion.div
                  key={index}
                  className={`modern-tile relative overflow-hidden rounded-2xl ${feature.bgColor} border border-white/50 shadow-lg backdrop-blur-sm`}
                  initial={{ opacity: 0, y: 100, scale: 0.8 }}
                  animate={isMobile ? {
                    // Mobile: Simple stagger animation
                    opacity: 1,
                    y: 0,
                    scale: 1,
                  } : {
                    // Desktop: Scroll-triggered animation
                    opacity: currentTile > index ? 1 : 0,
                    y: currentTile > index ? 0 : 100,
                    scale: currentTile > index ? 1 : 0.8,
                  }}
                  transition={isMobile ? {
                    duration: 0.6,
                    delay: index * 0.2,
                    ease: "easeOut"
                  } : {
                    duration: 0.8,
                    delay: currentTile > index ? (index * 0.15) : 0,
                    ease: "easeOut"
                  }}
                  whileInView={isMobile ? { opacity: 1, y: 0, scale: 1 } : undefined}
                  viewport={isMobile ? { once: true } : undefined}
                  whileHover={{ 
                    scale: 1.05,
                    y: -10,
                    transition: { duration: 0.3 }
                  }}
                >
                  {/* Gradient overlay */}
                  <div className={`absolute inset-0 bg-gradient-to-br ${feature.gradient} opacity-5`}></div>

                  {/* Content */}
                  <div className="relative z-10 p-8 h-full flex flex-col">
                    {/* Icon */}
                    <div className={`w-16 h-16 ${feature.iconBg} rounded-2xl flex items-center justify-center mb-6 shadow-sm`}>
                      <span className="text-2xl">{feature.icon}</span>
                    </div>

                    {/* Title */}
                    <h3 className="text-xl font-bold mb-4 text-gray-900 leading-tight">
                      {feature.title}
                    </h3>

                    {/* Description */}
                    <p className="text-gray-600 leading-relaxed flex-grow">
                      {feature.description}
                    </p>

                    {/* Decorative element */}
                    <div className={`mt-6 h-1 w-12 bg-gradient-to-r ${feature.gradient} rounded-full`}></div>
                  </div>

                  {/* Hover effect overlay */}
                  <div className="absolute inset-0 bg-white/10 opacity-0 hover:opacity-100 transition-opacity duration-300"></div>
                </motion.div>
              ))}
            </div>

            {/* Pricing Highlight */}
            <motion.div 
              className="text-center bg-white/80 backdrop-blur-sm rounded-3xl p-8 border border-white/50 shadow-xl relative z-10"
              initial={{ opacity: 0, scale: 0.95 }}
              animate={isMobile ? {
                opacity: 1,
                scale: 1,
              } : {
                opacity: currentTile >= 4 ? 1 : 0,
                scale: currentTile >= 4 ? 1 : 0.95,
              }}
              transition={{ duration: 0.8, delay: isMobile ? 0.8 : 0.5 }}
              whileInView={isMobile ? { opacity: 1, scale: 1 } : undefined}
              viewport={isMobile ? { once: true } : undefined}
            >
              <div className="text-primary text-lg font-medium mb-2">Starting at</div>
              <div className="text-4xl md:text-5xl font-bold text-gray-900 mb-2">₹120<span className="text-xl text-gray-600">/day</span></div>
              <p className="text-gray-600 mb-6">Fresh meals delivered to your doorstep</p>

              <motion.button 
                className="bg-gradient-to-r from-primary to-orange-600 hover:from-primary/90 hover:to-orange-600/90 text-white font-medium px-8 py-3 rounded-full shadow-lg transition-all transform hover:scale-105"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => {
                  const section = document.getElementById('subscription-plans');
                  if (section) {
                    window.scrollTo({
                      top: section.offsetTop - 80,
                      behavior: 'smooth'
                    });
                  }
                }}
              >
                Get Started
              </motion.button>
            </motion.div>
          </div>
        </div>

        {/* Spacer to create scroll distance for the animation */}
        <div className="scroll-spacer"></div>
      </div>
    </section>
  );
};

export default ProblemStatement;
